      const { text } = req.body;
      const pageNum = parseInt(pageNumber);

      if (!text || typeof text !== 'string') {
        return res.status(400).json({ error: "Text content is required" });
      }

      console.log(`‚úèÔ∏è Manual edit for document ${documentId}, page ${pageNum}: ${text.length} chars`);

      // Get previous text for audit trail
      const previousResult = await db.execute(sql`
        SELECT extracted_text FROM ocr_pages 
        WHERE document_id = ${documentId} AND page_number = ${pageNum}
      `);

      const previousText = previousResult.rows?.[0]?.extracted_text || '';

      // Update with manual edit
      await db.execute(sql`
        INSERT INTO ocr_pages (document_id, page_number, extracted_text, confidence, status, engine, is_manual_edit, created_at, updated_at)
        VALUES (${documentId}, ${pageNum}, ${text}, 0.99, 'completed', 'manual', true, NOW(), NOW())
        ON CONFLICT (document_id, page_number) 
        DO UPDATE SET 
          extracted_text = EXCLUDED.extracted_text,
          confidence = 0.99,
          engine = 'manual',
          is_manual_edit = true,
          updated_at = NOW()
      `);

      // Also update ocr_cache
      await db.execute(sql`
        INSERT INTO ocr_cache (document_id, page_number, extracted_text, confidence, ocr_engine, created_at, processed_at)
        VALUES (${documentId}, ${pageNum}, ${text}, '0.99', 'manual', NOW(), NOW())
        ON CONFLICT (document_id, page_number) 
        DO UPDATE SET 
          extracted_text = EXCLUDED.extracted_text,
          confidence = '0.99',
          ocr_engine = 'manual',
          processed_at = NOW()
      `);

      console.log(`üíæ Manual edit saved for page ${pageNum}`);

      res.json({
        success: true,
        pageNumber: pageNum,
        textLength: (text as string)?.length || 0,
        previousTextLength: (previousText as string)?.length || 0,
        message: `Page ${pageNum} manually edited and saved`
      });

    } catch (error) {
      console.error('Save page edit error:', error);
      res.status(500).json({
        error: 'Failed to save page edit',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Get OCR audit trail for a page
  app.get("/api/documents/:documentId/pages/:pageNumber/audit", async (req: any, res) => {
    try {
      const { documentId, pageNumber } = req.params;
      const pageNum = parseInt(pageNumber);

      // Get current OCR data
      const currentResult = await db.execute(sql`
        SELECT extracted_text, confidence, engine, is_manual_edit, updated_at
        FROM ocr_pages 
        WHERE document_id = ${documentId} AND page_number = ${pageNum}
      `);

      if (!currentResult.rows?.length) {
        return res.status(404).json({ error: "No OCR data found for this page" });
      }

      const current = currentResult.rows[0];

      res.json({
        pageNumber: pageNum,
        current: {
          text: current.extracted_text,
          confidence: current.confidence,
          engine: current.engine,
          isManualEdit: current.is_manual_edit,
          lastUpdated: current.updated_at
        },
        textLength: (current.extracted_text as string)?.length || 0,
        wordCount: (current.extracted_text as string)?.split(/\s+/).length || 0
      });

    } catch (error) {
      console.error('Get page audit error:', error);
      res.status(500).json({
        error: 'Failed to get page audit trail',
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // LLM OCR Quality Verification function
  async function verifyOCRWithLLM(pdfPath: string, pageNumber: number, ocrText: string) {
    try {
      // Convert PDF page to image for LLM analysis
      const { pdfToImageBuffer } = await import('./services/pdfUtils');
      const imageBuffer = await pdfToImageBuffer(pdfPath, pageNumber);
      const imageBase64 = imageBuffer.toString('base64');

      const verificationPrompt = `You are an OCR quality verification specialist for legal documents. Analyze the OCR output against the PDF page image and determine quality.

ANALYZE FOR:
1. Text Completeness (0-100): Are all visible text elements captured?
2. Index Item Detection (Critical for pages 1-10): Are numbered items properly recognized?
3. Legal References Accuracy (0-100): Case citations, statutes, dates, exhibits
4. Character-Level Accuracy (0-100): No garbled text, proper punctuation

OCR Text to verify:
${ocrText.substring(0, 2000)}...

OUTPUT VALID JSON ONLY:
{
  "qualityScore": 0-100,
  "needsReOCR": true/false,
  "issues": ["missing_text", "garbled", "structure", "index_error"],
  "confidence": 0.0-1.0,
  "recommendedAction": "approve|reocr|manual_review"
}

DECISION CRITERIA:
- If qualityScore < 85: needsReOCR = true
- If index page with score < 95: needsReOCR = true
- If legal citations corrupted: needsReOCR = true`;

      // Try Anthropic Claude first, then OpenAI as fallback
      let response;
      try {
        response = await anthropic.messages.create({
          model: "claude-3-haiku-20240307",
          max_tokens: 1000,
          messages: [{
            role: 'user',
            content: [
              { type: 'text', text: verificationPrompt },
              { 
                type: 'image', 
                source: {
                  type: 'base64',
                  media_type: 'image/png',
                  data: imageBase64
                }
              }
            ]
          }]
        });

        const content = response.content[0];
        const responseText = (content && 'text' in content) ? content.text : '{}';

        // Extract JSON from response
        const jsonMatch = responseText.match(/\{[\s\S]*?\}/);
        if (jsonMatch) {
          return JSON.parse(jsonMatch[0]);
        }
      } catch (claudeError) {
        console.warn('Claude verification failed, trying OpenAI:', claudeError);

        // Fallback to OpenAI
        const OpenAI = (await import('openai')).default;
        const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

        const openaiResponse = await openai.chat.completions.create({
          model: 'gpt-4o',
          max_tokens: 1000,
          messages: [{
            role: 'user',
            content: [
              { type: 'text', text: verificationPrompt },
              { 
                type: 'image_url', 
                image_url: { url: `data:image/png;base64,${imageBase64}` }
              }
            ]
          }]
        });

        const openaiText = openaiResponse.choices[0]?.message?.content || '{}';
        const jsonMatch = openaiText.match(/\{[\s\S]*?\}/);
        if (jsonMatch) {
          return JSON.parse(jsonMatch[0]);
        }
      }

      // Fallback quality assessment
      return {
        qualityScore: 75,
        needsReOCR: false,
        issues: ["llm_verification_failed"],
        confidence: 0.5,
        recommendedAction: "manual_review"
      };

    } catch (error) {
      console.error('LLM verification error:', error);
      return {
        qualityScore: 50,
        needsReOCR: false,
        issues: ["verification_error"],
        confidence: 0.0,
        recommendedAction: "manual_review"
      };
    }
  }

  // Force process all queued batches immediately (bypass disabled workers)
  app.post("/api/documents/:documentId/force-process-batches", async (req, res) => {
    try {
      const { documentId } = req.params;

      console.log(`üöÄ FORCE PROCESSING: Starting immediate batch processing for document ${documentId}`);

      // Import parallel batch processor
      const { ParallelBatchProcessor } = await import('./services/parallelBatch');

      // Get all queued batches for this document
      const batches = await storage.getBatchesByDocument(documentId);
      const queuedBatches = batches.filter(batch => batch.status === 'queued');

      if (queuedBatches.length === 0) {
        return res.json({
          success: true,
          message: 'No queued batches found to process',
          batchesProcessed: 0
        });
      }

      console.log(`üì¶ Found ${queuedBatches.length} queued batches - processing immediately`);

      // Process all batches immediately (bypass worker queue)
      const result = await ParallelBatchProcessor.processDocumentParallel(documentId, 2);

      res.json({
        success: true,
        message: `Successfully processed ${queuedBatches.length} batches`,
        batchesProcessed: queuedBatches.length,
        result
      });

    } catch (error) {
      console.error('‚ùå Force batch processing failed:', error);
      res.status(500).json({
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  // Generate hyperlinked PDF from tab items
  app.post("/api/documents/:documentId/generate-hyperlinked-pdf", isAuthenticated, async (req, res) => {
    try {
      const documentId = req.params.documentId;

      // Get document info
      const document = await storage.getDocument(documentId);
      if (!document) {
        return res.status(404).json({ error: "Document not found" });
      }

      // Get tab items from database  
      const tabItems = await db.select().from(indexItems)
        .where(eq(indexItems.documentId, documentId))
        .orderBy(indexItems.ordinal);

      if (tabItems.length === 0) {
        return res.status(400).json({ error: "No tab items found for this document" });
      }

      // Get the original PDF path
      const originalPdfPath = path.join(process.cwd(), "temp-uploads", `${documentId}.pdf`);

      // Check if the original PDF exists
      if (!fs.existsSync(originalPdfPath)) {
        return res.status(404).json({ error: "Original PDF file not found" });
      }

      // Prepare tab items for Python service
      const tabItemsForGeneration = tabItems.map(item => ({
        tabNumber: item.tabNumber || item.ordinal?.toString() || "1",
        title: item.title || item.label || `Tab ${item.tabNumber || item.ordinal}`,
        dateField: item.dateField || "",
        targetPage: item.targetPage || item.pageHint || 1
      }));

      // Create output path
      const outputDir = path.join(process.cwd(), "temp-outputs");
      if (!fs.existsSync(outputDir)) {
        fs.mkdirSync(outputDir, { recursive: true });
      }

      const outputPath = path.join(outputDir, `hyperlinked_${documentId}_${Date.now()}.pdf`);
      const tabItemsJsonPath = path.join(outputDir, `tab_items_${documentId}_${Date.now()}.json`);

      // Save tab items to JSON file for Python service
      await fs.writeJson(tabItemsJsonPath, tabItemsForGeneration);

      // Call Python PDF generation service
      const pythonProcess = spawn('python3', [
        path.join(process.cwd(), 'server/services/pdfGenerator.py'),
        originalPdfPath,
        tabItemsJsonPath,
        outputPath
      ]);

      let pythonOutput = '';
      let pythonError = '';

      pythonProcess.stdout.on('data', (data) => {
        pythonOutput += data.toString();
      });

      pythonProcess.stderr.on('data', (data) => {
        pythonError += data.toString();
      });

      pythonProcess.on('close', async (code) => {
        try {
          // Clean up temp JSON file
          await fs.remove(tabItemsJsonPath);

          if (code !== 0) {
            console.error('Python PDF generation failed:', pythonError);
            return res.status(500).json({ 
              error: "PDF generation failed", 
              details: pythonError 
            });
          }

          // Parse result from Python output
          let result;
          try {
            const jsonMatch = pythonOutput.match(/\{[\s\S]*\}/);
            if (jsonMatch) {
              result = JSON.parse(jsonMatch[0]);
            } else {
              result = { success: true, message: "PDF generated successfully" };
            }
          } catch (parseError) {
            result = { success: true, message: "PDF generated successfully" };
          }

          if (!result.success) {
            return res.status(500).json({ 
              error: "PDF generation failed", 
              details: result.error 
            });
          }

          // Check if output file exists
          if (!fs.existsSync(outputPath)) {
            return res.status(500).json({ error: "Generated PDF file not found" });
          }

          // Send the PDF file
          res.setHeader('Content-Type', 'application/pdf');
          res.setHeader('Content-Disposition', `attachment; filename="hyperlinked_${document.title || 'document'}.pdf"`);

          const fileStream = fs.createReadStream(outputPath);
          fileStream.pipe(res);

          // Clean up the output file after sending
          fileStream.on('end', async () => {
            try {
              await fs.remove(outputPath);
            } catch (cleanupError) {
              console.error('Error cleaning up output file:', cleanupError);
            }
          });

        } catch (error) {
          console.error('Error processing PDF generation result:', error);
          return res.status(500).json({ 
            error: "Error processing PDF generation result", 
            details: error instanceof Error ? error.message : String(error)
          });
        }
      });

    } catch (error) {
      console.error("Error generating hyperlinked PDF:", error);
      res.status(500).json({ 
        error: "Failed to generate hyperlinked PDF",
        details: error instanceof Error ? error.message : String(error)
      });
    }
  });

  // ===== SCREENSHOT API ENDPOINTS =====

  // Get all screenshots for a document
  app.get("/api/documents/:documentId/screenshots", isAuthenticated, async (req, res) => {
    try {
      const { documentId } = req.params;

      const result = await db.select().from(screenshots)
        .where(eq(screenshots.documentId, documentId))
        .orderBy(screenshots.createdAt);

      res.json(result.map(screenshot => ({
        id: screenshot.id,
        name: screenshot.originalName,
        url: screenshot.imageData,
        ocrText: screenshot.ocrText || '',
        isOcrProcessing: screenshot.ocrProcessingStatus === 'processing',
        clickableAreas: screenshot.clickableAreas || []
      })));
    } catch (error) {
      console.error("Error fetching screenshots:", error);
      res.status(500).json({ error: "Failed to fetch screenshots" });
    }
  });

  // Save a new screenshot
  app.post("/api/documents/:documentId/screenshots", isAuthenticated, async (req, res) => {
    try {
      const { documentId } = req.params;
      const { name, url, clickableAreas } = req.body;

      if (!name || !url) {
        return res.status(400).json({ error: "Name and URL are required" });
      }

      // Extract file size from base64 data URL
      const base64Data = url.split(',')[1] || url;
      const fileSize = Math.round((base64Data.length * 3) / 4); // Approximate base64 decoded size

      const filename = `screenshot_${documentId}_${Date.now()}.png`;

      const result = await db.insert(screenshots).values({
        documentId,
        filename,
        originalName: name,
        mimeType: 'image/png',
        fileSize,
        imageData: url,
        ocrProcessingStatus: 'pending',
        clickableAreas: clickableAreas || []
      }).returning();

      const screenshot = result[0];

      res.json({
        id: screenshot.id,
        name: screenshot.originalName,
        url: screenshot.imageData,
        ocrText: screenshot.ocrText || '',
        isOcrProcessing: screenshot.ocrProcessingStatus === 'processing',
        clickableAreas: screenshot.clickableAreas || []
      });
    } catch (error) {
      console.error("Error saving screenshot:", error);
      res.status(500).json({ error: "Failed to save screenshot" });
    }
  });

  // Update screenshot OCR text
  app.put("/api/documents/:documentId/screenshots/:screenshotId", isAuthenticated, async (req, res) => {
    try {
      const { screenshotId } = req.params;
      const { ocrText, isOcrProcessing, clickableAreas } = req.body;

      const updateData: any = {};
      if (ocrText !== undefined) updateData.ocrText = ocrText;
      if (isOcrProcessing !== undefined) {
        updateData.ocrProcessingStatus = isOcrProcessing ? 'processing' : 'completed';
      }
      if (clickableAreas !== undefined) updateData.clickableAreas = clickableAreas;
      updateData.updatedAt = sql`NOW()`;

      const result = await db.update(screenshots)
        .set(updateData)
        .where(eq(screenshots.id, screenshotId))
        .returning();

      if (result.length === 0) {
        return res.status(404).json({ error: "Screenshot not found" });
      }

      const screenshot = result[0];

      res.json({
        id: screenshot.id,
        name: screenshot.originalName,
        url: screenshot.imageData,
        ocrText: screenshot.ocrText || '',
        isOcrProcessing: screenshot.ocrProcessingStatus === 'processing',
        clickableAreas: screenshot.clickableAreas || []
      });
    } catch (error) {
      console.error("Error updating screenshot:", error);
      res.status(500).json({ error: "Failed to update screenshot" });
    }
  });

  // Delete a screenshot
  app.delete("/api/documents/:documentId/screenshots/:screenshotId", isAuthenticated, async (req, res) => {
    try {
      const { screenshotId } = req.params;

      const result = await db.delete(screenshots)
        .where(eq(screenshots.id, screenshotId))
        .returning();

      if (result.length === 0) {
        return res.status(404).json({ error: "Screenshot not found" });
      }

      res.json({ success: true, message: "Screenshot deleted successfully" });
    } catch (error) {
      console.error("Error deleting screenshot:", error);
      res.status(500).json({ error: "Failed to delete screenshot" });
    }
  });

  // Test endpoint for Vision API
  app.post("/api/test-vision", async (req, res) => {
    try {
      const { isVisionApiAvailable } = await import('./services/vision');
      const isAvailable = await isVisionApiAvailable();

      res.json({
        success: true,
        visionApiAvailable: isAvailable,
        message: isAvailable ? 
          "‚úÖ Google Cloud Vision API is working properly!" : 
          "‚ùå Google Cloud Vision API is not available - check credentials and billing"
      });
    } catch (error) {
      res.status(500).json({
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        message: "‚ùå Failed to test Vision API"
      });
    }
  });

  // üíæ PERMANENT DATABASE SAVE: Save OCR table rows with hyperlink pages
  app.put("/api/documents/:documentId/ocr-rows", isAuthenticated, async (req, res) => {
    try {
      const { documentId } = req.params;
      const { ocrRows } = req.body;

      if (!Array.isArray(ocrRows)) {
        return res.status(400).json({ error: "ocrRows must be an array" });
      }

      console.log(`üíæ PERMANENT: Saving ${ocrRows.length} OCR table rows for document ${documentId}`);

      // Delete existing OCR rows for this document to replace with new data
      await db.delete(indexItems)
        .where(eq(indexItems.documentId, documentId));

      // Insert new OCR rows if any exist
      if (ocrRows.length > 0) {
        const insertData = ocrRows.map((row: any, index: number) => ({
          documentId,
          tabNumber: row.tabNo || (index + 1).toString(),
          fullText: row.fullText || '',
          pageNumber: row.hyperlinkPage ? parseInt(row.hyperlinkPage) : null,
          hyperlinkUrl: row.hyperlinkUrl || '',
          orderIndex: index,
          status: 'active' as const
        }));

        await db.insert(indexItems).values(insertData);
      }

      console.log(`‚úÖ PERMANENT: Successfully saved ${ocrRows.length} OCR rows to database`);

      res.json({ 
        success: true, 
        saved: ocrRows.length,
        message: `OCR table rows saved permanently to database`
      });

    } catch (error) {
      console.error("‚ùå Error saving OCR table rows to database:", error);
      res.status(500).json({ 
        error: "Failed to save OCR table rows to database",
        details: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { randomUUID } from "crypto";
import { eq, and, sql, count } from "drizzle-orm";
import { drizzle } from "drizzle-orm/node-postgres";
import { Pool } from "pg";
import { cases, documents, links, documentMemory, users, ocrCache, ocrJobs, ocrBatches, pageLinkPositions, type Case, type Document, type Link, type DocumentMemory, type User, type UpsertUser, type InsertCase, type InsertDocument, type InsertLink, type InsertDocumentMemory, type OcrCache, type OcrJob, type OcrBatch, type InsertOcrCache, type InsertOcrJob, type InsertOcrBatch, type PageLinkPosition, type InsertPageLinkPosition } from "@shared/schema";

if (!process.env.DATABASE_URL) {
  throw new Error("DATABASE_URL is required");
}

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
});

const db = drizzle(pool);

export interface IStorage {
  // Cases
  getCases(): Promise<Case[]>;
  getCase(id: string): Promise<Case | undefined>;
  createCase(data: InsertCase): Promise<Case>;
  updateCase(id: string, data: Partial<Case>): Promise<Case>;
  deleteCase(id: string): Promise<void>;

  // Documents
  getDocuments(): Promise<Document[]>;
  getDocumentsByCase(caseId: string): Promise<Document[]>;
  getDocument(id: string): Promise<Document | undefined>;
  createDocument(data: InsertDocument): Promise<Document>;
  updateDocument(id: string, data: Partial<Document>): Promise<Document>;
  deleteDocument(id: string): Promise<void>;

  // Links
  getLinks(): Promise<Link[]>;
  getLinksByDocument(docId: string): Promise<Link[]>;
  getLink(id: string): Promise<Link | undefined>;
  createLink(data: InsertLink): Promise<Link>;
  updateLink(id: string, data: Partial<Link>): Promise<Link>;
  deleteLink(id: string): Promise<void>;
  deleteAllLinksForCase(caseId: string): Promise<void>;

  // Document Memory
  getDocumentSuggestions(query: string): Promise<DocumentMemory[]>;
  saveDocumentMemory(data: InsertDocumentMemory): Promise<DocumentMemory>;
  checkDuplicateDocument(caseId: string, fileName: string): Promise<Document[]>;

  // Progress tracking (optional extension)
  getProgress?(key: string): any;
  setProgress?(key: string, progress: any): void;

  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // OCR Cache operations
  getOcrCacheByDocument(documentId: string): Promise<OcrCache[]>;
  createOcrCache(data: InsertOcrCache): Promise<OcrCache>;
  updateOcrCache(id: string, data: Partial<OcrCache>): Promise<OcrCache>;
  deleteOcrCacheByDocument(documentId: string): Promise<void>;

  // OCR Job operations
  createOcrJob(data: InsertOcrJob): Promise<OcrJob>;
  getOcrJob(id: string): Promise<OcrJob | undefined>;
  getOcrJobByDocument(documentId: string): Promise<OcrJob | undefined>;
  updateOcrJob(id: string, data: Partial<OcrJob>): Promise<OcrJob>;
  getQueuedOcrJobs(limit?: number): Promise<OcrJob[]>;

  // OCR Batch operations
  createOcrBatch(data: InsertOcrBatch): Promise<OcrBatch>;
  getOcrBatch(id: string): Promise<OcrBatch | undefined>;
  getBatchesByDocument(documentId: string): Promise<OcrBatch[]>;
  updateOcrBatch(id: string, data: Partial<OcrBatch>): Promise<OcrBatch>;
  deleteOcrBatchesByDocument(documentId: string): Promise<void>;

  // Page Link Position operations
  getPageLinkPositions(documentId: string, pageNumber?: number): Promise<PageLinkPosition[]>;
  upsertPageLinkPositions(documentId: string, positions: InsertPageLinkPosition[]): Promise<PageLinkPosition[]>;
  deletePageLinkPosition(id: string, documentId: string): Promise<void>;
  deletePageLinkPositionsByDocument(documentId: string): Promise<void>;
  patchPageLinkPosition(
    documentId: string, 
    pageNumber: number, 
    tabNumber: string, 
    data: Partial<Pick<PageLinkPosition, 'yOffset' | 'locked' | 'xNorm' | 'yNorm' | 'targetPage'>>
  ): Promise<PageLinkPosition>;
}

export class PostgresStorage implements IStorage {
  private progressStore: Map<string, any> = new Map();
  // Cases
  async getCases(): Promise<Case[]> {
    return db.select().from(cases);
  }

  async getCase(id: string): Promise<Case | undefined> {
    const result = await db.select().from(cases).where(eq(cases.id, id)).limit(1);
    return result[0];
  }

  async createCase(data: InsertCase): Promise<Case> {
    const caseData = { ...data, id: randomUUID() };
    const result = await db.insert(cases).values(caseData).returning();
    return result[0];
  }

  async updateCase(id: string, data: Partial<Case>): Promise<Case> {
    const result = await db.update(cases)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(cases.id, id))
      .returning();
    return result[0];
  }

  async deleteCase(id: string): Promise<void> {
    // First delete all documents associated with this case
    await db.delete(documents).where(eq(documents.caseId, id));
    // Then delete the case
    await db.delete(cases).where(eq(cases.id, id));
  }

  // Documents
  async getDocuments(): Promise<Document[]> {
    return db.select().from(documents);
  }

  async getDocumentsByCase(caseId: string): Promise<Document[]> {
    return db.select().from(documents).where(eq(documents.caseId, caseId));
  }

  async getDocument(id: string): Promise<Document | undefined> {
    const result = await db.select().from(documents).where(eq(documents.id, id)).limit(1);
    return result[0];
  }

  async createDocument(data: InsertDocument): Promise<Document> {
    const docData = { ...data, id: randomUUID() };
    const result = await db.insert(documents).values(docData).returning();
    return result[0];
  }

  async updateDocument(id: string, data: Partial<Document>): Promise<Document> {
    const result = await db.update(documents)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(documents.id, id))
      .returning();
    return result[0];
  }

  async deleteDocument(id: string): Promise<void> {
    const startTime = Date.now();
    try {
      // First delete all links associated with this document
      await db.delete(links).where(eq(links.srcDocId, id));
      await db.delete(links).where(eq(links.targetDocId, id));

      // Then delete the document itself
      const result = await db.delete(documents).where(eq(documents.id, id));
      const duration = Date.now() - startTime;

      console.log(`üóëÔ∏è delete_doc ok doc=${id} ms=${duration}`);
    } catch (error) {
      const duration = Date.now() - startTime;
      console.log(`üóëÔ∏è delete_doc fail doc=${id} ms=${duration} err="${error}"`);
      throw error;
    }
  }

  // Links
  async getLinks(): Promise<Link[]> {
    return db.select().from(links);
  }

  async getLinksByDocument(docId: string): Promise<Link[]> {
    return db.select().from(links).where(eq(links.srcDocId, docId));
  }

  async getLinksByCase(caseId: string): Promise<Link[]> {
    return db.select().from(links).where(eq(links.caseId, caseId));
  }

  async getLink(id: string): Promise<Link | undefined> {
    const result = await db.select().from(links).where(eq(links.id, id)).limit(1);
    return result[0];
  }

  async createLink(data: InsertLink): Promise<Link> {
    const linkData = { ...data, id: randomUUID() };
    const result = await db.insert(links).values(linkData).returning();
    return result[0];
  }

  async updateLink(id: string, data: Partial<Link>): Promise<Link> {
    const result = await db.update(links)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(links.id, id))
      .returning();
    return result[0];
  }

  async deleteLink(id: string): Promise<void> {
    await db.delete(links).where(eq(links.id, id));
  }

  async deleteAllLinksForCase(caseId: string): Promise<void> {
    await db.delete(links).where(eq(links.caseId, caseId));
    console.log(`üóëÔ∏è Deleted all links for case ${caseId} from database`);
  }

  // Document Memory
  async getDocumentSuggestions(query: string): Promise<DocumentMemory[]> {
    if (!query.trim()) {
      // Return recent documents if no query
      return db.select().from(documentMemory)
        .orderBy(documentMemory.lastUsed)
        .limit(10);
    }

    // Search for documents matching the query
    return db.select().from(documentMemory)
      .where(
        sql`${documentMemory.documentName} ILIKE ${`%${query}%`} OR 
            ${documentMemory.fileNumber} ILIKE ${`%${query}%`} OR 
            ${documentMemory.alias} ILIKE ${`%${query}%`}`
      )
      .orderBy(documentMemory.usageCount, documentMemory.lastUsed)
      .limit(10);
  }

  async saveDocumentMemory(data: InsertDocumentMemory): Promise<DocumentMemory> {
    // Check if a similar document name already exists
    const existing = await db.select().from(documentMemory)
      .where(eq(documentMemory.documentName, data.documentName))
      .limit(1);

    if (existing.length > 0) {
      // Update usage count and last used timestamp
      const result = await db.update(documentMemory)
        .set({ 
          usageCount: existing[0].usageCount + 1,
          lastUsed: new Date(),
          fileNumber: data.fileNumber || existing[0].fileNumber,
          alias: data.alias || existing[0].alias
        })
        .where(eq(documentMemory.id, existing[0].id))
        .returning();
      return result[0];
    } else {
      // Create new memory entry
      const result = await db.insert(documentMemory)
        .values({ ...data, id: randomUUID() })
        .returning();
      return result[0];
    }
  }

  async checkDuplicateDocument(caseId: string, fileName: string): Promise<Document[]> {
    const cleanFileName = fileName.replace(/\.[^/.]+$/, ""); // Remove extension

    return db.select().from(documents)
      .where(
        and(
          eq(documents.caseId, caseId),
          sql`${documents.originalName} ILIKE ${`%${cleanFileName}%`} OR 
              ${documents.title} ILIKE ${`%${cleanFileName}%`}`
        )
      );
  }

  async getStuckIndexDetections(): Promise<Document[]> {
    // Find documents with pending status older than 10 minutes
    const tenMinutesAgo = new Date(Date.now() - 10 * 60 * 1000);

    return db.select().from(documents)
      .where(
        and(
          eq(documents.indexStatus, "pending"),
          sql`${documents.indexDetectedAt} < ${tenMinutesAgo} OR ${documents.indexDetectedAt} IS NULL`
        )
      );
  }

  // Progress tracking implementation
  getProgress(key: string): any {
    return this.progressStore.get(key);
  }

  setProgress(key: string, progress: any): void {
    this.progressStore.set(key, progress);
  }

  // User operations (required for Replit Auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // OCR Cache operations
  async getOcrCacheByDocument(documentId: string): Promise<OcrCache[]> {
    return db.select().from(ocrCache)
      .where(eq(ocrCache.documentId, documentId))
      .orderBy(ocrCache.pageNumber);
  }

  async createOcrCache(data: InsertOcrCache): Promise<OcrCache> {
    const cacheData = { ...data, id: randomUUID() };
    const result = await db.insert(ocrCache).values(cacheData).returning();
    return result[0];
  }

  async updateOcrCache(id: string, data: Partial<OcrCache>): Promise<OcrCache> {
    const result = await db.update(ocrCache)
      .set(data)
      .where(eq(ocrCache.id, id))
      .returning();
    return result[0];
  }

  async deleteOcrCacheByDocument(documentId: string): Promise<void> {
    await db.delete(ocrCache).where(eq(ocrCache.documentId, documentId));
  }

  // OCR Job operations
  async createOcrJob(data: InsertOcrJob): Promise<OcrJob> {
    const jobData = { ...data, id: randomUUID() };
    const result = await db.insert(ocrJobs).values(jobData).returning();
    return result[0];
  }

  async getOcrJob(id: string): Promise<OcrJob | undefined> {
    const result = await db.select().from(ocrJobs).where(eq(ocrJobs.id, id)).limit(1);
    return result[0];
  }

  async getOcrJobByDocument(documentId: string): Promise<OcrJob | undefined> {
    const result = await db.select().from(ocrJobs)
      .where(eq(ocrJobs.documentId, documentId))
      .orderBy(sql`${ocrJobs.startedAt} DESC`)
      .limit(1);
    return result[0];
  }

  async updateOcrJob(id: string, data: Partial<OcrJob>): Promise<OcrJob> {
    const result = await db.update(ocrJobs)
      .set(data)
      .where(eq(ocrJobs.id, id))
      .returning();
    return result[0];
  }

  async getQueuedOcrJobs(limit: number = 10): Promise<OcrJob[]> {
    return db.select().from(ocrJobs)
      .where(eq(ocrJobs.status, "processing"))
      .orderBy(ocrJobs.startedAt)
      .limit(limit);
  }

  // OCR Progress tracking
  async countOcrPages(documentId: string): Promise<number> {
    const result = await db.select({ count: count() }).from(ocrCache)
      .where(eq(ocrCache.documentId, documentId));
    return result[0]?.count || 0;
  }

  async getOcrTimingStats(documentId: string): Promise<{
    avgMsPerPage: number;
    lastUpdatedAt: Date | null;
  } | null> {
    // Simplified to avoid SQL function issues - just return basic timing info
    const pages = await db.select().from(ocrCache)
      .where(eq(ocrCache.documentId, documentId))
      .limit(10); // Get last 10 pages for averaging

    if (pages.length === 0) {
      return {
        avgMsPerPage: 2000, // Default estimate: 2 seconds per page
        lastUpdatedAt: null
      };
    }

    // Calculate average processing time from metadata
    let totalTime = 0;
    let validPages = 0;
    let lastUpdated: Date | null = null;

    for (const page of pages) {
      if (page.processingMetadata && typeof page.processingMetadata === 'object') {
        const metadata = page.processingMetadata as any;
        if (metadata.processingTime && typeof metadata.processingTime === 'number') {
          totalTime += metadata.processingTime;
          validPages++;
        }
      }
      if (page.processedAt && (!lastUpdated || page.processedAt > lastUpdated)) {
        lastUpdated = page.processedAt;
      }
    }

    return {
      avgMsPerPage: validPages > 0 ? Math.round(totalTime / validPages) : 2000,
      lastUpdatedAt: lastUpdated
    };
  }

  // OCR Batch operations
  async createOcrBatch(data: InsertOcrBatch): Promise<OcrBatch> {
    const [batch] = await db.insert(ocrBatches).values({
      id: randomUUID(),
      ...data,
    }).returning();
    return batch;
  }

  async getOcrBatch(id: string): Promise<OcrBatch | undefined> {
    const [batch] = await db.select().from(ocrBatches).where(eq(ocrBatches.id, id));
    return batch;
  }

  async getBatchesByDocument(documentId: string): Promise<OcrBatch[]> {
    return db.select().from(ocrBatches).where(eq(ocrBatches.documentId, documentId));
  }

  async updateOcrBatch(id: string, data: Partial<OcrBatch>): Promise<OcrBatch> {
    const [batch] = await db.update(ocrBatches)
      .set(data)
      .where(eq(ocrBatches.id, id))
      .returning();
    return batch;
  }

  async deleteOcrBatchesByDocument(documentId: string): Promise<void> {
    await db.delete(ocrBatches).where(eq(ocrBatches.documentId, documentId));
  }

  // Page Link Position operations
  async getPageLinkPositions(documentId: string, pageNumber: number = 2): Promise<PageLinkPosition[]> {
    return db.select().from(pageLinkPositions)
      .where(and(
        eq(pageLinkPositions.documentId, documentId),
        eq(pageLinkPositions.pageNumber, pageNumber)
      ))
      .orderBy(pageLinkPositions.tabNumber);
  }

  async upsertPageLinkPositions(documentId: string, positions: InsertPageLinkPosition[]): Promise<PageLinkPosition[]> {
    const results: PageLinkPosition[] = [];

    for (const position of positions) {
      // Try to find existing position for this document/page/tab combination
      const existing = await db.select().from(pageLinkPositions)
        .where(and(
          eq(pageLinkPositions.documentId, documentId),
          eq(pageLinkPositions.pageNumber, position.pageNumber || 2),
          eq(pageLinkPositions.tabNumber, position.tabNumber)
        ))
        .limit(1);

      if (existing.length > 0) {
        // Update existing position
        const [updated] = await db.update(pageLinkPositions)
          .set({ 
            ...position,
            documentId,
            updatedAt: new Date()
          })
          .where(eq(pageLinkPositions.id, existing[0].id))
          .returning();
        results.push(updated);
      } else {
        // Create new position
        const [created] = await db.insert(pageLinkPositions)
          .values({
            id: randomUUID(),
            ...position,
            documentId,
          })
          .returning();
        results.push(created);
      }
    }

    return results;
  }

  async deletePageLinkPosition(id: string, documentId: string): Promise<void> {
    await db.delete(pageLinkPositions).where(
      and(
        eq(pageLinkPositions.id, id),
        eq(pageLinkPositions.documentId, documentId)
      )
    );
  }

  async deletePageLinkPositionsByDocument(documentId: string): Promise<void> {
    await db.delete(pageLinkPositions).where(eq(pageLinkPositions.documentId, documentId));
  }

  // Atomic update for individual page link position (for PATCH endpoint)
  async patchPageLinkPosition(
    documentId: string, 
    pageNumber: number, 
    tabNumber: string, 
    data: Partial<Pick<PageLinkPosition, 'yOffset' | 'locked' | 'xNorm' | 'yNorm' | 'targetPage' | 'isAutoAligned'>>
  ): Promise<PageLinkPosition> {
    // Find existing position
    const existing = await db.select().from(pageLinkPositions)
      .where(and(
        eq(pageLinkPositions.documentId, documentId),
        eq(pageLinkPositions.pageNumber, pageNumber),
        eq(pageLinkPositions.tabNumber, tabNumber)
      ))
      .limit(1);

    if (existing.length === 0) {
      throw new Error(`Page link position not found for document ${documentId}, page ${pageNumber}, tab ${tabNumber}`);
    }

    // If user is manually adjusting position or locking, mark as not auto-aligned
    const updateData = { ...data };
    if ('yOffset' in data || 'locked' in data || 'xNorm' in data || 'yNorm' in data) {
      updateData.isAutoAligned = false;
    }

    // Update the position atomically
    const [updated] = await db.update(pageLinkPositions)
      .set({ 
        ...updateData,
        updatedAt: new Date()
      })
      .where(eq(pageLinkPositions.id, existing[0].id))
      .returning();

    return updated;
  }
}

export const storage = new PostgresStorage();

