            data-testid="button-upload-documents"
          >
            <i className="fas fa-plus text-sm"></i>
            Upload Documents
          </button>
        </div>
      </div>
    </header>
  );
}

client/src/components/ObjectUploader.tsx.content:
import { useState, useRef } from "react";
import { useToast } from "@/hooks/use-toast";
import { api } from "@/lib/api";
import SmartDocumentInput from "./SmartDocumentInput";

interface ObjectUploaderProps {
  caseId: string;
  onUploadComplete: () => void;
  onClose: () => void;
}

export default function ObjectUploader({ caseId, onUploadComplete, onClose }: ObjectUploaderProps) {
  const [files, setFiles] = useState<File[]>([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<Record<string, number>>({});
  const [failedFiles, setFailedFiles] = useState<Set<string>>(new Set());
  const [uploadingFiles, setUploadingFiles] = useState<Set<string>>(new Set());
  const [documentInfos, setDocumentInfos] = useState<Record<string, { title: string; alias?: string; fileNumber?: string }>>({});
  const [duplicateWarnings, setDuplicateWarnings] = useState<Record<string, any[]>>({});
  const [indexStatuses, setIndexStatuses] = useState<Record<string, { status: string; count: number | null }>>({});
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  // Retry index detection for a specific file
  const retryIndexDetection = async (fileName: string) => {
    const documentId = Object.keys(indexStatuses).find(key => key === fileName);
    if (!documentId) return;

    try {
      setIndexStatuses(prev => ({ ...prev, [fileName]: { status: "pending", count: null } }));
      await api.upload.retryIndexDetection(documentId);

      // Start polling again
      pollIndexStatus(documentId, fileName);

      toast({
        title: "Index Detection",
        description: "Retrying index detection...",
      });
    } catch (error) {
      console.error("Error retrying index detection:", error);
      toast({
        title: "Retry Failed",
        description: "Failed to retry index detection. Please try again.",
        variant: "destructive",
      });
    }
  };

  // Poll for index detection status
  const pollIndexStatus = async (documentId: string, fileName: string) => {
    let tries = 0;
    const maxTries = 40; // ~20s at 500ms intervals

    const poll = async () => {
      tries++;
      try {
        const status = await api.upload.getIndexStatus(documentId);
        setIndexStatuses(prev => ({ 
          ...prev, 
          [fileName]: { 
            status: status.index_status || "error", 
            count: status.index_count 
          } 
        }));

        if (status.index_status === "ok" || status.index_status === "error" || tries >= maxTries) {
          return; // Stop polling
        }

        // Continue polling
        setTimeout(poll, 500);
      } catch (error) {
        console.error("Error polling index status:", error);
        if (tries < maxTries) {
          setTimeout(poll, 500);
        }
      }
    };

    poll();
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const droppedFiles = Array.from(e.dataTransfer.files).filter(
      file => file.type === 'application/pdf' || file.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
    );
    setFiles(prev => [...prev, ...droppedFiles]);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      const selectedFiles = Array.from(e.target.files).filter(
        file => file.type === 'application/pdf' || file.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
      );
      setFiles(prev => [...prev, ...selectedFiles]);
    }
  };

  const removeFile = (index: number) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
  };

  const uploadFiles = async () => {
    if (files.length === 0) return;

    setUploading(true);
    setFailedFiles(new Set());
    const newFailedFiles = new Set<string>();
    let successCount = 0;

    // Upload all files in parallel
    const uploadPromises = files.map(async (file) => {
      try {
        setUploadingFiles(prev => new Set(Array.from(prev).concat([file.name])));
        setUploadProgress(prev => ({ ...prev, [file.name]: 10 }));

        // Upload via object storage with real progress tracking
        const uploadResult = await api.upload.document(file, caseId, (progressPercent) => {
          setUploadProgress(prev => ({ ...prev, [file.name]: Math.round(progressPercent) }));
        });
        setUploadProgress(prev => ({ ...prev, [file.name]: 100 }));
        setUploadingFiles(prev => {
          const newSet = new Set(prev);
          newSet.delete(file.name);
          return newSet;
        });

        // Start polling for index detection status
        if (uploadResult.id) {
          setIndexStatuses(prev => ({ ...prev, [file.name]: { status: "pending", count: null } }));
          pollIndexStatus(uploadResult.id, file.name);
        }

        successCount++;

      } catch (error) {
        console.error(`Upload error for ${file.name}:`, error);
        newFailedFiles.add(file.name);
        setUploadProgress(prev => ({ ...prev, [file.name]: 0 }));
        setUploadingFiles(prev => {
          const newSet = new Set(prev);
          newSet.delete(file.name);
          return newSet;
        });
      }
    });

    // Wait for all uploads to complete
    await Promise.allSettled(uploadPromises);

    setFailedFiles(newFailedFiles);
    setUploading(false);

    if (successCount > 0) {
      toast({
        title: "Upload Progress",
        description: `${successCount} file${successCount > 1 ? 's' : ''} uploaded successfully${newFailedFiles.size > 0 ? `, ${newFailedFiles.size} failed` : ''}`,
      });

      if (newFailedFiles.size === 0) {
        setFiles([]);
        setUploadProgress({});
        onUploadComplete();
        onClose();
      } else {
        onUploadComplete(); // Refresh the document list
      }
    } else if (newFailedFiles.size > 0) {
      toast({
        title: "Upload Failed", 
        description: `${newFailedFiles.size} file${newFailedFiles.size > 1 ? 's' : ''} failed to upload. Check file size and format.`,
        variant: "destructive",
      });
    }
  };

  const retryFailedFile = async (fileName: string) => {
    const file = files.find(f => f.name === fileName);
    if (!file) return;

    setUploadingFiles(prev => new Set(Array.from(prev).concat([fileName])));
    setUploadProgress(prev => ({ ...prev, [fileName]: 10 }));

    try {
      await api.upload.document(file, caseId, (progressPercent) => {
        setUploadProgress(prev => ({ ...prev, [fileName]: Math.round(progressPercent) }));
      });
      setFailedFiles(prev => {
        const newSet = new Set(prev);
        newSet.delete(fileName);
        return newSet;
      });

      toast({
        title: "Retry Successful",
        description: `${fileName} uploaded successfully`,
      });

      onUploadComplete();

    } catch (error) {
      console.error(`Retry failed for ${fileName}:`, error);
      setUploadProgress(prev => ({ ...prev, [fileName]: 0 }));
      toast({
        title: "Retry Failed",
        description: `Failed to upload ${fileName}. Please check the file and try again.`,
        variant: "destructive",
      });
    } finally {
      setUploadingFiles(prev => {
        const newSet = new Set(prev);
        newSet.delete(fileName);
        return newSet;
      });
    }
  };

  const removeFailedFile = (fileName: string) => {
    setFiles(prev => prev.filter(f => f.name !== fileName));
    setFailedFiles(prev => {
      const newSet = new Set(prev);
      newSet.delete(fileName);
      return newSet;
    });
    setUploadProgress(prev => {
      const newProg = { ...prev };
      delete newProg[fileName];
      return newProg;
    });
  };

  if (!caseId) return null;

  return (
    <div className="mx-6 mt-6" data-testid="object-uploader">
      <div 
        className="upload-area rounded-lg p-8 text-center transition-all duration-200"
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
        data-testid="upload-area"
      >
        <div className="flex flex-col items-center gap-4">
          <div className="w-16 h-16 bg-primary/20 rounded-full flex items-center justify-center">
            <i className="fas fa-cloud-upload-alt text-primary text-2xl"></i>
          </div>
          <div>
            <h3 className="text-lg font-semibold text-foreground">Upload Legal Documents</h3>
            <p className="text-muted-foreground mt-1">Drag and drop PDF or DOCX files here, or click to browse</p>
          </div>
          <div className="flex flex-col gap-2 text-sm text-muted-foreground">
            <p>‚Ä¢ Supports PDF and DOCX files</p>
            <p>‚Ä¢ Maximum file size: 500MB</p>
            <p>‚Ä¢ Multiple files supported</p>
            <p>‚Ä¢ Files stored securely in object storage</p>
          </div>
          <input 
            type="file" 
            multiple 
            accept=".pdf,.docx" 
            className="hidden" 
            ref={fileInputRef}
            onChange={handleFileSelect}
            data-testid="file-input"
          />
          <button 
            className="px-6 py-2 bg-primary text-primary-foreground rounded-md hover:bg-primary/90 transition-colors"
            onClick={() => fileInputRef.current?.click()}
            data-testid="button-select-files"
          >
            Select Files
          </button>
        </div>
      </div>

      {files.length > 0 && (
        <div className="mt-6 bg-card rounded-lg border border-border p-4">
          <h4 className="text-sm font-medium text-foreground mb-4">Selected Files ({files.length})</h4>
          <div className="space-y-3">
            {files.map((file, index) => (
              <div key={index} className="space-y-4">
                <div className="flex items-center justify-between p-3 bg-muted/50 rounded">
                  <div className="flex items-center gap-3">
                    <i className={`fas ${file.type === 'application/pdf' ? 'fa-file-pdf text-destructive' : 'fa-file-word text-blue-600'} text-lg`}></i>
                    <div>
                      <div className="font-medium text-foreground">{file.name}</div>
                      <div className="text-sm text-muted-foreground">
                        {(file.size / 1024 / 1024).toFixed(2)} MB
                        {/* Page count will be shown after upload */}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    {uploadingFiles.has(file.name) ? (
                    <div className="flex items-center gap-2">
                      <div className="w-32 bg-muted rounded-full h-3 overflow-hidden">
                        <div
                          className="h-full bg-gradient-to-r from-blue-500 to-blue-600 rounded-full transition-all duration-300 ease-out"
                          style={{ width: `${uploadProgress[file.name] || 0}%` }}
                        />
                      </div>
                      <span className="text-sm font-medium text-foreground min-w-[3rem] text-right">
                        {Math.round(uploadProgress[file.name] || 0)}%
                      </span>
                    </div>
                  ) : failedFiles.has(file.name) ? (
                    <div className="flex items-center gap-1">
                      <span className="text-xs text-red-400 font-medium">Failed</span>
                      <button
                        onClick={() => retryFailedFile(file.name)}
                        className="p-1 hover:bg-secondary rounded text-green-400 hover:text-green-300 transition-colors"
                        title="Retry upload"
                        data-testid={`button-retry-${index}`}
                      >
                        <i className="fas fa-redo text-xs"></i>
                      </button>
                      <button
                        onClick={() => removeFailedFile(file.name)}
                        className="p-1 hover:bg-secondary rounded text-red-400 hover:text-red-300 transition-colors"
                        title="Remove failed file"
                        data-testid={`button-remove-failed-${index}`}
                      >
                        <i className="fas fa-times text-xs"></i>
                      </button>
                    </div>
                  ) : uploadProgress[file.name] === 100 ? (
                    <div className="flex items-center gap-1">
                      <span className="text-xs text-green-400 font-medium">Success</span>
                      <i className="fas fa-check text-green-400 text-xs"></i>
                      {indexStatuses[file.name] && (
                        <div className="ml-2 flex items-center gap-1">
                          {indexStatuses[file.name].status === "pending" ? (
                            <span className="text-xs text-yellow-400 font-medium">üîç Detecting index...</span>
                          ) : indexStatuses[file.name].status === "ok" ? (
                            indexStatuses[file.name].count === 0 ? (
                              <span className="text-xs text-orange-400 font-medium">
                                üìÑ No Index found ‚Äî open Review to set start pages manually
                              </span>
                            ) : (
                              <span className="text-xs text-blue-400 font-medium">
                                üìã Index items detected: {indexStatuses[file.name].count}
                              </span>
                            )
                          ) : indexStatuses[file.name].status === "error" ? (
                            <div className="flex items-center gap-1">
                              <span className="text-xs text-red-400 font-medium">‚ùå Index detection failed</span>
                              <button
                                onClick={() => retryIndexDetection(file.name)}
                                className="text-xs text-blue-400 hover:text-blue-300 underline ml-1"
                                data-testid={`button-retry-index-${index}`}
                              >
                                Retry detection
                              </button>
                            </div>
                          ) : null}
                        </div>
                      )}
                    </div>
                  ) : (
                    <button
                      onClick={() => removeFile(index)}
                      className="p-1 hover:bg-secondary rounded text-muted-foreground hover:text-foreground transition-colors"
                      data-testid={`button-remove-file-${index}`}
                    >
                      <i className="fas fa-times text-sm"></i>
                    </button>
                  )}
                  </div>
                </div>

                {/* Optional Smart Document Input for each file */}
                {!uploadProgress[file.name] || uploadProgress[file.name] < 100 ? (
                  <details className="group">
                    <summary className="cursor-pointer p-2 bg-gray-100 hover:bg-gray-200 rounded text-sm text-gray-600 transition-colors">
                      <span className="group-open:hidden">üìù Add document details (optional)</span>
                      <span className="hidden group-open:inline">üìù Document details</span>
                    </summary>
                    <div className="mt-2 p-3 bg-blue-50 rounded border border-blue-200">
                      <SmartDocumentInput
                        caseId={caseId}
                        fileName={file.name}
                        onDocumentInfo={(info) => {
                          setDocumentInfos(prev => ({ ...prev, [file.name]: info }));
                        }}
                        onDuplicateWarning={(duplicates) => {
                          setDuplicateWarnings(prev => ({ ...prev, [file.name]: duplicates }));
                        }}
                      />
                    </div>
                  </details>
                ) : null}
              </div>
            ))}
          </div>

          <div className="flex items-center gap-3 mt-4">
            <button
              onClick={uploadFiles}
              disabled={uploading || files.length === 0}
              className="px-6 py-2 bg-primary text-primary-foreground rounded-md hover:bg-primary/90 transition-colors disabled:opacity-50 flex items-center gap-2"
              data-testid="button-upload-files"
            >
              {uploading ? (
                <>
                  <i className="fas fa-spinner fa-spin"></i>
                  <div className="flex flex-col items-start gap-1">
                    <span>Uploading... ({Object.keys(uploadProgress).filter(k => uploadProgress[k] === 100).length}/{files.length})</span>
                    <div className="w-32 bg-muted rounded-full h-2">
                      <div
                        className="h-full bg-gradient-to-r from-green-500 to-green-600 rounded-full transition-all duration-300"
                        style={{ 
                          width: `${Math.round((Object.keys(uploadProgress).filter(k => uploadProgress[k] === 100).length / files.length) * 100)}%` 
                        }}
                      />
                    </div>
                  </div>
                </>
              ) : (
                <>
                  <i className="fas fa-upload"></i>
                  Upload {files.filter(f => !failedFiles.has(f.name) && uploadProgress[f.name] !== 100).length} File{files.filter(f => !failedFiles.has(f.name) && uploadProgress[f.name] !== 100).length > 1 ? 's' : ''}
                </>
              )}
            </button>

            {failedFiles.size > 0 && (
              <button
                onClick={() => {
                  const failedFileList = files.filter(f => failedFiles.has(f.name));
                  failedFileList.forEach(f => retryFailedFile(f.name));
                }}
                disabled={uploading}
                className="px-4 py-2 bg-yellow-600 text-white rounded-md hover:bg-yellow-700 transition-colors disabled:opacity-50 flex items-center gap-2"
                data-testid="button-retry-all-failed"
              >
                <i className="fas fa-redo"></i>
                Retry All Failed ({failedFiles.size})
              </button>
            )}

            <button
              onClick={onClose}
              className="px-4 py-2 bg-secondary text-secondary-foreground rounded-md hover:bg-secondary/80 transition-colors"
              data-testid="button-cancel-upload"
            >
              {failedFiles.size > 0 || (files.length > 0 && files.some(f => uploadProgress[f.name] === 100)) ? 'Close' : 'Cancel'}
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/ReviewModal.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import type { Document, Link } from "@shared/schema";

interface ReviewModalProps {
  documentId: string;
  caseId: string;
  onClose: () => void;
}

export default function ReviewModal({ documentId, caseId, onClose }: ReviewModalProps) {
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedLinkId, setSelectedLinkId] = useState<string | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: document } = useQuery({
    queryKey: ['/api/documents', documentId],
    queryFn: () => api.documents.getById(documentId),
  });

  const { data: links = [] } = useQuery({
    queryKey: ['/api/documents', documentId, 'links'],
    queryFn: () => api.links.getByDocumentId(documentId),
  });

  const updateLinkMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<Link> }) => 
      api.links.update(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/documents', documentId, 'links'] });
    },
  });

  const updateDocumentMutation = useMutation({
    mutationFn: (data: Partial<Document>) => 
      api.documents.update(documentId, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/documents', documentId] });
      queryClient.invalidateQueries({ queryKey: ['/api/cases', caseId, 'documents'] });
    },
  });

  const regeneratePDFMutation = useMutation({
    mutationFn: () => api.documents.processHyperlinks([documentId]),
    onSuccess: () => {
      toast({
        title: "PDF Regeneration Started",
        description: "The document is being reprocessed with updated links",
      });
    },
  });

  const confirmLink = (linkId: string) => {
    updateLinkMutation.mutate({
      id: linkId,
      data: { status: 'confirmed' }
    });
  };

  const removeLink = (linkId: string) => {
    updateLinkMutation.mutate({
      id: linkId,
      data: { status: 'removed' }
    });
  };

  const markAsApproved = () => {
    updateDocumentMutation.mutate({ reviewStatus: 'approved' });
    toast({
      title: "Document Approved",
      description: "Document has been marked as court-ready",
    });
    onClose();
  };

  const regeneratePDF = () => {
    regeneratePDFMutation.mutate();
  };

  const nextPage = () => {
    if (document && currentPage < (document.pageCount || 1)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const previousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const highlightLink = (linkId: string) => {
    setSelectedLinkId(linkId);
  };

  const confirmedLinks = links.filter(link => link.status === 'confirmed');
  const pendingLinks = links.filter(link => link.status === 'auto');

  if (!document) {
    return (
      <div className="fixed inset-0 bg-background/80 backdrop-blur-sm z-50 flex items-center justify-center">
        <div className="text-center">
          <i className="fas fa-spinner fa-spin text-2xl text-muted-foreground mb-4"></i>
          <p className="text-muted-foreground">Loading document...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-background/80 backdrop-blur-sm z-50" data-testid="review-modal">
      <div className="flex min-h-full items-center justify-center p-4">
        <div className="bg-card border border-border rounded-lg w-full max-w-7xl h-[90vh] flex flex-col">
          {/* Modal Header */}
          <div className="flex items-center justify-between p-6 border-b border-border">
            <div>
              <h3 className="text-xl font-semibold text-foreground">Link Review - {document.originalName}</h3>
              <p className="text-sm text-muted-foreground mt-1">Review and edit hyperlinks before court submission</p>
            </div>
            <div className="flex items-center gap-3">
              <button 
                className="px-4 py-2 bg-primary text-primary-foreground rounded-md hover:bg-primary/90 transition-colors flex items-center gap-2"
                onClick={regeneratePDF}
                disabled={regeneratePDFMutation.isPending}
                data-testid="button-regenerate-pdf"
              >
                <i className={`fas ${regeneratePDFMutation.isPending ? 'fa-spinner fa-spin' : 'fa-sync-alt'} mr-2`}></i>
                Regenerate PDF
              </button>
              <button 
                className="px-4 py-2 bg-secondary text-secondary-foreground rounded-md hover:bg-secondary/80 transition-colors flex items-center gap-2"
                onClick={markAsApproved}
                disabled={updateDocumentMutation.isPending}
                data-testid="button-mark-approved"
              >
                <i className="fas fa-check mr-2"></i>
                Mark as Approved
              </button>
              <button 
                className="p-2 hover:bg-secondary rounded text-muted-foreground hover:text-foreground transition-colors"
                onClick={onClose}
                data-testid="button-close-modal"
              >
                <i className="fas fa-times"></i>
              </button>
            </div>
          </div>

          {/* Split Screen Content */}
          <div className="flex-1 flex overflow-hidden">
            {/* PDF Viewer */}
            <div className="flex-1 bg-muted/30 p-4 overflow-auto" data-testid="pdf-viewer">
              <div className="bg-white rounded shadow-lg mx-auto" style={{ width: '600px', height: '800px', position: 'relative' }}>
                {/* PDF content placeholder */}
                <div className="w-full h-full flex items-center justify-center text-gray-400">
                  <div className="text-center">
                    <i className="fas fa-file-pdf text-6xl mb-4"></i>
                    <p className="text-lg font-medium">PDF Viewer</p>
                    <p className="text-sm">{document.originalName}</p>
                    <p className="text-xs mt-2">Page {currentPage} of {document.pageCount}</p>
                  </div>
                </div>

                {/* Link annotations overlay */}
                {links
                  .filter(link => link.srcPage === currentPage && link.bbox && link.status !== 'removed')
                  .map((link, index) => (
                    <div
                      key={link.id}
                      className={`link-annotation absolute rounded cursor-pointer ${
                        selectedLinkId === link.id ? 'bg-primary/30 border-primary' : ''
                      }`}
                      style={{
                        top: `${120 + index * 80}px`,
                        left: `${50 + index * 20}px`,
                        width: '180px',
                        height: '20px'
                      }}
                      title={`Link to page ${link.targetPage}`}
                      onClick={() => highlightLink(link.id)}
                      data-testid={`link-annotation-${link.id}`}
                    />
                  ))}
              </div>

              {/* PDF Navigation */}
              <div className="flex items-center justify-center gap-4 mt-4">
                <button 
                  className="p-2 bg-card border border-border rounded hover:bg-secondary transition-colors disabled:opacity-50"
                  onClick={previousPage}
                  disabled={currentPage === 1}
                  data-testid="button-previous-page"
                >
                  <i className="fas fa-chevron-left"></i>
                </button>
                <span className="text-sm text-muted-foreground" data-testid="page-info">
                  Page {currentPage} of {document.pageCount || 1}
                </span>
                <button 
                  className="p-2 bg-card border border-border rounded hover:bg-secondary transition-colors disabled:opacity-50"
                  onClick={nextPage}
                  disabled={currentPage === (document.pageCount || 1)}
                  data-testid="button-next-page"
                >
                  <i className="fas fa-chevron-right"></i>
                </button>
              </div>
            </div>

            {/* Links Panel */}
            <div className="w-96 border-l border-border bg-card/50 flex flex-col" data-testid="links-panel">
              <div className="p-4 border-b border-border">
                <h4 className="font-semibold text-foreground">Detected Links</h4>
                <p className="text-sm text-muted-foreground mt-1">{links.length} links found</p>
              </div>

              <div className="flex-1 overflow-auto p-4 space-y-3">
                {links.filter(link => link.status !== 'removed').map((link) => (
                  <div 
                    key={link.id} 
                    className={`bg-card border border-border rounded-lg p-4 hover:bg-secondary/50 transition-colors ${
                      selectedLinkId === link.id ? 'border-primary' : ''
                    }`}
                    data-testid={`link-item-${link.id}`}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <span className="text-xs px-2 py-1 bg-primary/20 text-primary rounded">
                            Page {link.srcPage}
                          </span>
                          <i className="fas fa-arrow-right text-muted-foreground text-xs"></i>
                          <span className="text-xs px-2 py-1 bg-accent/20 text-accent-foreground rounded">
                            Page {link.targetPage}
                          </span>
                        </div>
                        <p className="text-sm text-foreground font-medium">
                          Link reference detected
                        </p>
                        <p className="text-xs text-muted-foreground mt-1">
                          {link.why || 'Reference found in document'}
                        </p>
                        <div className="flex items-center gap-2 mt-2">
                          <div className="flex items-center gap-1">
                            <i className="fas fa-brain text-primary text-xs"></i>
                            <span className="text-xs text-muted-foreground">
                              AI Confidence: {Math.round(parseFloat(link.confidence || "0") * 100)}%
                            </span>
                          </div>
                          {link.status === 'confirmed' && (
                            <span className="text-xs px-2 py-1 bg-green-500/20 text-green-400 rounded">
                              Confirmed
                            </span>
                          )}
                        </div>
                      </div>
                      <div className="flex gap-1 ml-2">
                        <button 
                          className={`p-1 hover:bg-secondary rounded text-green-400 hover:text-green-300 transition-colors ${
                            link.status === 'confirmed' ? 'opacity-50' : ''
                          }`}
                          title="Confirm Link"
                          onClick={() => confirmLink(link.id)}
                          disabled={link.status === 'confirmed'}
                          data-testid={`button-confirm-${link.id}`}
                        >
                          <i className="fas fa-check text-xs"></i>
                        </button>
                        <button 
                          className="p-1 hover:bg-secondary rounded text-red-400 hover:text-red-300 transition-colors"
                          title="Remove Link"
                          onClick={() => removeLink(link.id)}
                          data-testid={`button-remove-${link.id}`}
                        >
                          <i className="fas fa-times text-xs"></i>
                        </button>
                      </div>
                    </div>
                  </div>
                ))}

                {links.filter(link => link.status !== 'removed').length === 0 && (
                  <div className="text-center p-8 text-muted-foreground">
                    <i className="fas fa-link text-2xl mb-4"></i>
                    <p>No links detected</p>
                  </div>
                )}
              </div>

              {/* Panel Footer */}
              <div className="p-4 border-t border-border">
                <div className="grid grid-cols-3 gap-2 text-center text-xs">
                  <div className="p-2 bg-muted/50 rounded">
                    <div className="font-medium text-foreground" data-testid="stat-total-links">
                      {links.filter(link => link.status !== 'removed').length}
                    </div>
                    <div className="text-muted-foreground">Total</div>
                  </div>
                  <div className="p-2 bg-green-500/20 rounded">
                    <div className="font-medium text-green-400" data-testid="stat-confirmed-links">
                      {confirmedLinks.length}
                    </div>
                    <div className="text-muted-foreground">Confirmed</div>
                  </div>
                  <div className="p-2 bg-primary/20 rounded">
                    <div className="font-medium text-primary" data-testid="stat-pending-links">
                      {pendingLinks.length}
                    </div>
                    <div className="text-muted-foreground">Pending</div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/Sidebar.tsx.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import type { Case } from "@shared/schema";

export default function Sidebar() {
  const [location, setLocation] = useLocation();
  const [showCaseManager, setShowCaseManager] = useState(false);
  const [editingCase, setEditingCase] = useState<Case | null>(null);
  const [newCaseTitle, setNewCaseTitle] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: cases = [] } = useQuery({
    queryKey: ['/api/cases'],
    queryFn: () => api.cases.getAll(),
  });

  const updateCaseMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<Case> }) => api.cases.update(id, data),
    onSuccess: () => {
      toast({ title: "Case updated successfully" });
      queryClient.invalidateQueries({ queryKey: ['/api/cases'] });
      setEditingCase(null);
    },
    onError: () => {
      toast({ title: "Failed to update case", variant: "destructive" });
    },
  });

  const deleteCaseMutation = useMutation({
    mutationFn: (id: string) => api.cases.delete(id),
    onSuccess: () => {
      toast({ title: "Case deleted successfully" });
      queryClient.invalidateQueries({ queryKey: ['/api/cases'] });
    },
    onError: () => {
      toast({ title: "Failed to delete case", variant: "destructive" });
    },
  });

  const createCaseMutation = useMutation({
    mutationFn: (data: { caseNumber: string; title: string; status: string }) => api.cases.create(data),
    onSuccess: (newCase) => {
      toast({ title: "Case created successfully" });
      queryClient.invalidateQueries({ queryKey: ['/api/cases'] });
      setLocation(`/cases/${newCase.id}`);
      setNewCaseTitle("");
    },
    onError: () => {
      toast({ title: "Failed to create case", variant: "destructive" });
    },
  });

  const handleEditCase = (caseItem: Case) => {
    setEditingCase(caseItem);
  };

  const handleSaveCase = () => {
    if (editingCase) {
      updateCaseMutation.mutate({
        id: editingCase.id,
        data: { title: editingCase.title }
      });
    }
  };

  const handleDeleteCase = (caseId: string) => {
    if (confirm("Are you sure you want to delete this case? This will also delete all associated documents.")) {
      deleteCaseMutation.mutate(caseId);
    }
  };

  const handleCreateCase = () => {
    if (newCaseTitle.trim()) {
      createCaseMutation.mutate({
        caseNumber: `2024-CV-${Date.now().toString().slice(-6)}`,
        title: newCaseTitle.trim(),
        status: "active"
      });
    }
  };

  const navigationItems = [
    { path: "/case-management", icon: "fas fa-briefcase", label: "1. Create Case", step: 1 },
    { path: "/", icon: "fas fa-folder-open", label: "2. Upload Documents", step: 2 },
    { path: "/ocr", icon: "fas fa-eye", label: "3. OCR Processing", step: 3 },
    { path: "/links", icon: "fas fa-link", label: "4. AI Hyperlinking", step: 4 },
    { path: "/review", icon: "fas fa-check-circle", label: "5. Lawyer Review", step: 5 },
    { path: "/court-ready", icon: "fas fa-download", label: "6. Court Submit", step: 6 },
  ];

  return (
    <aside className="w-64 bg-card border-r border-border flex flex-col" data-testid="sidebar">
      <div className="p-6 border-b border-border">
        <div className="flex items-center gap-3">
          <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
            <i className="fas fa-gavel text-primary-foreground text-sm"></i>
          </div>
          <div>
            <h1 className="text-xl font-bold text-foreground">hyperlinklaw.com</h1>
            <p className="text-xs text-muted-foreground">Legal Document System</p>
          </div>
        </div>
      </div>

      <nav className="flex-1 p-4 space-y-2" data-testid="navigation">
        {navigationItems.map((item, index) => (
          <Link key={index} href={item.path}>
            <span
              className={`flex items-center gap-3 px-3 py-2 rounded-md font-medium transition-colors cursor-pointer ${
                location === item.path
                  ? "bg-primary text-primary-foreground"
                  : "hover:bg-secondary text-muted-foreground hover:text-foreground"
              }`}
              data-testid={`nav-${item.label.toLowerCase().replace(" ", "-")}`}
            >
              <i className={`${item.icon} text-sm`}></i>
              {item.label}
            </span>
          </Link>
        ))}

        <div className="pt-4 border-t border-border">
          <button
            onClick={() => setShowCaseManager(!showCaseManager)}
            className="w-full flex items-center gap-3 px-3 py-2 rounded-md font-medium transition-colors hover:bg-secondary text-muted-foreground hover:text-foreground"
            data-testid="button-manage-cases"
          >
            <i className="fas fa-cog text-sm"></i>
            Manage Cases
            <i className={`fas fa-chevron-${showCaseManager ? 'up' : 'down'} text-xs ml-auto`}></i>
          </button>

          {showCaseManager && (
            <div className="mt-2 space-y-2">
              <div className="px-3">
                <div className="flex gap-2">
                  <input
                    type="text"
                    placeholder="New case title..."
                    value={newCaseTitle}
                    onChange={(e) => setNewCaseTitle(e.target.value)}
                    className="flex-1 px-2 py-1 text-xs bg-input border border-border rounded"
                    data-testid="input-new-case"
                  />
                  <button
                    onClick={handleCreateCase}
                    disabled={!newCaseTitle.trim() || createCaseMutation.isPending}
                    className="px-2 py-1 text-xs bg-primary text-primary-foreground rounded hover:bg-primary/90 disabled:opacity-50"
                    data-testid="button-create-case"
                  >
                    <i className="fas fa-plus"></i>
                  </button>
                </div>
              </div>

              <div className="max-h-48 overflow-y-auto space-y-1">
                {cases.map((caseItem) => (
                  <div
                    key={caseItem.id}
                    className="mx-3 p-2 bg-muted/50 rounded text-xs"
                    data-testid={`case-item-${caseItem.id}`}
                  >
                    {editingCase?.id === caseItem.id ? (
                      <div className="space-y-2">
                        <input
                          type="text"
                          value={editingCase.title}
                          onChange={(e) => setEditingCase({ ...editingCase, title: e.target.value })}
                          className="w-full px-2 py-1 text-xs bg-input border border-border rounded"
                          data-testid={`input-edit-case-${caseItem.id}`}
                        />
                        <div className="flex gap-1">
                          <button
                            onClick={handleSaveCase}
                            disabled={updateCaseMutation.isPending}
                            className="px-2 py-1 text-xs bg-green-600 text-white rounded hover:bg-green-700"
                            data-testid={`button-save-case-${caseItem.id}`}
                          >
                            <i className="fas fa-check"></i>
                          </button>
                          <button
                            onClick={() => setEditingCase(null)}
                            className="px-2 py-1 text-xs bg-gray-600 text-white rounded hover:bg-gray-700"
                            data-testid={`button-cancel-edit-${caseItem.id}`}
                          >
                            <i className="fas fa-times"></i>
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div>
                        <div className="font-medium text-foreground truncate">{caseItem.title}</div>
                        <div className="text-muted-foreground">{caseItem.caseNumber}</div>
                        <div className="flex gap-1 mt-2">
                          <button
                            onClick={() => setLocation(`/cases/${caseItem.id}`)}
                            className="px-2 py-1 text-xs bg-blue-600 text-white rounded hover:bg-blue-700"
                            title="Open Case"
                            data-testid={`button-open-case-${caseItem.id}`}
                          >
                            <i className="fas fa-folder-open"></i>
                          </button>
                          <button
                            onClick={() => handleEditCase(caseItem)}
                            className="px-2 py-1 text-xs bg-yellow-600 text-white rounded hover:bg-yellow-700"
                            title="Edit Case"
                            data-testid={`button-edit-case-${caseItem.id}`}
                          >
                            <i className="fas fa-edit"></i>
                          </button>
                          <button
                            onClick={() => handleDeleteCase(caseItem.id)}
                            disabled={deleteCaseMutation.isPending}
                            className="px-2 py-1 text-xs bg-red-600 text-white rounded hover:bg-red-700"
                            title="Delete Case"
                            data-testid={`button-delete-case-${caseItem.id}`}
                          >
                            <i className="fas fa-trash"></i>
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </nav>

      <div className="p-4 border-t border-border">
        <div className="flex items-center gap-3">
          <div className="w-8 h-8 bg-muted rounded-full flex items-center justify-center">
            <i className="fas fa-user text-muted-foreground text-sm"></i>
          </div>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-foreground truncate">Legal Associate</p>
            <p className="text-xs text-muted-foreground truncate">Active Session</p>
          </div>
        </div>
      </div>
    </aside>
  );
}

