                        )}
                        {data.review && (
                          <a
                            href={data.review.pdfUrl}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="inline-block px-2 py-1 bg-blue-600 hover:bg-blue-500 text-white rounded text-xs"
                          >
                            üìÑ PDF
                          </a>
                        )}
