              {result.review && (
                <div className="flex gap-2">
                  <a
                    href={result.review.pdfUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="px-3 py-1 bg-blue-600 hover:bg-blue-500 text-white rounded text-sm font-medium"
                  >
                    üìÑ Open Master PDF
                  </a>
                  <button
                    onClick={() => console.log("Review data:", result.review)}
                    className="px-3 py-1 bg-emerald-600 hover:bg-emerald-500 text-white rounded text-sm font-medium"
                  >
                    üîç View Links ({result.review.total})
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="space-y-3">
              <div className="flex items-center gap-2 text-red-400">
                <span>‚ùå</span>
                <span className="font-semibold">Detection Failed</span>
              </div>
              <div className="bg-red-900/20 border border-red-500/30 rounded p-3">
                <div className="text-red-300 text-sm font-mono">
                  {result.error}
                </div>
                {result.stderr && (
                  <details className="mt-2">
                    <summary className="text-red-400 cursor-pointer text-xs">Show Error Details</summary>
                    <pre className="mt-1 text-xs text-red-300 whitespace-pre-wrap">{result.stderr}</pre>
                  </details>
                )}
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

client/src/components/ReviewLinksButton..content:
import React from "react";

interface ReviewLinksButtonProps {
  docKey: "supp13" | "doc63" | "tr5";
  title: string;
  className?: string;
}

export default function ReviewLinksButton({ docKey, title, className = "" }: ReviewLinksButtonProps) {
  const [open, setOpen] = React.useState(false);
  const [loading, setLoading] = React.useState(false);
  const [pdfUrl, setPdfUrl] = React.useState("");
  const [links, setLinks] = React.useState<Array<{tab_number:number|string; brief_page:number; tr_dest_page:number; is_marker?:boolean; type?: string; title?: string;}>>([]);

  async function load() {
    setLoading(true);
    try {
      const response = await fetch(`/api/review-links/${docKey}`);
      const data = await response.json();

      if (data.ok) {
        setPdfUrl(data.pdfUrl);
        setLinks(data.links || []);
      } else {
        console.error("Failed to load links:", data.error);
      }
    } catch (error) {
      console.error("Failed to load links:", error);
    }
    setLoading(false);
  }

  const labels = {
    supp13: { color: "blue", count: "13" },
    doc63: { color: "green", count: "4" },
    tr5: { color: "purple", count: "5" }
  };

  const config = labels[docKey];

  return (
    <>
      <button
        className={`px-4 py-2 rounded font-medium transition-colors ${className} ${
          config.color === "blue" ? "bg-blue-600 hover:bg-blue-500" :
          config.color === "green" ? "bg-green-600 hover:bg-green-500" :
          "bg-purple-600 hover:bg-purple-500"
        } text-white`}
        onMouseEnter={() => { if (!open && links.length === 0) load(); }} // prefetch
        onClick={() => { setOpen(true); if (links.length === 0) load(); }}
        data-testid={`button-review-links-${docKey}`}
      >
        üîó Review {config.count} Links
      </button>

      {open && (
        <div className="fixed inset-0 bg-black/60 z-50 flex items-center justify-center">
          <div className="w-[900px] max-h-[80vh] bg-slate-900 rounded-xl shadow-xl p-4 overflow-hidden">
            <div className="flex items-center justify-between">
              <h2 className="text-lg font-semibold text-white">
                {title} ‚Äî {links.length} Links 
                {docKey === "doc63" && (
                  <span className="text-sm font-normal text-gray-300 ml-2">
                    (4 üìë Tabs + 2 üìã Exhibits A, B)
                  </span>
                )}
              </h2>
              <div className="space-x-2">
                <a 
                  className="underline text-blue-400 hover:text-blue-300" 
                  href={pdfUrl} 
                  target="_blank" 
                  rel="noreferrer"
                  data-testid={`link-master-pdf-${docKey}`}
                >
                  Open Master PDF
                </a>
                <button 
                  className="px-2 py-1 bg-slate-700 hover:bg-slate-600 rounded text-white" 
                  onClick={() => setOpen(false)}
                  data-testid={`button-close-review-${docKey}`}
                >
                  Close
                </button>
              </div>
            </div>

            {loading ? (
              <div className="p-6 text-white">Loading‚Ä¶</div>
            ) : (
              <div className="mt-3 overflow-auto max-h-[65vh] space-y-2">
                {links.length === 0 ? (
                  <div className="p-6 text-center text-gray-400">
                    No links found. Run the deterministic rebuild first.
                  </div>
                ) : (
                  links.map(x => (
                    <div key={`${x.type}-${x.tab_number}`} className={`flex items-center justify-between border rounded-md p-2 ${
                      x.type === 'exhibit' 
                        ? 'bg-purple-900 border-purple-700' 
                        : 'bg-slate-800 border-slate-700'
                    }`}>
                      <div className="text-white flex items-center gap-2">
                        <div className="flex items-center gap-1">
                          <span className={`text-sm px-2 py-1 rounded text-xs font-semibold ${
                            x.type === 'exhibit' 
                              ? 'bg-purple-600 text-purple-100' 
                              : 'bg-blue-600 text-blue-100'
                          }`}>
                            {x.type === 'exhibit' ? 'üìã EXHIBIT' : (docKey === "tr5" ? "üìÑ ITEM" : "üìë TAB")}
                          </span>
                          <b>{x.tab_number}</b>
                          {x.is_marker && (
                            <span className="px-1.5 py-0.5 bg-yellow-500/20 text-yellow-300 text-xs rounded border border-yellow-500/30 font-mono">
                              ‚ú® marker
                            </span>
                          )}
                        </div>

                        {x.type === 'exhibit' && x.title && (
                          <span className="text-purple-200 italic">‚Äî {x.title}</span>
                        )}

                        <span className="text-gray-300">
                          ‚Äî page {x.brief_page}
                          {x.tr_dest_page !== x.brief_page && ` ‚Üí ${x.tr_dest_page}`}
                        </span>

                        <div className="flex items-center gap-1 text-sm">
                          <a 
                            className="underline text-blue-400 hover:text-blue-300" 
                            href={`${pdfUrl}#page=${x.brief_page}`} 
                            target="_blank" 
                            rel="noreferrer"
                            data-testid={`link-${docKey}-${x.type}-${x.tab_number}-source`}
                          >
                            üìÑ open page
                          </a>
                          {x.tr_dest_page !== x.brief_page && (
                            <>
                              <span className="text-gray-500">|</span>
                              <a 
                                className="underline text-blue-400 hover:text-blue-300" 
                                href={`${pdfUrl}#page=${x.tr_dest_page}`} 
                                target="_blank" 
                                rel="noreferrer"
                                data-testid={`link-${docKey}-${x.type}-${x.tab_number}-dest`}
                              >
                                üéØ open dest
                              </a>
                            </>
                          )}
                        </div>
                      </div>
                      <InlineEdit docKey={docKey} tab={x.tab_number} current={x.tr_dest_page} onUpdate={load} />
                    </div>
                  ))
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </>
  );
}

function InlineEdit({ docKey, tab, current, onUpdate }: {docKey: string; tab: number|string; current: number; onUpdate: () => void}) {
  const [val, setVal] = React.useState<number>(current);
  const [busy, setBusy] = React.useState(false);

  React.useEffect(() => {
    setVal(current);
  }, [current]);

  return (
    <div className="flex items-center gap-2">
      <input
        type="number" 
        min={1}
        className="w-24 px-2 py-1 bg-slate-900 border border-slate-700 rounded text-white"
        value={val} 
        onChange={e => setVal(Number(e.target.value))}
        data-testid={`input-${docKey}-tab-${tab}-page`}
      />
      <button
        disabled={busy}
        className="px-2 py-1 bg-emerald-700 hover:bg-emerald-600 disabled:bg-emerald-800 rounded text-white"
        onClick={async () => {
          setBusy(true);
          try {
            const response = await fetch(`/api/review-links/${docKey}/override`, {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ tab_number: tab, tr_dest_page: val })
            });
            const data = await response.json();

            if (!data.ok) {
              alert(data.error || "Update failed");
            } else {
              onUpdate();
            }
          } catch (error) {
            console.error("Override failed:", error);
            alert("Failed to update link");
          }
          setBusy(false);
        }}
        data-testid={`button-${docKey}-tab-${tab}-save`}
      >
        {busy ? "..." : "Save"}
      </button>
    </div>
  );
}

server/routes/deterministic.ts.content:
import { Router } from "express";
import fs from "fs";
import path from "path";
import { spawn } from "child_process";

const r = Router();

// Trigger deterministic rebuild of all three document types
r.post("/api/rebuild-deterministic", (req, res) => {
  console.log("üöÄ Starting deterministic rebuild...");

  const scriptPath = path.resolve("scripts/rebuild_index_deterministic.sh");

  if (!fs.existsSync(scriptPath)) {
    return res.status(500).json({
      ok: false,
      error: "Rebuild script not found"
    });
  }

  // Make script executable
  try {
    fs.chmodSync(scriptPath, '755');
  } catch (error) {
    console.error("Could not make script executable:", error);
  }

  const childProcess = spawn('bash', [scriptPath], {
    stdio: 'pipe',
    env: { ...process.env, PATH: process.env.PATH }
  });

  let stdout = '';
  let stderr = '';

  childProcess.stdout.on('data', (data) => {
    const output = data.toString();
    stdout += output;
    console.log(output);
  });

  childProcess.stderr.on('data', (data) => {
    const output = data.toString();
    stderr += output;
    console.error(output);
  });

  childProcess.on('close', (code) => {
    if (code === 0) {
      // Success - load results from all three folders
      const results = {
        supp13: loadFolderResults('out/review_13'),
        doc63: loadFolderResults('out/review_63'),
        tr5: loadFolderResults('out/review_tr5')
      };

      res.json({
        ok: true,
        message: "Deterministic rebuild completed successfully",
        results,
        stdout,
        totalLinks: (results.supp13?.total || 0) + (results.doc63?.total || 0) + (results.tr5?.total || 0)
      });
    } else {
      res.status(500).json({
        ok: false,
        error: "Deterministic rebuild failed",
        code,
        stdout,
        stderr
      });
    }
  });
});

// Build document with dynamic hyperlink detection
r.post("/api/build-document-dynamic", (req, res) => {
  let body = "";
  req.on("data", chunk => body += chunk);
  req.on("end", () => {
    const { briefPath, trialPath, indexPages, outputDir, indexOnly, reviewJson } = JSON.parse(body || "{}");

    if (!briefPath) {
      return res.status(400).json({ ok: false, error: "Brief path is required" });
    }

    // Generate unique output directory if not provided
    const finalOutputDir = outputDir || `out/dynamic_${Date.now()}`;

    let args = ["scripts/dynamic_hyperlink_processor.py", "--brief", briefPath];

    if (trialPath) {
      args.push("--trial", trialPath);
    }

    if (indexPages) {
      args.push("--index_pages", indexPages);
    }

    args.push("--out_dir", finalOutputDir);

    if (indexOnly) {
      args.push("--index_only");
    }

    if (reviewJson) {
      args.push("--review_json");
    }

    args.push("--verbose");

    console.log(`üîß Processing document dynamically:`, "python", args.join(" "));

    const childProcess = spawn('python', args, { stdio: 'pipe' });

    let stdout = '';
    let stderr = '';

    childProcess.stdout.on('data', (data) => {
      const output = data.toString();
      stdout += output;
      console.log(output);
    });

    childProcess.stderr.on('data', (data) => {
      const output = data.toString();
      stderr += output;
      console.error(output);
    });

    childProcess.on('close', (code) => {
      if (code === 0) {
        const results = loadFolderResults(finalOutputDir);
        res.json({
          ok: true,
          message: "Dynamic document processing completed successfully",
          results,
          stdout,
          outputDir: finalOutputDir
        });
      } else {
        res.status(500).json({
          ok: false,
          error: "Dynamic document processing failed",
          code,
          stdout,
          stderr,
          outputDir: finalOutputDir
        });
      }
    });
  });
});

// Detect index items in a document (preview mode)
r.post("/api/detect-index", (req, res) => {
  let body = "";
  req.on("data", chunk => body += chunk);
  req.on("end", () => {
    const { documentPath, indexPages } = JSON.parse(body || "{}");

    if (!documentPath) {
      return res.status(400).json({ ok: false, error: "Document path is required" });
    }

    let args = ["scripts/dynamic_index_detector.py", "--document", documentPath, "--verbose"];

    if (indexPages) {
      args.push("--index_pages", indexPages);
    }

    console.log(`üîç Detecting index items in:`, "python", args.join(" "));

    const childProcess = spawn('python', args, { stdio: 'pipe' });

    let stdout = '';
    let stderr = '';

    childProcess.stdout.on('data', (data) => {
      const output = data.toString();
      stdout += output;
      console.log(output);
    });

    childProcess.stderr.on('data', (data) => {
      const output = data.toString();
      stderr += output;
      console.error(output);
    });

    childProcess.on('close', (code) => {
      if (code === 0) {
        try {
          // Parse the JSON output from the script
          const lines = stdout.split('\n');
          let jsonOutput = '';
          let inJsonSection = false;

          for (const line of lines) {
            if (line.trim().startsWith('{')) {
              inJsonSection = true;
            }
            if (inJsonSection) {
              jsonOutput += line + '\n';
            }
          }

          const result = JSON.parse(jsonOutput);
          res.json({
            ok: true,
            message: "Index detection completed successfully",
            ...result,
            stdout
          });
        } catch (parseError) {
          res.json({
            ok: true,
            message: "Index detection completed successfully",
            raw_output: stdout,
            parse_error: String(parseError)
          });
        }
      } else {
        res.status(500).json({
          ok: false,
          error: "Index detection failed",
          code,
          stdout,
          stderr
        });
      }
    });
  });
});

// Get status of all document builds
r.get("/api/deterministic-status", (req, res) => {
  const status = {
    supp13: getFolderStatus('out/review_13'),
    doc63: getFolderStatus('out/review_63'),
    tr5: getFolderStatus('out/review_tr5')
  };

  const totalLinks = status.supp13.total + status.doc63.total + status.tr5.total;

  res.json({
    ok: true,
    status,
    totalLinks,
    allBuilt: status.supp13.built && status.doc63.built && status.tr5.built
  });
});

function loadFolderResults(folderPath: string) {
  try {
    const reviewPath = path.join(folderPath, "review.json");
    const validationPath = path.join(folderPath, "validation.json");

    let review = null;
    let validation = null;

    if (fs.existsSync(reviewPath)) {
      review = JSON.parse(fs.readFileSync(reviewPath, "utf8"));
    }

    if (fs.existsSync(validationPath)) {
      validation = JSON.parse(fs.readFileSync(validationPath, "utf8"));
    }

    return { review, validation, total: review?.total || 0 };
  } catch (error) {
    console.error(`Error loading results from ${folderPath}:`, error);
    return { review: null, validation: null, total: 0 };
  }
}

function getFolderStatus(folderPath: string) {
  const reviewPath = path.join(folderPath, "review.json");
  const masterPath = path.join(folderPath, "Master.TabsRange.linked.pdf");

  const built = fs.existsSync(reviewPath) && fs.existsSync(masterPath);

  let total = 0;
  if (built) {
    try {
      const review = JSON.parse(fs.readFileSync(reviewPath, "utf8"));
      total = review.total || 0;
    } catch (error) {
      console.error(`Error reading review.json from ${folderPath}:`, error);
    }
  }

  return { built, total };
}

export default r;

server/routes/indexFirstAPI.ts.content:
import { Router } from "express";
import fs from "fs";
import path from "path";
import { spawn } from "child_process";

const r = Router();

// New API endpoint to trigger index-first detection
r.post("/api/index-first-detection", (req, res) => {
  let body = "";
  req.on("data", chunk => body += chunk);
  req.on("end", async () => {
    try {
      const { briefPath, trialRecordPath, outputDir } = JSON.parse(body);

      if (!briefPath || !trialRecordPath || !outputDir) {
        return res.status(400).json({ 
          ok: false, 
          error: "Missing required parameters: briefPath, trialRecordPath, outputDir" 
        });
      }

      // Check if files exist
      if (!fs.existsSync(briefPath)) {
        return res.status(400).json({ ok: false, error: `Brief file not found: ${briefPath}` });
      }

      if (!fs.existsSync(trialRecordPath)) {
        return res.status(400).json({ ok: false, error: `Trial record not found: ${trialRecordPath}` });
      }

      // Run the index-first detector
      const pythonProcess = spawn("python", [
        "server/services/indexFirstDetector.py",
        "--brief", briefPath,
        "--trial", trialRecordPath,
        "--output", outputDir,
        "--config", "config/linking.json"
      ]);

      let stdout = "";
      let stderr = "";

      pythonProcess.stdout.on("data", (data) => {
        stdout += data.toString();
        console.log(data.toString());
      });

      pythonProcess.stderr.on("data", (data) => {
        stderr += data.toString();
        console.error(data.toString());
      });

      pythonProcess.on("close", (code) => {
        if (code === 0) {
          // Success - load the validation report
          const validationPath = path.join(outputDir, "validation.json");
          const reviewPath = path.join(outputDir, "review.json");

          let validation = {};
          let review = {};

          try {
            if (fs.existsSync(validationPath)) {
              validation = JSON.parse(fs.readFileSync(validationPath, "utf8"));
            }
            if (fs.existsSync(reviewPath)) {
              review = JSON.parse(fs.readFileSync(reviewPath, "utf8"));
            }
          } catch (error) {
            console.error("Error loading result files:", error);
          }

          res.json({
            ok: true,
            message: "Index-first detection completed successfully",
            validation,
            review,
            stdout,
            outputDir
          });
        } else {
          res.status(500).json({
            ok: false,
            error: "Index-first detection failed",
            code,
            stdout,
            stderr
          });
        }
      });

    } catch (error) {
      console.error("Error in index-first detection:", error);
      res.status(500).json({ 
        ok: false, 
        error: error instanceof Error ? error.message : "Unknown error" 
      });
    }
  });
});

// Get configuration for a specific document
r.get("/api/linking-config/:filename", (req, res) => {
  try {
    const configPath = "config/linking.json";
    let config = {};

    if (fs.existsSync(configPath)) {
      config = JSON.parse(fs.readFileSync(configPath, "utf8"));
    }

    const filename = decodeURIComponent(req.params.filename);
    const documentConfig = getDocumentConfig(config, filename);

    res.json({
      ok: true,
      filename,
      config: documentConfig,
      allConfigs: config
    });

  } catch (error) {
    res.status(500).json({
      ok: false,
      error: error instanceof Error ? error.message : "Failed to load config"
    });
  }
});

// Update configuration for a specific document
r.post("/api/linking-config/:filename", (req, res) => {
  let body = "";
  req.on("data", chunk => body += chunk);
  req.on("end", () => {
    try {
      const filename = decodeURIComponent(req.params.filename);
      const { scan_first_pages, expected_tabs } = JSON.parse(body);

      const configPath = "config/linking.json";
      let config = {};

      if (fs.existsSync(configPath)) {
        config = JSON.parse(fs.readFileSync(configPath, "utf8"));
      }

      config[filename] = {
        scan_first_pages: Number(scan_first_pages),
        expected_tabs: Number(expected_tabs)
      };

      // Ensure config directory exists
      const configDir = path.dirname(configPath);
      if (!fs.existsSync(configDir)) {
        fs.mkdirSync(configDir, { recursive: true });
      }

      fs.writeFileSync(configPath, JSON.stringify(config, null, 2));

      res.json({
        ok: true,
        message: "Configuration updated successfully",
        filename,
        config: config[filename]
      });

    } catch (error) {
      res.status(500).json({
        ok: false,
        error: error instanceof Error ? error.message : "Failed to update config"
      });
    }
  });
});

// Helper function to get document configuration with fallbacks
function getDocumentConfig(config: any, filename: string) {
  const defaultConfig = {
    scan_first_pages: 10,
    expected_tabs: 0
  };

  // Try exact filename match first
  if (config[filename]) {
    return { ...defaultConfig, ...config[filename] };
  }

  // Try partial filename matching
  for (const configFilename in config) {
    if (configFilename.toLowerCase().includes(filename.toLowerCase()) || 
        filename.toLowerCase().includes(configFilename.toLowerCase())) {
      return { ...defaultConfig, ...config[configFilename] };
    }
  }

  return defaultConfig;
}

export default r;

