import { useState, useEffect, useRef } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { ScrollArea } from '@/components/ui/scroll-area';
import { MessageCircle, Send, Bot, User, AlertCircle } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { Badge } from '@/components/ui/badge';

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  metadata?: any;
  createdAt: string;
}

interface Conversation {
  id: string;
  title: string;
  status: string;
  createdAt: string;
  documentId?: string;
  caseId?: string;
}

interface ChatProps {
  documentId?: string;
  caseId?: string;
  className?: string;
}

export function Chat({ documentId, caseId, className }: ChatProps) {
  const [currentConversationId, setCurrentConversationId] = useState<string | null>(null);
  const [messageInput, setMessageInput] = useState('');
  const [isExpanded, setIsExpanded] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const queryClient = useQueryClient();

  // Get conversations for this document/case
  const { data: conversations = [] } = useQuery<Conversation[]>({
    queryKey: ['/api/chat/conversations', { documentId }],
    enabled: false, // Disable auto-fetching for now since we need auth
  });

  // Get messages for current conversation
  const { data: messages = [], isLoading: messagesLoading } = useQuery<Message[]>({
    queryKey: ['/api/chat/conversations', currentConversationId, 'messages'],
    enabled: false, // Disable auto-fetching for now since we need proper auth
  });

  // Create new conversation
  const createConversationMutation = useMutation({
    mutationFn: async (data: { documentId?: string; caseId?: string; title?: string }) =>
      apiRequest('POST', '/api/chat/conversations', data),
    onSuccess: (newConversation) => {
      setCurrentConversationId(newConversation.id);
      queryClient.invalidateQueries({ queryKey: ['/api/chat/conversations'] });
    },
  });

  // Send message
  const sendMessageMutation = useMutation({
    mutationFn: async ({ conversationId, message }: { conversationId: string; message: string }) =>
      apiRequest('POST', `/api/chat/conversations/${conversationId}/messages`, { message }),
    onSuccess: () => {
      queryClient.invalidateQueries({ 
        queryKey: ['/api/chat/conversations', currentConversationId, 'messages'] 
      });
      setMessageInput('');
    },
  });

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Start a new conversation if none exists
  const handleStartChat = async () => {
    if (conversations.length === 0) {
      const title = documentId ? 'Document Feedback Chat' : 'General Chat';
      await createConversationMutation.mutateAsync({ documentId, caseId, title });
    } else {
      setCurrentConversationId(conversations[0].id);
    }
    setIsExpanded(true);
  };

  // Send a message
  const handleSendMessage = async () => {
    if (!messageInput.trim() || !currentConversationId) return;

    await sendMessageMutation.mutateAsync({
      conversationId: currentConversationId,
      message: messageInput.trim(),
    });
  };

  // Handle Enter key press
  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (!isExpanded) {
    return (
      <div className={`fixed bottom-4 right-4 z-50 ${className}`}>
        <Button
          onClick={handleStartChat}
          className="rounded-full w-14 h-14 shadow-lg bg-blue-600 hover:bg-blue-700"
          data-testid="button-open-chat"
        >
          <MessageCircle className="w-6 h-6" />
        </Button>
      </div>
    );
  }

  return (
    <div className={`fixed bottom-4 right-4 z-50 ${className}`}>
      <Card className="w-96 h-[500px] shadow-xl">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <MessageCircle className="w-5 h-5 text-blue-600" />
              <CardTitle className="text-lg">
                Document Assistant
              </CardTitle>
            </div>
            <div className="flex gap-2">
              {documentId && <Badge variant="outline" data-testid="badge-document-chat">Document</Badge>}
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsExpanded(false)}
                data-testid="button-close-chat"
              >
                √ó
              </Button>
            </div>
          </div>
        </CardHeader>

        <CardContent className="flex flex-col h-[400px] p-4">
          {/* Messages Area */}
          <ScrollArea className="flex-1 pr-2 mb-4">
            {messagesLoading ? (
              <div className="text-center py-4 text-gray-500" data-testid="text-loading">
                Loading messages...
              </div>
            ) : messages.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                <Bot className="w-12 h-12 mx-auto mb-3 opacity-50" />
                <p className="text-sm" data-testid="text-welcome">
                  Hi! I'm here to help with document processing feedback. 
                  You can tell me about any OCR mistakes or processing issues.
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                {messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex gap-3 ${
                      message.role === 'user' ? 'justify-end' : 'justify-start'
                    }`}
                    data-testid={`message-${message.role}-${message.id}`}
                  >
                    {message.role === 'assistant' && (
                      <div className="w-8 h-8 rounded-full bg-blue-100 flex items-center justify-center flex-shrink-0">
                        <Bot className="w-4 h-4 text-blue-600" />
                      </div>
                    )}
                    <div
                      className={`max-w-[80%] px-4 py-2 rounded-lg ${
                        message.role === 'user'
                          ? 'bg-blue-600 text-white'
                          : 'bg-gray-100 text-gray-900 border'
                      }`}
                    >
                      <p className="text-sm whitespace-pre-wrap">{message.content}</p>
                    </div>
                    {message.role === 'user' && (
                      <div className="w-8 h-8 rounded-full bg-blue-600 flex items-center justify-center flex-shrink-0">
                        <User className="w-4 h-4 text-white" />
                      </div>
                    )}
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>
            )}
          </ScrollArea>

          {/* Input Area */}
          <div className="flex gap-2">
            <Input
              placeholder="Describe any issues or ask questions..."
              value={messageInput}
              onChange={(e) => setMessageInput(e.target.value)}
              onKeyPress={handleKeyPress}
              disabled={sendMessageMutation.isPending}
              data-testid="input-message"
            />
            <Button
              onClick={handleSendMessage}
              disabled={!messageInput.trim() || sendMessageMutation.isPending}
              size="sm"
              data-testid="button-send-message"
            >
              <Send className="w-4 h-4" />
            </Button>
          </div>

          {sendMessageMutation.isPending && (
            <div className="flex items-center gap-2 mt-2 text-sm text-gray-500" data-testid="text-sending">
              <div className="w-3 h-3 border border-gray-300 border-t-blue-600 rounded-full animate-spin" />
              Sending message...
            </div>
          )}

          {/* Quick actions */}
          <div className="flex flex-wrap gap-1 mt-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setMessageInput("The system missed an index item")}
              className="text-xs"
              data-testid="button-quick-missing-item"
            >
              <AlertCircle className="w-3 h-3 mr-1" />
              Missing Item
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setMessageInput("There's an OCR error in the text")}
              className="text-xs"
              data-testid="button-quick-ocr-error"
            >
              OCR Error
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setMessageInput("The index count is wrong")}
              className="text-xs"
              data-testid="button-quick-wrong-count"
            >
              Wrong Count
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

server/services/chatService.ts.content:
import OpenAI from 'openai';
import { db } from '../db';
import { chatConversations, chatMessages, documents, cases } from '@shared/schema';
import { eq, and, desc } from 'drizzle-orm';

const openai = new OpenAI({
  apiKey: process.env.OPENAI_API_KEY,
});

export class ChatService {
  async createConversation(userId: string, documentId?: string, caseId?: string, title?: string) {
    const [conversation] = await db.insert(chatConversations).values({
      userId,
      documentId,
      caseId,
      title: title || 'Document Feedback Chat'
    }).returning();

    return conversation;
  }

  async getConversations(userId: string, documentId?: string) {
    if (documentId) {
      return db.select().from(chatConversations)
        .where(and(
          eq(chatConversations.userId, userId),
          eq(chatConversations.documentId, documentId)
        ))
        .orderBy(desc(chatConversations.updatedAt));
    }

    return db.select().from(chatConversations)
      .where(eq(chatConversations.userId, userId))
      .orderBy(desc(chatConversations.updatedAt));
  }

  async getMessages(conversationId: string) {
    return db.select().from(chatMessages)
      .where(eq(chatMessages.conversationId, conversationId))
      .orderBy(chatMessages.createdAt);
  }

  async sendMessage(conversationId: string, role: 'user' | 'assistant', content: string, metadata?: any) {
    const [message] = await db.insert(chatMessages).values({
      conversationId,
      role,
      content,
      metadata
    }).returning();

    // Update conversation timestamp
    await db.update(chatConversations)
      .set({ updatedAt: new Date() })
      .where(eq(chatConversations.id, conversationId));

    return message;
  }

  async processUserMessage(conversationId: string, userMessage: string) {
    // Get conversation context
    const [conversation] = await db.select()
      .from(chatConversations)
      .where(eq(chatConversations.id, conversationId));

    if (!conversation) {
      throw new Error('Conversation not found');
    }

    // Get document context if available
    let documentContext = '';
    if (conversation.documentId) {
      const [document] = await db.select()
        .from(documents)
        .where(eq(documents.id, conversation.documentId));

      if (document) {
        documentContext = `
Document: ${document.title}
Index Items Found: ${document.indexCount || 0}
Current Index Items: ${JSON.stringify(document.indexItems || [], null, 2)}
OCR Status: ${document.ocrStatus}
Index Status: ${document.indexStatus}
`;
      }
    }

    // Get recent conversation history
    const recentMessages = await db.select()
      .from(chatMessages)
      .where(eq(chatMessages.conversationId, conversationId))
      .orderBy(desc(chatMessages.createdAt))
      .limit(10);

    const conversationHistory = recentMessages.reverse().map(m => 
      `${m.role}: ${m.content}`
    ).join('\n');

    // Save user message
    await this.sendMessage(conversationId, 'user', userMessage);

    // Create AI prompt for document processing feedback
    const systemPrompt = `You are an intelligent assistant helping with legal document processing and OCR correction. 

Current document context:
${documentContext}

Recent conversation:
${conversationHistory}

Your role is to:
1. Understand user feedback about OCR mistakes or missing index items
2. Provide helpful suggestions for corrections
3. Explain what might have gone wrong with the processing
4. Guide users on how to improve results
5. If the user mentions specific corrections, acknowledge them and explain next steps

The system uses enhanced OCR that is permanently enabled and processes scanned legal documents. Users may report:
- Missing index items that should have been detected
- Incorrect text recognition in index items  
- Wrong item counts
- Processing errors or failures

Respond in a helpful, professional tone suitable for legal professionals. Keep responses concise but thorough.`;

    try {
      const completion = await openai.chat.completions.create({
        model: "gpt-4o-mini",
        messages: [
          { role: "system", content: systemPrompt },
          { role: "user", content: userMessage }
        ],
        max_tokens: 500,
        temperature: 0.7,
      });

      const assistantResponse = completion.choices[0]?.message?.content || 
        "I understand your feedback. Let me help you with that document processing issue.";

      // Save assistant response
      const assistantMessage = await this.sendMessage(conversationId, 'assistant', assistantResponse);

      return {
        userMessage: userMessage,
        assistantResponse: assistantResponse,
        messageId: assistantMessage.id
      };

    } catch (error) {
      console.error('OpenAI API error:', error);

      // Fallback response if OpenAI fails
      const fallbackResponse = "I'm here to help with document processing feedback. Could you tell me more about the specific issue you're experiencing with the OCR results or index detection?";

      const assistantMessage = await this.sendMessage(conversationId, 'assistant', fallbackResponse);

      return {
        userMessage: userMessage,
        assistantResponse: fallbackResponse,
        messageId: assistantMessage.id
      };
    }
  }

  async processCorrection(conversationId: string, correction: {
    type: 'missing_item' | 'incorrect_text' | 'wrong_count' | 'other';
    details: string;
    expectedResult?: any;
  }) {
    // Log the correction for processing
    const metadata = {
      correction,
      timestamp: new Date().toISOString(),
      type: 'user_correction'
    };

    const systemMessage = `User provided correction: ${correction.type} - ${correction.details}`;

    await this.sendMessage(conversationId, 'assistant', systemMessage, metadata);

    // In a real implementation, this would trigger re-processing
    // For now, we acknowledge the correction
    const response = `Thank you for the correction. I've noted that there's a ${correction.type.replace('_', ' ')} issue: "${correction.details}". 

This feedback helps improve the OCR processing. In the current system, you can manually edit the index items and re-run the processing if needed.`;

    await this.sendMessage(conversationId, 'assistant', response);

    return {
      success: true,
      message: 'Correction recorded successfully'
    };
  }
}

export const chatService = new ChatService();

