import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import type { Document } from "@shared/schema";

interface AIHyperlinkingStepProps {
  caseId: string;
  documents: Document[];
  onHyperlinksGenerated: () => void;
}

export function AIHyperlinkingStep({ caseId, documents, onHyperlinksGenerated }: AIHyperlinkingStepProps) {
  const [selectedDocuments, setSelectedDocuments] = useState<string[]>([]);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch detected index items
  const { data: indexData } = useQuery({
    queryKey: ["/api/documents/index-items", caseId],
    enabled: documents.some(doc => doc.ocrStatus === "completed"),
  });

  // Fetch hyperlink generation progress
  const { data: hyperlinkProgress } = useQuery({
    queryKey: ["/api/hyperlinks/progress", caseId],
    refetchInterval: 2000,
  });

  const generateHyperlinksMutation = useMutation({
    mutationFn: async () => {
      return apiRequest(`/api/hyperlinks/generate/${caseId}`, {
        method: "POST",
        body: { documentIds: selectedDocuments },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hyperlinks/progress", caseId] });
      onHyperlinksGenerated();
      toast({
        title: "Hyperlinks Generated",
        description: "AI hyperlink detection completed successfully.",
      });
    },
    onError: (error) => {
      toast({
        title: "Generation Failed",
        description: `Failed to generate hyperlinks: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const approveIndexMutation = useMutation({
    mutationFn: async (documentId: string) => {
      return apiRequest(`/api/documents/${documentId}/approve-index`, {
        method: "POST",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/documents/index-items", caseId] });
      toast({
        title: "Index Approved",
        description: "Document index has been approved for hyperlink generation.",
      });
    },
  });

  const handleDocumentToggle = (documentId: string, checked: boolean) => {
    if (checked) {
      setSelectedDocuments(prev => [...prev, documentId]);
    } else {
      setSelectedDocuments(prev => prev.filter(id => id !== documentId));
    }
  };

  const handleSelectAll = () => {
    const eligibleDocs = documents.filter(doc => doc.ocrStatus === "completed");
    setSelectedDocuments(eligibleDocs.map(doc => doc.id));
  };

  const handleSelectNone = () => {
    setSelectedDocuments([]);
  };

  const eligibleDocuments = documents.filter(doc => doc.ocrStatus === "completed");
  const documentsWithIndex = eligibleDocuments.filter(doc => doc.indexItems && doc.indexCount > 0);
  const allIndexesApproved = documentsWithIndex.every(doc => doc.indexStatus === "ok");
  const hyperlinksGenerated = hyperlinkProgress?.completed || false;

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <div className="mb-8">
        <div className="flex items-center gap-3 mb-4">
          <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
            hyperlinksGenerated ? "bg-green-100 dark:bg-green-900" : 
            generateHyperlinksMutation.isPending ? "bg-blue-100 dark:bg-blue-900" : "bg-primary"
          }`}>
            <i className={`text-xl ${
              hyperlinksGenerated ? "fas fa-check text-green-600" :
              generateHyperlinksMutation.isPending ? "fas fa-spinner fa-spin text-blue-600" : "fas fa-link text-primary-foreground"
            }`}></i>
          </div>
          <div>
            <h1 className="text-3xl font-bold text-foreground">
              Step 4: AI Hyperlinking {hyperlinksGenerated ? "‚úÖ" : ""}
            </h1>
            <p className="text-lg text-muted-foreground">
              {hyperlinksGenerated 
                ? "Hyperlinks generated successfully - ready for review"
                : "Detect index items and generate hyperlinks between documents"
              }
            </p>
          </div>
        </div>
      </div>

      {/* Index Detection Results */}
      {documentsWithIndex.length > 0 && (
        <div className="bg-card border border-border rounded-lg p-6 mb-6">
          <h3 className="text-lg font-semibold mb-4">Detected Index Items</h3>
          <div className="space-y-4">
            {documentsWithIndex.map((doc) => (
              <div key={doc.id} className="border border-border rounded-lg p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center gap-3">
                    <i className="fas fa-file-pdf text-red-500 text-lg"></i>
                    <div>
                      <div className="font-medium">{doc.title}</div>
                      <div className="text-sm text-muted-foreground">
                        {doc.indexCount} index items detected
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    {doc.indexStatus === "ok" ? (
                      <span className="px-3 py-1 bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200 rounded-full text-sm font-medium">
                        ‚úì Approved
                      </span>
                    ) : (
                      <Button
                        size="sm"
                        onClick={() => approveIndexMutation.mutate(doc.id)}
                        disabled={approveIndexMutation.isPending}
                        data-testid={`button-approve-index-${doc.id}`}
                      >
                        {approveIndexMutation.isPending ? (
                          <i className="fas fa-spinner fa-spin mr-1"></i>
                        ) : (
                          <i className="fas fa-check mr-1"></i>
                        )}
                        Approve Index
                      </Button>
                    )}
                  </div>
                </div>

                {/* Index Items Preview */}
                {doc.indexItems && Array.isArray(doc.indexItems) && (
                  <div className="mt-3">
                    <div className="text-sm font-medium mb-2">Index Items:</div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-2 text-sm">
                      {(doc.indexItems as any[]).slice(0, 6).map((item, index) => (
                        <div key={index} className="flex items-center gap-2 text-muted-foreground">
                          <i className="fas fa-chevron-right text-xs"></i>
                          <span>{item.text || item}</span>
                          {item.page && <span className="text-xs">(p. {item.page})</span>}
                        </div>
                      ))}
                      {(doc.indexItems as any[]).length > 6 && (
                        <div className="text-xs text-muted-foreground italic">
                          +{(doc.indexItems as any[]).length - 6} more items...
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Document Selection for Hyperlink Generation */}
      {allIndexesApproved && !hyperlinksGenerated && (
        <div className="bg-card border border-border rounded-lg p-6 mb-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">Select Documents for Hyperlink Generation</h3>
            <div className="flex gap-2">
              <Button size="sm" variant="outline" onClick={handleSelectAll}>
                Select All
              </Button>
              <Button size="sm" variant="outline" onClick={handleSelectNone}>
                Select None
              </Button>
            </div>
          </div>

          <div className="space-y-3">
            {eligibleDocuments.map((doc) => (
              <div key={doc.id} className="flex items-center gap-3 p-3 border border-border rounded">
                <Checkbox
                  checked={selectedDocuments.includes(doc.id)}
                  onCheckedChange={(checked) => handleDocumentToggle(doc.id, checked as boolean)}
                  data-testid={`checkbox-document-${doc.id}`}
                />
                <div className="flex items-center gap-3 flex-1">
                  <i className="fas fa-file-pdf text-red-500"></i>
                  <div>
                    <div className="font-medium">{doc.title}</div>
                    <div className="text-sm text-muted-foreground">
                      {doc.indexCount} index items ‚Ä¢ {doc.pageCount} pages
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {selectedDocuments.length > 0 && (
            <div className="mt-4 pt-4 border-t border-border">
              <Button
                onClick={() => generateHyperlinksMutation.mutate()}
                disabled={generateHyperlinksMutation.isPending}
                className="bg-primary hover:bg-primary/90"
                data-testid="button-generate-hyperlinks"
              >
                {generateHyperlinksMutation.isPending ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Generating Hyperlinks...
                  </>
                ) : (
                  <>
                    <i className="fas fa-magic mr-2"></i>
                    Generate Hyperlinks ({selectedDocuments.length} documents)
                  </>
                )}
              </Button>
            </div>
          )}
        </div>
      )}

      {/* Continue Button */}
      {hyperlinksGenerated && (
        <div className="flex gap-4">
          <Button
            className="bg-green-600 hover:bg-green-700"
            onClick={onHyperlinksGenerated}
            data-testid="button-continue-to-review"
          >
            Continue to Step 5: Lawyer Review
            <i className="fas fa-arrow-right ml-2"></i>
          </Button>
        </div>
      )}

      {/* AI Information */}
      <div className="mt-8 bg-purple-50 dark:bg-purple-950 border border-purple-200 dark:border-purple-800 rounded-lg p-4">
        <div className="flex items-start gap-3">
          <i className="fas fa-robot text-purple-500 mt-1"></i>
          <div>
            <h4 className="font-medium text-purple-900 dark:text-purple-100">AI Hyperlink Detection</h4>
            <div className="text-sm text-purple-700 dark:text-purple-200 mt-2 space-y-1">
              <p>‚Ä¢ <strong>Index-deterministic:</strong> Creates exactly as many hyperlinks as index items detected</p>
              <p>‚Ä¢ <strong>Context-aware:</strong> Understands legal document structure and relationships</p>
              <p>‚Ä¢ <strong>High precision:</strong> AI analyzes document content to create accurate cross-references</p>
              <p>‚Ä¢ <strong>Lawyer approval:</strong> Index items require professional review before hyperlink generation</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/workflow/CourtSubm.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import type { Case, Document } from "@shared/schema";

interface CourtSubmitStepProps {
  caseId: string;
  caseData: Case;
  documents: Document[];
}

export function CourtSubmitStep({ caseId, caseData, documents }: CourtSubmitStepProps) {
  const [selectedDocuments, setSelectedDocuments] = useState<string[]>([]);
  const [bundleGenerated, setBundleGenerated] = useState(false);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch court bundle status
  const { data: bundleStatus } = useQuery({
    queryKey: ["/api/cases", caseId, "bundle-status"],
  });

  const generateBundleMutation = useMutation({
    mutationFn: async () => {
      return apiRequest(`/api/cases/${caseId}/generate-court-bundle`, {
        method: "POST",
        body: { documentIds: selectedDocuments },
      });
    },
    onSuccess: (result) => {
      setBundleGenerated(true);
      queryClient.invalidateQueries({ queryKey: ["/api/cases", caseId, "bundle-status"] });
      toast({
        title: "Court Bundle Generated",
        description: "Your court-ready document bundle has been created successfully.",
      });
    },
    onError: (error) => {
      toast({
        title: "Bundle Generation Failed",
        description: `Failed to generate court bundle: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const submitToCourtMutation = useMutation({
    mutationFn: async () => {
      return apiRequest(`/api/cases/${caseId}/submit-to-court`, {
        method: "POST",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cases", caseId] });
      toast({
        title: "Case Submitted",
        description: "Your case has been marked as submitted to court.",
      });
    },
    onError: (error) => {
      toast({
        title: "Submission Failed",
        description: `Failed to submit case: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const handleDocumentToggle = (documentId: string, checked: boolean) => {
    if (checked) {
      setSelectedDocuments(prev => [...prev, documentId]);
    } else {
      setSelectedDocuments(prev => prev.filter(id => id !== documentId));
    }
  };

  const handleSelectAll = () => {
    const approvedDocs = documents.filter(doc => doc.lawyerReviewed);
    setSelectedDocuments(approvedDocs.map(doc => doc.id));
  };

  const handleSelectNone = () => {
    setSelectedDocuments([]);
  };

  const approvedDocuments = documents.filter(doc => doc.lawyerReviewed);
  const isSubmitted = caseData.status === "submitted" || bundleStatus?.submitted;

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <div className="mb-8">
        <div className="flex items-center gap-3 mb-4">
          <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
            isSubmitted ? "bg-green-100 dark:bg-green-900" : 
            bundleGenerated ? "bg-blue-100 dark:bg-blue-900" : "bg-primary"
          }`}>
            <i className={`text-xl ${
              isSubmitted ? "fas fa-check text-green-600" :
              bundleGenerated ? "fas fa-file-archive text-blue-600" : "fas fa-download text-primary-foreground"
            }`}></i>
          </div>
          <div>
            <h1 className="text-3xl font-bold text-foreground">
              Step 6: Court Submit {isSubmitted ? "‚úÖ" : ""}
            </h1>
            <p className="text-lg text-muted-foreground">
              {isSubmitted 
                ? "Case successfully submitted to court"
                : bundleGenerated
                ? "Court bundle ready for download and submission"
                : "Generate final court-ready document bundle"
              }
            </p>
          </div>
        </div>
      </div>

      {/* Case Summary */}
      <div className="bg-card border border-border rounded-lg p-6 mb-6">
        <h3 className="text-lg font-semibold mb-4">Case Summary</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <div className="text-sm font-medium text-muted-foreground">Case Number</div>
            <div className="text-lg font-semibold">{caseData.caseNumber}</div>
          </div>
          <div>
            <div className="text-sm font-medium text-muted-foreground">Filing Date</div>
            <div className="text-lg">{new Date(caseData.filingDate).toLocaleDateString()}</div>
          </div>
          <div className="md:col-span-2">
            <div className="text-sm font-medium text-muted-foreground">Case Title</div>
            <div className="text-lg font-semibold">{caseData.title}</div>
          </div>
          <div>
            <div className="text-sm font-medium text-muted-foreground">Court</div>
            <div className="text-lg">{caseData.courtName || "Not specified"}</div>
          </div>
          <div>
            <div className="text-sm font-medium text-muted-foreground">Judge</div>
            <div className="text-lg">{caseData.judgeName || "Not assigned"}</div>
          </div>
        </div>
      </div>

      {/* Document Selection for Bundle */}
      {!bundleGenerated && !isSubmitted && (
        <div className="bg-card border border-border rounded-lg p-6 mb-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">Select Documents for Court Bundle</h3>
            <div className="flex gap-2">
              <Button size="sm" variant="outline" onClick={handleSelectAll}>
                Select All Approved
              </Button>
              <Button size="sm" variant="outline" onClick={handleSelectNone}>
                Select None
              </Button>
            </div>
          </div>

          <div className="space-y-3">
            {approvedDocuments.map((doc) => (
              <div key={doc.id} className="flex items-center gap-3 p-3 border border-border rounded">
                <Checkbox
                  checked={selectedDocuments.includes(doc.id)}
                  onCheckedChange={(checked) => handleDocumentToggle(doc.id, checked as boolean)}
                  data-testid={`checkbox-bundle-document-${doc.id}`}
                />
                <div className="flex items-center gap-3 flex-1">
                  <i className="fas fa-file-pdf text-red-500"></i>
                  <div>
                    <div className="font-medium">{doc.title}</div>
                    <div className="text-sm text-muted-foreground">
                      Reviewed by: {doc.reviewedBy || "System"} ‚Ä¢ 
                      {doc.reviewedAt && ` on ${new Date(doc.reviewedAt).toLocaleDateString()}`}
                    </div>
                  </div>
                </div>
                <span className="px-2 py-1 bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200 rounded text-xs font-medium">
                  ‚úì Approved
                </span>
              </div>
            ))}
          </div>

          {selectedDocuments.length > 0 && (
            <div className="mt-6 pt-4 border-t border-border">
              <Button
                onClick={() => generateBundleMutation.mutate()}
                disabled={generateBundleMutation.isPending}
                className="bg-primary hover:bg-primary/90"
                data-testid="button-generate-court-bundle"
              >
                {generateBundleMutation.isPending ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Generating Court Bundle...
                  </>
                ) : (
                  <>
                    <i className="fas fa-file-archive mr-2"></i>
                    Generate Court Bundle ({selectedDocuments.length} documents)
                  </>
                )}
              </Button>
            </div>
          )}
        </div>
      )}

      {/* Bundle Generated - Download Options */}
      {bundleGenerated && !isSubmitted && (
        <div className="bg-card border border-border rounded-lg p-6 mb-6">
          <h3 className="text-lg font-semibold mb-4">Court Bundle Ready</h3>
          <div className="flex items-center gap-6 mb-6">
            <div className="flex items-center gap-3">
              <i className="fas fa-file-archive text-blue-500 text-2xl"></i>
              <div>
                <div className="font-medium">Court Bundle PDF</div>
                <div className="text-sm text-muted-foreground">
                  {selectedDocuments.length} documents with hyperlinks
                </div>
              </div>
            </div>
            <div className="flex gap-3">
              <Button variant="outline" data-testid="button-download-bundle">
                <i className="fas fa-download mr-2"></i>
                Download Bundle
              </Button>
              <Button variant="outline" data-testid="button-print-bundle">
                <i className="fas fa-print mr-2"></i>
                Print Bundle
              </Button>
            </div>
          </div>

          <div className="pt-4 border-t border-border">
            <Button
              onClick={() => submitToCourtMutation.mutate()}
              disabled={submitToCourtMutation.isPending}
              className="bg-green-600 hover:bg-green-700"
              data-testid="button-submit-to-court"
            >
              {submitToCourtMutation.isPending ? (
                <>
                  <i className="fas fa-spinner fa-spin mr-2"></i>
                  Submitting to Court...
                </>
              ) : (
                <>
                  <i className="fas fa-gavel mr-2"></i>
                  Mark as Submitted to Court
                </>
              )}
            </Button>
          </div>
        </div>
      )}

      {/* Submission Completed */}
      {isSubmitted && (
        <div className="bg-green-50 dark:bg-green-950 border border-green-200 dark:border-green-800 rounded-lg p-6 mb-6">
          <div className="flex items-center gap-3 mb-4">
            <i className="fas fa-check-circle text-green-600 text-2xl"></i>
            <div>
              <h3 className="text-lg font-semibold text-green-900 dark:text-green-100">
                Case Successfully Submitted
              </h3>
              <p className="text-green-700 dark:text-green-200">
                Your case has been completed and submitted to court with all hyperlinked documents.
              </p>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center">
            <div>
              <div className="text-2xl font-bold text-green-600">{documents.length}</div>
              <div className="text-sm text-green-700 dark:text-green-200">Total Documents</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-green-600">{approvedDocuments.length}</div>
              <div className="text-sm text-green-700 dark:text-green-200">Approved Documents</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-green-600">100%</div>
              <div className="text-sm text-green-700 dark:text-green-200">Workflow Complete</div>
            </div>
          </div>
        </div>
      )}

      {/* Court Submission Guidelines */}
      <div className="mt-8 bg-blue-50 dark:bg-blue-950 border border-blue-200 dark:border-blue-800 rounded-lg p-4">
        <div className="flex items-start gap-3">
          <i className="fas fa-gavel text-blue-500 mt-1"></i>
          <div>
            <h4 className="font-medium text-blue-900 dark:text-blue-100">Court Submission Guidelines</h4>
            <div className="text-sm text-blue-700 dark:text-blue-200 mt-2 space-y-1">
              <p>‚Ä¢ <strong>Hyperlinked documents:</strong> All cross-references are automatically linked for easy navigation</p>
              <p>‚Ä¢ <strong>Professional formatting:</strong> Documents maintain court-approved formatting standards</p>
              <p>‚Ä¢ <strong>Complete bundle:</strong> Includes manifest, table of contents, and all supporting documents</p>
              <p>‚Ä¢ <strong>Digital ready:</strong> Optimized for electronic court filing systems</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/workflow/CreateCas.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { insertCaseSchema, type Case } from "@shared/schema";
import { z } from "zod";

const createCaseFormSchema = insertCaseSchema.extend({
  filingDate: z.string().min(1, "Filing date is required"),
});

interface CreateCaseStepProps {
  caseData?: Case;
  onCaseCreated: (caseData: Case) => void;
}

export function CreateCaseStep({ caseData, onCaseCreated }: CreateCaseStepProps) {
  const [isEditing, setIsEditing] = useState(!caseData);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const form = useForm<z.infer<typeof createCaseFormSchema>>({
    resolver: zodResolver(createCaseFormSchema),
    defaultValues: {
      caseNumber: caseData?.caseNumber || `2024-CV-${Date.now().toString().slice(-6)}`,
      title: caseData?.title || "",
      filingDate: caseData?.filingDate || new Date().toISOString().split('T')[0],
      plaintiff: caseData?.plaintiff || "",
      defendant: caseData?.defendant || "",
      courtName: caseData?.courtName || "",
      judgeName: caseData?.judgeName || "",
      storagePath: caseData?.storagePath || "",
    },
  });

  const createCaseMutation = useMutation({
    mutationFn: async (data: z.infer<typeof createCaseFormSchema>) => {
      const storagePathValue = data.storagePath || `cases/${data.caseNumber}`;
      return apiRequest("/api/cases", {
        method: "POST",
        body: { ...data, storagePath: storagePathValue },
      });
    },
    onSuccess: (newCase) => {
      queryClient.invalidateQueries({ queryKey: ["/api/cases"] });
      onCaseCreated(newCase);
      setIsEditing(false);
      toast({
        title: "Case Created",
        description: `Case ${newCase.caseNumber} has been created successfully.`,
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: `Failed to create case: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const updateCaseMutation = useMutation({
    mutationFn: async (data: z.infer<typeof createCaseFormSchema>) => {
      return apiRequest(`/api/cases/${caseData?.id}`, {
        method: "PATCH",
        body: data,
      });
    },
    onSuccess: (updatedCase) => {
      queryClient.invalidateQueries({ queryKey: ["/api/cases"] });
      onCaseCreated(updatedCase);
      setIsEditing(false);
      toast({
        title: "Case Updated",
        description: `Case ${updatedCase.caseNumber} has been updated successfully.`,
      });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: `Failed to update case: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: z.infer<typeof createCaseFormSchema>) => {
    if (caseData) {
      updateCaseMutation.mutate(data);
    } else {
      createCaseMutation.mutate(data);
    }
  };

  const mutation = caseData ? updateCaseMutation : createCaseMutation;

  if (caseData && !isEditing) {
    return (
      <div className="p-8 max-w-4xl mx-auto">
        <div className="mb-8">
          <div className="flex items-center gap-3 mb-4">
            <div className="w-12 h-12 bg-green-100 dark:bg-green-900 rounded-full flex items-center justify-center">
              <i className="fas fa-check text-green-600 text-xl"></i>
            </div>
            <div>
              <h1 className="text-3xl font-bold text-foreground">Step 1: Case Created ‚úÖ</h1>
              <p className="text-lg text-muted-foreground">Your legal case has been set up successfully</p>
            </div>
          </div>
        </div>

        <div className="bg-card border border-border rounded-lg p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <Label className="text-sm font-medium text-muted-foreground">Case Number</Label>
              <div className="text-lg font-semibold">{caseData.caseNumber}</div>
            </div>
            <div>
              <Label className="text-sm font-medium text-muted-foreground">Filing Date</Label>
              <div className="text-lg">{new Date(caseData.filingDate).toLocaleDateString()}</div>
            </div>
            <div className="md:col-span-2">
              <Label className="text-sm font-medium text-muted-foreground">Case Title</Label>
              <div className="text-lg font-semibold">{caseData.title}</div>
            </div>
            <div>
              <Label className="text-sm font-medium text-muted-foreground">Plaintiff</Label>
              <div className="text-lg">{caseData.plaintiff}</div>
            </div>
            <div>
