import { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface IndexLinkPanelProps {
  documentId: string;
}

interface IndexHighlight {
  id: string;
  documentId: string;
  pageNumber: number;
  rect: any;
  text: string;
  status: "new" | "linking" | "linked" | "failed";
  createdAt: string;
  targetPage?: number;
  confidence?: number;
  method?: string;
}

export default function IndexLinkPanel({ documentId }: IndexLinkPanelProps) {
  const [highlights, setHighlights] = useState<IndexHighlight[]>([]);
  const [loading, setLoading] = useState(false);
  const [linkingIds, setLinkingIds] = useState<Set<string>>(new Set());

  const loadHighlights = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/documents/${documentId}/index-highlights`);
      if (response.ok) {
        const data = await response.json();
        setHighlights(data);
      }
    } catch (error) {
      console.error("Failed to load highlights:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadHighlights();
  }, [documentId]);

  const linkOne = async (id: string) => {
    setLinkingIds(prev => new Set(prev).add(id));
    try {
      const response = await fetch(`/api/documents/${documentId}/index-highlights/${id}/link`, {
        method: "POST"
      });

      if (response.ok) {
        const result = await response.json();
        console.log("Link result:", result);
        await loadHighlights(); // Refresh the list
      } else {
        console.error("Failed to link highlight");
      }
    } catch (error) {
      console.error("Error linking highlight:", error);
    } finally {
      setLinkingIds(prev => {
        const newSet = new Set(prev);
        newSet.delete(id);
        return newSet;
      });
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "new":
        return <Badge variant="outline" className="bg-blue-50 text-blue-700 border-blue-200">New</Badge>;
      case "linking":
        return <Badge variant="outline" className="bg-yellow-50 text-yellow-700 border-yellow-200">Linking...</Badge>;
      case "linked":
        return <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">Linked</Badge>;
      case "failed":
        return <Badge variant="outline" className="bg-red-50 text-red-700 border-red-200">Failed</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  if (loading && highlights.length === 0) {
    return (
      <div className="p-4 text-center text-slate-500">
        Loading highlights...
      </div>
    );
  }

  return (
    <div className="space-y-3 p-4">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold">üìã INDEX Highlights</h3>
        <Button 
          variant="outline" 
          size="sm" 
          onClick={loadHighlights}
          disabled={loading}
          data-testid="button-refresh-highlights"
        >
          üîÑ Refresh
        </Button>
      </div>

      {highlights.length === 0 ? (
        <div className="text-center py-8 text-slate-500">
          <div className="text-6xl mb-4">üìù</div>
          <div className="text-lg font-medium mb-2">No highlights yet</div>
          <div className="text-sm">
            Draw rectangles around INDEX items on the left to create highlights
          </div>
        </div>
      ) : (
        <div className="space-y-3">
          {highlights.map((highlight) => (
            <div 
              key={highlight.id} 
              className="rounded-lg border border-slate-200 dark:border-slate-700 p-4 bg-white dark:bg-slate-800"
              data-testid={`highlight-card-${highlight.id}`}
            >
              <div className="space-y-3">
                {/* Header with status */}
                <div className="flex items-start justify-between gap-3">
                  <div className="flex-1 min-w-0">
                    <div className="text-sm font-medium text-slate-900 dark:text-slate-100 break-words">
                      {highlight.text}
                    </div>
                  </div>
                  <div className="flex-shrink-0">
                    {getStatusBadge(highlight.status)}
                  </div>
                </div>

                {/* Metadata */}
                <div className="text-xs text-slate-500 space-y-1">
                  <div>üìÑ Index page {highlight.pageNumber}</div>
                  {highlight.targetPage && (
                    <div className="flex items-center gap-2">
                      üéØ Target page {highlight.targetPage}
                      {highlight.confidence && (
                        <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium">
                          {Math.round(highlight.confidence)}% match
                        </span>
                      )}
                    </div>
                  )}
                </div>

                {/* Actions */}
                <div className="flex gap-2">
                  {highlight.status === "new" || highlight.status === "failed" ? (
                    <Button 
                      size="sm" 
                      onClick={() => linkOne(highlight.id)}
                      disabled={linkingIds.has(highlight.id)}
                      className="bg-blue-500 hover:bg-blue-600 text-white"
                      data-testid={`button-find-sources-${highlight.id}`}
                    >
                      {linkingIds.has(highlight.id) ? "üîç Finding..." : "üîç Find sources"}
                    </Button>
                  ) : highlight.status === "linked" && highlight.targetPage ? (
                    <Button 
                      size="sm" 
                      variant="outline"
                      onClick={() => {
                        // TODO: Jump to target page in PDF viewer
                        console.log(`Jump to page ${highlight.targetPage}`);
                      }}
                      data-testid={`button-go-to-page-${highlight.id}`}
                    >
                      üìñ Go to page {highlight.targetPage}
                    </Button>
                  ) : null}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/document-processing.tsx.content:
import { useState } from "react";
import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import OCRPageManager from "@/components/OCRPageManager";
import IndexLinkPanel from "@/components/IndexLinkPanel";
import IndexHighlighter from "@/components/IndexHighlighter";
import { FileText, Eye, Settings, BookOpen, Link, Zap } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import EnhancedBatchManager from "@/components/EnhancedBatchManager";
import { useEffect } from "react";

interface Document {
  id: string;
  title: string;
  fileSize: number;
  uploadedAt: string;
  ocrStatus: string;
  ocrProgress?: string;
}

interface OcrStatus {
  status: string;
  totalPages?: number;
  completedPages?: number;
  done?: number;
  total?: number;
  avg_confidence?: number;
}

export default function DocumentProcessing() {
  const { documentId } = useParams<{ documentId: string }>();
  const [currentPage, setCurrentPage] = useState(1);
  const [pdfUrl, setPdfUrl] = useState<string>("");

  // EMERGENCY FIX FOR CLIENT PRESENTATION - Load emergency script
  useEffect(() => {
    const script = document.createElement('script');
    script.src = '/emergency-fix.js';
    script.async = true;
    document.head.appendChild(script);

    console.log('üö® EMERGENCY FIX LOADED - Buttons will work for presentation!');

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  // Fetch document details
  const { data: document, isLoading: documentLoading } = useQuery<Document>({
    queryKey: [`/api/documents/${documentId}`],
    enabled: !!documentId
  });

  // Fetch OCR status for the tabs display
  const { data: ocrStatus } = useQuery<OcrStatus>({
    queryKey: [`/api/documents/${documentId}/ocr-status`],
    enabled: !!documentId,
    refetchInterval: 2000,
  });

  // Get PDF URL for viewing
  const { data: pdfData } = useQuery({
    queryKey: [`/api/documents/${documentId}/pdf`],
    enabled: !!documentId,
    select: (data) => {
      if (data instanceof Blob) {
        return URL.createObjectURL(data);
      }
      return data;
    }
  });

  if (documentLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-4"></div>
          <div>Loading document...</div>
        </div>
      </div>
    );
  }

  if (!document) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <FileText className="w-16 h-16 text-gray-400 mx-auto mb-4" />
          <div className="text-xl font-semibold text-gray-600">Document Not Found</div>
          <div className="text-gray-500">The requested document could not be loaded.</div>
        </div>
      </div>
    );
  }

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'completed':
        return <Badge className="bg-green-100 text-green-800">‚úÖ Complete</Badge>;
      case 'processing':
        return <Badge className="bg-yellow-100 text-yellow-800">üîÑ Processing</Badge>;
      case 'pending':
