                  </CardContent>
                </Card>
              </div>
            </div>
          </TabsContent>

          {/* Generated Links Tab */}
          <TabsContent value="links">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Link className="w-5 h-5" />
                  AI-Generated Hyperlinks
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-center py-12 text-gray-500">
                  <Link className="w-16 h-16 mx-auto mb-4 text-gray-300" />
                  <div className="text-lg font-semibold mb-2">Generated Links Coming Soon</div>
                  <div>Complete manual highlighting to generate hyperlinks between INDEX items and document pages.</div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Settings Tab */}
          <TabsContent value="settings">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Settings className="w-5 h-5" />
                  Document Processing Settings
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <div className="text-sm font-medium">OCR Confidence Threshold</div>
                      <div className="text-xs text-gray-600">Minimum confidence for OCR text acceptance</div>
                      <Badge variant="outline">80% (Default)</Badge>
                    </div>
                    <div className="space-y-2">
                      <div className="text-sm font-medium">AI Matching Algorithm</div>
                      <div className="text-xs text-gray-600">Hybrid fuzzy matching with exact phrase detection</div>
                      <Badge variant="outline">Hybrid (Recommended)</Badge>
                    </div>
                  </div>

                  <div className="border-t pt-4">
                    <div className="text-sm font-medium mb-2">Document Statistics</div>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>Total Pages: <span className="font-medium">{ocrStatus?.totalPages || 'Loading...'}</span></div>
                      <div>OCR Status: {getStatusBadge(document?.ocrStatus || 'pending')}</div>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

server/services/gcvOcr.ts.content:
import { v1 as vision } from '@google-cloud/vision';
import { Storage } from '@google-cloud/storage';
import { promises as fs } from 'fs';
import path from 'path';

const creds = JSON.parse(process.env.GCP_CREDENTIALS_JSON!);
const client = new vision.ImageAnnotatorClient({ credentials: creds });
const storage = new Storage({ credentials: creds });

const INPUT_BUCKET = process.env.GCP_INPUT_BUCKET!;
const OUTPUT_BUCKET = process.env.GCP_OUTPUT_BUCKET!;

export interface GCVOCRResult {
  pageNumber: number;
  text: string;
  confidence: number;
  words: Array<{
    text: string;
    confidence: number;
    boundingBox: any;
  }>;
}

export class GoogleCloudVisionOCR {

  /**
   * Upload PDF to Google Cloud Storage for processing
   */
  async uploadPdfToGCS(localPath: string, gcsPath: string): Promise<string> {
    try {
      await storage.bucket(INPUT_BUCKET).upload(localPath, { 
        destination: gcsPath,
        metadata: {
          contentType: 'application/pdf'
        }
      });
      return `gs://${INPUT_BUCKET}/${gcsPath}`;
    } catch (error) {
      console.error('Error uploading to GCS:', error);
      throw error;
    }
  }

  /**
   * Start async OCR processing on a PDF
   */
  async startGcvPdfOcr(gcsInputUri: string, docId: string): Promise<{
    operationName: string;
    outputPrefix: string;
  }> {
    const outputPrefix = `results/${docId}/${Date.now()}/`;
    const gcsOutputUri = `gs://${OUTPUT_BUCKET}/${outputPrefix}`;

    console.log(`üöÄ Starting GCV OCR for ${gcsInputUri} -> ${gcsOutputUri}`);

    const request = {
      requests: [{
        inputConfig: {
          gcsSource: { uri: gcsInputUri },
          mimeType: 'application/pdf',
        },
        features: [{ type: 'DOCUMENT_TEXT_DETECTION' }],
        outputConfig: {
          gcsDestination: { uri: gcsOutputUri },
          batchSize: 20, // Process in batches of 20 pages
        },
      }],
    };

    try {
      const [operation] = await client.asyncBatchAnnotateFiles(request as any);

      console.log(`‚úÖ GCV OCR operation started: ${operation.name}`);

      return { 
        operationName: operation.name!, 
        outputPrefix 
      };
    } catch (error) {
      console.error('Error starting GCV OCR:', error);
      throw error;
    }
  }

  /**
   * Check if OCR operation is complete and process results
   */
  async pollAndIngestGcvResult(
    operationName: string,
    outputPrefix: string,
    docId: string,
    onPageProcessed: (pageNo: number, text: string, confidence: number, words: any[]) => Promise<void>,
    onProgress: (done: number, total: number, avgConfidence: number) => Promise<void>
  ): Promise<{ done: boolean; pagesProcessed?: number }> {

    try {
      // Check if operation is complete
      const operation = await client.checkAsyncBatchAnnotateFilesProgress(operationName);

      if (!operation.done) {
        console.log(`‚è≥ GCV operation still processing: ${operationName}`);
        return { done: false };
      }

      console.log(`‚úÖ GCV operation completed: ${operationName}`);

      // List and process output files
      const [files] = await storage.bucket(OUTPUT_BUCKET).getFiles({ prefix: outputPrefix });

      let pagesProcessed = 0;
      let totalConfidence = 0;
      const results: GCVOCRResult[] = [];

      for (const file of files) {
        if (!file.name.endsWith('.json')) continue;

        console.log(`üìÑ Processing GCV result file: ${file.name}`);

        const [buffer] = await file.download();
        const json = JSON.parse(buffer.toString());

        // Process each response in the JSON
        for (const response of json.responses || []) {
          const annotation = response.fullTextAnnotation;
          if (!annotation) continue;

          // Extract page number from context or increment
          const pageNumber = response.context?.pageNumber || (pagesProcessed + 1);

          // Extract text
          const text = annotation.text || '';

          // Calculate confidence and extract words with bounding boxes
          const words: any[] = [];
          let confidenceSum = 0;
          let wordCount = 0;

          for (const page of annotation.pages || []) {
            for (const block of page.blocks || []) {
              for (const paragraph of block.paragraphs || []) {
                for (const word of paragraph.words || []) {
                  const wordText = (word.symbols || [])
                    .map((symbol: any) => symbol.text)
                    .join('');

                  const confidence = Number(word.confidence || 0.9);

                  words.push({
                    text: wordText,
                    confidence,
                    boundingBox: word.boundingBox?.vertices || []
                  });

                  confidenceSum += confidence;
                  wordCount++;
                }
              }
            }
          }

          const avgConfidence = wordCount > 0 ? confidenceSum / wordCount : 0.95;

          // Store result
          results.push({
            pageNumber,
            text,
            confidence: avgConfidence,
            words
          });

          pagesProcessed++;
          totalConfidence += avgConfidence;
        }
      }

      // Sort results by page number
      results.sort((a, b) => a.pageNumber - b.pageNumber);

      // Process each page result
      for (const result of results) {
        await onPageProcessed(
          result.pageNumber,
          result.text,
          result.confidence,
          result.words
        );
      }

      // Report final progress
      const avgConfidence = pagesProcessed > 0 ? totalConfidence / pagesProcessed : 0;
      await onProgress(pagesProcessed, pagesProcessed, avgConfidence);

      console.log(`‚úÖ GCV processing complete: ${pagesProcessed} pages processed`);

      return { done: true, pagesProcessed };

    } catch (error) {
      console.error('Error processing GCV results:', error);
      throw error;
    }
  }

  /**
   * Clean up temporary files after processing
   */
  async cleanupGcsFiles(inputPath: string, outputPrefix: string): Promise<void> {
    try {
      // Delete input file
      await storage.bucket(INPUT_BUCKET).file(inputPath.replace(`gs://${INPUT_BUCKET}/`, '')).delete();

      // Delete output files
      const [files] = await storage.bucket(OUTPUT_BUCKET).getFiles({ prefix: outputPrefix });
      await Promise.all(files.map(file => file.delete()));

      console.log(`üßπ Cleaned up GCS files for ${inputPath}`);
    } catch (error) {
      console.warn('Error cleaning up GCS files:', error);
    }
  }
}

export const gcvOcr = new GoogleCloudVisionOCR();

server/services/cloudOcrService.ts.content:
import { gcvOcr } from './gcvOcr';
import { OCRProviderRouter } from './ocrProviderRouter';
import { db } from '../db';
import { documents, ocrPages, ocrJobs } from '@shared/schema';
import { eq, and } from 'drizzle-orm';
import type { Document } from '@shared/schema';
import path from 'path';

export class CloudOcrService {

  /**
   * Start OCR processing with automatic provider selection
   */
  async startOcrProcessing(
    documentId: string, 
    options: {
      forceProvider?: 'tesseract' | 'gcv';
      prioritizeSpeed?: boolean;
    } = {}
  ): Promise<{ provider: string; jobId?: string; estimated: { time: number; cost: number } }> {

    // Get document details
    const [document] = await db
      .select()
      .from(documents)
      .where(eq(documents.id, documentId));

    if (!document) {
      throw new Error(`Document not found: ${documentId}`);
    }

    // Choose optimal provider
    const recommendedProviders = OCRProviderRouter.getRecommendedProvider(document, options);
    let provider = options.forceProvider || recommendedProviders.primary;

    // Fallback if primary provider isn't available
    if (!OCRProviderRouter.isProviderAvailable(provider)) {
      provider = recommendedProviders.fallback;
      if (!OCRProviderRouter.isProviderAvailable(provider)) {
        throw new Error('No OCR providers are available');
      }
    }

    const totalPages = document.totalPages || document.pageCount || 0;
    const estimatedTime = OCRProviderRouter.getEstimatedProcessingTime(provider, totalPages);
    const estimatedCost = OCRProviderRouter.getEstimatedCost(provider, totalPages);

    console.log(`üöÄ Starting OCR with ${provider.toUpperCase()} for document ${documentId} (${totalPages} pages)`);
    console.log(`üìä Estimated: ${Math.round(estimatedTime / 1000)}s, $${estimatedCost.toFixed(2)}`);

    // Update document status
    await db
      .update(documents)
      .set({ 
        ocrStatus: 'processing',
        ocrStartedAt: new Date()
      })
      .where(eq(documents.id, documentId));

    if (provider === 'gcv') {
      return this.startGcvOcr(document);
    } else {
      // For Tesseract, delegate to existing system
      return { 
        provider: 'tesseract',
        estimated: { time: estimatedTime, cost: estimatedCost }
      };
    }
  }

  /**
   * Start Google Cloud Vision OCR processing
   */
  private async startGcvOcr(document: Document): Promise<{ provider: string; jobId: string; estimated: { time: number; cost: number } }> {
    try {
      // Upload PDF to Google Cloud Storage
      const documentPath = document.storagePath;
      const gcsPath = `pdfs/${document.id}/${Date.now()}.pdf`;

      const gcsInputUri = await gcvOcr.uploadPdfToGCS(documentPath, gcsPath);
      console.log(`üì§ Uploaded to GCS: ${gcsInputUri}`);

      // Start async OCR operation
      const { operationName, outputPrefix } = await gcvOcr.startGcvPdfOcr(gcsInputUri, document.id);

      // Create job record
      const [job] = await db
        .insert(ocrJobs)
        .values({
          documentId: document.id,
          provider: 'gcv',
          status: 'processing',
          operationName,
          outputPrefix,
          totalPages: document.totalPages || document.pageCount || 0
        })
        .returning({ id: ocrJobs.id });

      const totalPages = document.totalPages || document.pageCount || 0;
      const estimatedTime = OCRProviderRouter.getEstimatedProcessingTime('gcv', totalPages);
      const estimatedCost = OCRProviderRouter.getEstimatedCost('gcv', totalPages);

      console.log(`‚úÖ GCV job started: ${job.id}, operation: ${operationName}`);

      // Start polling in background (don't await)
      this.pollGcvJob(job.id, operationName, outputPrefix, document.id);

      return { 
        provider: 'gcv',
        jobId: job.id,
        estimated: { time: estimatedTime, cost: estimatedCost }
      };

    } catch (error) {
      console.error('Error starting GCV OCR:', error);

      // Update document status to failed
      await db
        .update(documents)
        .set({ 
          ocrStatus: 'failed',
          lastError: `GCV OCR failed: ${error.message}`
        })
        .where(eq(documents.id, document.id));

      throw error;
    }
  }

  /**
   * Poll Google Cloud Vision job status and ingest results
   */
  private async pollGcvJob(jobId: string, operationName: string, outputPrefix: string, documentId: string): Promise<void> {
    const maxPollAttempts = 60; // Poll for up to 10 minutes
    let attempt = 0;

    const pollInterval = setInterval(async () => {
      try {
        attempt++;
        console.log(`üîÑ Polling GCV job ${jobId}, attempt ${attempt}/${maxPollAttempts}`);

        const result = await gcvOcr.pollAndIngestGcvResult(
          operationName,
          outputPrefix,
          documentId,
          // On page processed callback
          async (pageNo: number, text: string, confidence: number, words: any[]) => {
            await this.saveOcrPage(documentId, pageNo, text, confidence, words, 'gcv');
          },
          // On progress callback
          async (done: number, total: number, avgConfidence: number) => {
            await this.updateDocumentProgress(documentId, done, total, avgConfidence);
          }
        );

        if (result.done) {
          console.log(`‚úÖ GCV job completed: ${jobId}, processed ${result.pagesProcessed} pages`);

          // Mark job as completed
          await db
            .update(ocrJobs)
            .set({ 
              status: 'completed',
              completedAt: new Date(),
              pagesProcessed: result.pagesProcessed || 0
            })
            .where(eq(ocrJobs.id, jobId));

          // Mark document as completed
          await db
            .update(documents)
            .set({ 
              ocrStatus: 'completed',
              ocrCompletedAt: new Date()
            })
            .where(eq(documents.id, documentId));

          clearInterval(pollInterval);
          return;
        }

        if (attempt >= maxPollAttempts) {
          console.error(`‚ùå GCV job ${jobId} polling timeout after ${attempt} attempts`);

          await db
            .update(ocrJobs)
            .set({ 
              status: 'failed',
              errorDetails: { error: 'Polling timeout' }
            })
            .where(eq(ocrJobs.id, jobId));

          clearInterval(pollInterval);
          return;
        }

      } catch (error) {
        console.error(`‚ùå Error polling GCV job ${jobId}:`, error);

        await db
          .update(ocrJobs)
          .set({ 
            status: 'failed',
            errorDetails: { error: error.message }
          })
          .where(eq(ocrJobs.id, jobId));

        clearInterval(pollInterval);
      }
    }, 10000); // Poll every 10 seconds
  }

  /**
   * Save OCR page result to database
   */
  private async saveOcrPage(
    documentId: string,
    pageNumber: number,
    text: string,
    confidence: number,
    words: any[],
    provider: 'tesseract' | 'gcv'
  ): Promise<void> {
    await db
      .insert(ocrPages)
      .values({
        documentId,
        pageNumber,
        provider,
        extractedText: text,
        confidence: confidence.toString(),
        wordsJson: words,
        status: 'completed',
        processingTimeMs: null // GCV doesn't provide per-page timing
      })
      .onConflictDoUpdate({
        target: [ocrPages.documentId, ocrPages.pageNumber],
        set: {
          extractedText: text,
          confidence: confidence.toString(),
          wordsJson: words,
          provider,
          status: 'completed'
        }
      });
  }

  /**
   * Update document processing progress
   */
  private async updateDocumentProgress(
    documentId: string,
    done: number,
    total: number,
    avgConfidence: number
  ): Promise<void> {
    await db
      .update(documents)
      .set({
        ocrPagesDone: done,
        ocrConfidenceAvg: avgConfidence.toString(),
        ocrStatus: done >= total ? 'completed' : 'processing'
      })
      .where(eq(documents.id, documentId));

    console.log(`üìä Document ${documentId} progress: ${done}/${total} pages (${Math.round(avgConfidence * 100)}% avg confidence)`);
  }

  /**
   * Get OCR job status
   */
  async getJobStatus(jobId: string) {
    const [job] = await db
      .select()
      .from(ocrJobs)
      .where(eq(ocrJobs.id, jobId));

    return job;
  }

  /**
   * Get all jobs for a document
   */
  async getDocumentJobs(documentId: string) {
    return db
      .select()
      .from(ocrJobs)
      .where(eq(ocrJobs.documentId, documentId));
  }
}

export const cloudOcrService = new CloudOcrService();

