// üñ•Ô∏è BROWSER CONSOLE VERIFICATION SCRIPT
// Copy-paste this into your browser console (F12) to monitor parallel OCR

console.log("üöÄ ============ PARALLEL OCR MONITOR STARTING ============");

const documentId = 'eeb2949a-feaf-4878-b79b-bb09a72290f7';
let lastPageCount = 0;
let startTime = Date.now();

// 1. üì° Real-time SSE Progress Monitor
const monitorSSE = () => {
    const eventSource = new EventSource(`/api/documents/${documentId}/ocr/stream`);

    eventSource.addEventListener('ocr_progress', (event) => {
        const data = JSON.parse(event.data);
        const currentTime = new Date().toLocaleTimeString();
        const pagesAdded = data.done - lastPageCount;
        const totalTime = ((Date.now() - startTime) / 1000 / 60).toFixed(1);

        console.log(`‚ö° [${currentTime}] OCR Progress:`, {
            pages: `${data.done}/${data.total}`,
            pagesAdded: pagesAdded,
            percent: `${data.percent}%`,
            totalMinutes: totalTime,
            confidence: `${data.avg_conf}%`
        });

        // üî• PARALLEL DETECTION
        if (pagesAdded >= 10) {
            console.log(`üöÄ PARALLEL CONFIRMED: ${pagesAdded} pages added in one batch!`);
        } else if (pagesAdded === 1) {
            console.log(`üêå SERIAL DETECTED: Only 1 page added (slow processing)`);
        }

        lastPageCount = data.done;
    });

    eventSource.onerror = (error) => {
        console.log("‚ùå SSE connection error:", error);
    };

    return eventSource;
};

// 2. üìä API Polling Monitor (backup method)
const monitorAPI = () => {
    const pollStatus = async () => {
        try {
            const response = await fetch(`/api/documents/${documentId}/ocr-status`);
            const data = await response.json();
            const currentTime = new Date().toLocaleTimeString();
            const pagesAdded = data.done - lastPageCount;

            console.log(`üìä [${currentTime}] API Status:`, {
                pages: `${data.done}/${data.total}`,
                pagesAdded: pagesAdded,
                status: data.status,
                progress: `${Math.round((data.done/data.total)*100)}%`
            });

            // Speed analysis
            if (pagesAdded >= 20) {
                console.log(`üöÄ HIGH SPEED: ${pagesAdded} pages in 5 seconds = PARALLEL MODE`);
            } else if (pagesAdded === 1) {
                console.log(`üêå LOW SPEED: 1 page in 5 seconds = SERIAL MODE`);
            }

            lastPageCount = data.done;
        } catch (error) {
            console.log("‚ùå API polling error:", error);
        }
    };

    return setInterval(pollStatus, 5000); // Poll every 5 seconds
};

// 3. üéØ Start Monitoring
console.log("üì° Starting SSE monitor...");
const sseConnection = monitorSSE();

console.log("üìä Starting API polling backup...");
const apiInterval = monitorAPI();

// 4. üõë Stop Monitoring Function
window.stopOCRMonitor = () => {
    console.log("üõë Stopping OCR monitoring...");
    sseConnection.close();
    clearInterval(apiInterval);
    console.log("‚úÖ Monitoring stopped. Call startOCRMonitor() to restart.");
};

// 5. üîÑ Restart Monitoring Function
window.startOCRMonitor = () => {
    console.log("üîÑ Restarting OCR monitoring...");
    lastPageCount = 0;
    startTime = Date.now();
    const newSSE = monitorSSE();
    const newAPI = monitorAPI();

    window.stopOCRMonitor = () => {
        newSSE.close();
        clearInterval(newAPI);
    };
};

console.log("‚úÖ Monitor started! Commands available:");
console.log("   ‚Ä¢ stopOCRMonitor() - Stop monitoring");
console.log("   ‚Ä¢ startOCRMonitor() - Restart monitoring");
console.log("============================================");

