import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { 
  Card, 
  CardContent, 
  CardHeader, 
  CardTitle 
} from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { 
  RefreshCw, 
  Edit3, 
  Save, 
  X, 
  AlertTriangle, 
  FileText,
  Eye,
  EyeOff,
  Loader2
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";

interface BatchDetailProps {
  batchId: string;
  documentId: string;
  batchNumber: number;
  startPage: number;
  endPage: number;
  onClose: () => void;
}

interface OcrPage {
  id: string;
  pageNumber: number;
  extractedText: string;
  confidence: number;
  ocrEngine: string;
  createdAt: string;
  correctedText?: string;
  isCorrected: boolean;
  correctedBy?: string;
  correctedAt?: string;
}

export default function BatchDetail({ 
  batchId, 
  documentId, 
  batchNumber, 
  startPage, 
  endPage, 
  onClose 
}: BatchDetailProps) {
  const { toast } = useToast();
  const [editingPage, setEditingPage] = useState<number | null>(null);
  const [editedText, setEditedText] = useState("");
  const [expandedPage, setExpandedPage] = useState<number | null>(null);

  // Fetch OCR pages for this batch
  const { data: pagesData, isLoading } = useQuery<{ success: boolean; pages: OcrPage[] }>({
    queryKey: [`/api/documents/${documentId}/batches/${batchId}/pages`],
    enabled: !!batchId && !!documentId
  });

  const pages = pagesData?.pages || [];
  const totalPages = endPage - startPage + 1;
  const pagesWithData = pages.length;
  const missingPages = totalPages - pagesWithData;

  // Re-OCR individual page mutation
  const reOcrPage = useMutation({
    mutationFn: async (pageNumber: number) => {
      const response = await fetch(`/api/documents/${documentId}/pages/${pageNumber}/re-ocr`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ engine: 'vision' })
      });
      if (!response.ok) throw new Error('Failed to re-OCR page');
      return response.json();
    },
    onSuccess: (_, pageNumber) => {
      toast({
        title: "Re-OCR Complete",
        description: `Page ${pageNumber} has been re-processed with Google Cloud Vision`,
      });
      queryClient.invalidateQueries({ 
        queryKey: [`/api/documents/${documentId}/batches/${batchId}/pages`] 
      });
    },
    onError: (error) => {
      toast({
        variant: "destructive",
        title: "Re-OCR Failed",
        description: error instanceof Error ? error.message : "Failed to re-OCR page",
      });
    }
  });

  // Save manual edit mutation
  const saveEdit = useMutation({
    mutationFn: async ({ pageNumber, text }: { pageNumber: number; text: string }) => {
      const response = await fetch(`/api/documents/${documentId}/pages/${pageNumber}/edit`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ correctedText: text })
      });
      if (!response.ok) throw new Error('Failed to save manual edit');
      return response.json();
    },
    onSuccess: (_, { pageNumber }) => {
      toast({
        title: "Text Updated",
        description: `Page ${pageNumber} text has been manually updated`,
      });
      setEditingPage(null);
      setEditedText("");
      queryClient.invalidateQueries({ 
        queryKey: [`/api/documents/${documentId}/batches/${batchId}/pages`] 
      });
    },
    onError: (error) => {
      toast({
        variant: "destructive", 
        title: "Save Failed",
        description: error instanceof Error ? error.message : "Failed to save manual edit",
      });
    }
  });

  const handleStartEdit = (page: OcrPage) => {
    setEditingPage(page.pageNumber);
    setEditedText(page.correctedText || page.extractedText || "");
  };

  const handleSaveEdit = () => {
    if (editingPage) {
      saveEdit.mutate({ pageNumber: editingPage, text: editedText });
    }
  };

  const handleCancelEdit = () => {
    setEditingPage(null);
    setEditedText("");
  };

  const togglePageExpansion = (pageNumber: number) => {
    setExpandedPage(expandedPage === pageNumber ? null : pageNumber);
  };

  const getPageData = (pageNumber: number) => {
    return pages.find(p => p.pageNumber === pageNumber);
  };

  const getPageStatus = (pageNumber: number) => {
    const pageData = getPageData(pageNumber);
    if (!pageData) return "missing";
    if (pageData.isCorreected) return "edited";
    if (pageData.confidence < 0.7) return "low-confidence";
    return "good";
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "missing":
        return <Badge variant="destructive" className="flex items-center gap-1">
          <AlertTriangle className="w-3 h-3" />Missing
        </Badge>;
      case "edited":
        return <Badge variant="default" className="bg-green-600 flex items-center gap-1">
          <Edit3 className="w-3 h-3" />Edited
        </Badge>;
      case "low-confidence":
        return <Badge variant="secondary" className="bg-yellow-500 text-yellow-900 flex items-center gap-1">
          <AlertTriangle className="w-3 h-3" />Low Quality
        </Badge>;
      case "good":
        return <Badge variant="default" className="bg-blue-500 flex items-center gap-1">
          <FileText className="w-3 h-3" />Good
        </Badge>;
      default:
        return <Badge variant="outline">Unknown</Badge>;
    }
  };

  if (isLoading) {
    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
        <Card className="w-full max-w-4xl m-4">
          <CardContent className="p-6">
            <div className="flex items-center justify-center">
              <Loader2 className="w-8 h-8 animate-spin mr-2" />
              Loading batch pages...
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
      <Card className="w-full max-w-6xl h-full max-h-[90vh] flex flex-col">
        <CardHeader className="flex-shrink-0">
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-3">
              <FileText className="w-5 h-5" />
              Batch {batchNumber} - Pages {startPage}-{endPage}
              <div className="flex gap-2">
                <Badge variant="outline">{pagesWithData}/{totalPages} pages</Badge>
                {missingPages > 0 && (
                  <Badge variant="destructive">{missingPages} missing</Badge>
                )}
              </div>
            </CardTitle>
            <div className="flex gap-2">
              <Button 
                variant="outline" 
                size="sm" 
                onClick={() => alert(`Re-OCR entire batch ${startPage}-${endPage} with fast processing`)}
                className="bg-orange-500 hover:bg-orange-600 text-white"
                data-testid="button-re-ocr-batch"
              >
                <RefreshCw className="w-4 h-4 mr-1" />
                Re-OCR Batch
              </Button>
              <Button 
                variant="outline" 
                size="sm" 
                onClick={onClose}
                data-testid="button-close-batch-detail"
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          </div>
        </CardHeader>

        <CardContent className="flex-1 overflow-y-auto space-y-4">
          {Array.from({ length: totalPages }, (_, i) => {
            const pageNumber = startPage + i;
            const pageData = getPageData(pageNumber);
            const status = getPageStatus(pageNumber);
            const isExpanded = expandedPage === pageNumber;
            const isEditing = editingPage === pageNumber;

            return (
              <Card key={pageNumber} className="border-l-4 border-l-blue-500">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center gap-3">
                      <span className="font-semibold">Page {pageNumber}</span>
                      {getStatusBadge(status)}
                      {pageData && (
                        <Badge variant="outline" className="text-xs">
                          {pageData.ocrEngine} ‚Ä¢ {Math.round((pageData.confidence || 0) * 100)}%
                        </Badge>
                      )}
                    </div>

                    <div className="flex items-center gap-2">
                      {pageData && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => togglePageExpansion(pageNumber)}
                          data-testid={`button-toggle-page-${pageNumber}`}
                        >
                          {isExpanded ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                          {isExpanded ? "Hide" : "View"}
                        </Button>
                      )}

                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => reOcrPage.mutate(pageNumber)}
                        disabled={reOcrPage.isPending}
                        data-testid={`button-reocr-page-${pageNumber}`}
                      >
                        {reOcrPage.isPending ? (
                          <Loader2 className="w-4 h-4 animate-spin" />
                        ) : (
                          <RefreshCw className="w-4 h-4" />
                        )}
                        Re-OCR
                      </Button>

                      {pageData && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleStartEdit(pageData)}
                          disabled={isEditing}
                          data-testid={`button-edit-page-${pageNumber}`}
                        >
                          <Edit3 className="w-4 h-4" />
                          Edit
                        </Button>
                      )}
                    </div>
                  </div>

                  {/* OCR Text Display/Edit */}
                  {isExpanded && (
                    <div className="mt-4">
                      {isEditing ? (
                        <div className="space-y-3">
                          <Textarea
                            value={editedText}
                            onChange={(e) => setEditedText(e.target.value)}
                            rows={10}
                            className="font-mono text-sm"
                            placeholder="Enter or edit the OCR text for this page..."
                            data-testid={`textarea-edit-page-${pageNumber}`}
                          />
                          <div className="flex justify-end gap-2">
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={handleCancelEdit}
                              data-testid={`button-cancel-edit-${pageNumber}`}
                            >
                              <X className="w-4 h-4 mr-1" />
                              Cancel
                            </Button>
                            <Button
                              size="sm"
                              onClick={handleSaveEdit}
                              disabled={saveEdit.isPending}
                              data-testid={`button-save-edit-${pageNumber}`}
                            >
                              {saveEdit.isPending ? (
                                <Loader2 className="w-4 h-4 animate-spin mr-1" />
                              ) : (
                                <Save className="w-4 h-4 mr-1" />
                              )}
                              Save
                            </Button>
                          </div>
                        </div>
                      ) : pageData ? (
                        <div className="bg-gray-50 p-4 rounded border font-mono text-sm whitespace-pre-wrap max-h-64 overflow-y-auto">
                          {pageData.correctedText || pageData.extractedText || "No text extracted"}
                        </div>
                      ) : (
                        <div className="bg-red-50 p-4 rounded border text-red-700">
                          <div className="flex items-center gap-2">
                            <AlertTriangle className="w-5 h-5" />
                            <span>No OCR data found for this page</span>
                          </div>
                          <p className="text-sm mt-2">
                            This page was not processed during the initial OCR run. 
                            Click "Re-OCR" to process it with Google Cloud Vision.
                          </p>
                        </div>
                      )}

                      {pageData?.correctedAt && (
                        <div className="text-xs text-gray-500 mt-2">
                          Last edited: {new Date(pageData.correctedAt).toLocaleString()}
                          {pageData.correctedBy && ` by ${pageData.correctedBy}`}
                        </div>
                      )}
                    </div>
                  )}
                </CardContent>
              </Card>
            );
          })}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/BatchOcrViewer.tsx.content:
import React, { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Textarea } from '@/components/ui/textarea';
import { X, FileText, RefreshCw, Copy, Download, CheckCircle, AlertCircle, ChevronLeft, ChevronRight, Eye, RotateCcw, Edit, Save, Sparkles, Brain } from 'lucide-react';
import { queryClient } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface BatchOcrViewerProps {
  batchId: string;
  documentId: string;
  startPage: number;
  endPage: number;
  onClose: () => void;
}

interface BatchOcrData {
  batchId: string;
  startPage: number;
  endPage: number;
  pages: Array<{
    pageNumber: number;
    extractedText: string;
    confidence: number;
    ocrEngine: string;
    status: 'missing' | 'empty' | 'completed' | 'failed';
    isCorrected?: boolean;
    isManualEdit?: boolean;
    qualityScore?: number;
    needsReOCR?: boolean;
    lastVerified?: string;
  }>;
  totalText: string;
  totalPages: number;
  pagesWithText: number;
  averageConfidence: number;
}

interface QualityCheck {
  qualityScore: number;
  needsReOCR: boolean;
  issues: string[];
  confidence: number;
  recommendedAction: string;
}

export default function BatchOcrViewer({
  batchId,
  documentId,
  startPage,
  endPage,
  onClose
}: BatchOcrViewerProps) {
  const [isReOCRing, setIsReOCRing] = useState(false);
  const [currentPageIndex, setCurrentPageIndex] = useState(0);
  const [viewMode, setViewMode] = useState<'combined' | 'individual'>('combined');
  const [reOCRingPage, setReOCRingPage] = useState<number | null>(null);
  const [editingPage, setEditingPage] = useState<number | null>(null);
  const [editText, setEditText] = useState('');
  const [verifyingPage, setVerifyingPage] = useState<number | null>(null);
  const { toast } = useToast();

  // Fetch batch OCR data
  const { data: batchData, isLoading, refetch } = useQuery<BatchOcrData>({
    queryKey: [`/api/documents/${documentId}/batches/${batchId}/ocr`],
    retry: 1
  });

  // Re-OCR entire batch
  const reOcrBatch = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/documents/${documentId}/batches/${batchId}/reocr`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      });
      if (!response.ok) throw new Error('Failed to re-OCR batch');
      return response.json();
    },
    onMutate: () => {
      setIsReOCRing(true);
    },
    onSuccess: () => {
      toast({
        title: "Batch Re-OCR Started",
        description: `Re-processing pages ${startPage}-${endPage} with Google Cloud Vision...`,
      });
      refetch();
      queryClient.invalidateQueries({ queryKey: [`/api/documents/${documentId}/batches`] });
    },
    onError: (error) => {
      toast({
        title: "Re-OCR Failed",
        description: error instanceof Error ? error.message : "Failed to start re-OCR",
        variant: "destructive"
      });
    },
    onSettled: () => {
      setIsReOCRing(false);
    }
  });

  // Re-OCR individual page with LLM verification
  const reOcrPage = useMutation({
    mutationFn: async ({ pageNumber, withLLM = true }: { pageNumber: number; withLLM?: boolean }) => {
      const response = await fetch(`/api/documents/${documentId}/pages/${pageNumber}/re-ocr`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ engine: 'vision', verifyWithLLM: withLLM })
      });
      if (!response.ok) throw new Error('Failed to re-OCR page');
      return response.json();
    },
    onMutate: ({ pageNumber }) => {
      setReOCRingPage(pageNumber);
    },
    onSuccess: (data, { pageNumber }) => {
      const qualityInfo = data.qualityCheck 
        ? ` (Quality: ${data.qualityCheck.qualityScore}%)`
        : '';
      toast({
        title: "Page Re-OCR Complete",
        description: `Page ${pageNumber} re-processed with ${data.textLength} characters${qualityInfo}`,
      });
      refetch();
      queryClient.invalidateQueries({ queryKey: [`/api/documents/${documentId}/batches`] });
    },
    onError: (error, { pageNumber }) => {
      toast({
        title: "Page Re-OCR Failed",
        description: `Failed to re-OCR page ${pageNumber}: ${error instanceof Error ? error.message : 'Unknown error'}`,
        variant: "destructive"
      });
    },
    onSettled: () => {
      setReOCRingPage(null);
    }
  });

  // Save manual edits
  const savePageEdit = useMutation({
    mutationFn: async ({ pageNumber, text }: { pageNumber: number; text: string }) => {
      const response = await fetch(`/api/documents/${documentId}/pages/${pageNumber}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text })
      });
      if (!response.ok) throw new Error('Failed to save page edit');
      return response.json();
    },
    onSuccess: (data, { pageNumber }) => {
      toast({
        title: "Page Edit Saved",
        description: `Page ${pageNumber} manually edited and saved (${data.textLength} characters)`,
      });
      setEditingPage(null);
      setEditText('');
      refetch();
      queryClient.invalidateQueries({ queryKey: [`/api/documents/${documentId}/batches`] });
    },
    onError: (error, { pageNumber }) => {
      toast({
        title: "Save Failed",
        description: `Failed to save edits for page ${pageNumber}: ${error instanceof Error ? error.message : 'Unknown error'}`,
        variant: "destructive"
      });
    }
  });

  // LLM Quality Verification
  const verifyWithLLM = useMutation({
    mutationFn: async (pageNumber: number) => {
      // Re-OCR with LLM verification enabled
      return reOcrPage.mutateAsync({ pageNumber, withLLM: true });
    },
    onMutate: (pageNumber) => {
      setVerifyingPage(pageNumber);
    },
    onSettled: () => {
      setVerifyingPage(null);
    }
  });

  const copyToClipboard = async () => {
    if (batchData?.totalText) {
      await navigator.clipboard.writeText(batchData.totalText);
      toast({
        title: "Copied to Clipboard",
        description: `${batchData.totalText.length} characters copied`,
      });
    }
  };

  const downloadText = () => {
    if (batchData?.totalText) {
      const blob = new Blob([batchData.totalText], { type: 'text/plain' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `batch-${startPage}-${endPage}-ocr.txt`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }
  };

  const getPageStatusBadge = (page: any) => {
    const { status, isManualEdit, qualityScore, needsReOCR } = page;

    if (isManualEdit) {
      return <Badge variant="default" className="bg-blue-500"><Edit className="w-3 h-3 mr-1" />Manual</Badge>;
    }

    if (qualityScore && qualityScore < 85) {
      return <Badge variant="destructive"><AlertCircle className="w-3 h-3 mr-1" />Low Quality ({qualityScore}%)</Badge>;
    }

    if (needsReOCR) {
      return <Badge variant="outline" className="border-orange-500 text-orange-600"><Brain className="w-3 h-3 mr-1" />Needs Re-OCR</Badge>;
    }

    switch (status) {
      case 'completed':
        const qualityText = qualityScore ? ` (${qualityScore}%)` : '';
        return <Badge variant="default" className="bg-green-500"><CheckCircle className="w-3 h-3 mr-1" />Complete{qualityText}</Badge>;
      case 'missing':
        return <Badge variant="destructive"><AlertCircle className="w-3 h-3 mr-1" />Missing</Badge>;
      case 'empty':
        return <Badge variant="secondary"><AlertCircle className="w-3 h-3 mr-1" />Empty</Badge>;
      case 'failed':
        return <Badge variant="destructive">Failed</Badge>;
      default:
        return <Badge variant="outline">Unknown</Badge>;
    }
  };

  const startEditing = (page: any) => {
    setEditingPage(page.pageNumber);
    setEditText(page.extractedText || '');
  };

  const cancelEdit = () => {
    setEditingPage(null);
    setEditText('');
  };

  const currentPage = batchData?.pages?.[currentPageIndex];
  const canNavigate = batchData?.pages && batchData.pages.length > 1;

  const goToPage = (direction: 'prev' | 'next') => {
    if (!batchData?.pages) return;

    if (direction === 'prev' && currentPageIndex > 0) {
      setCurrentPageIndex(currentPageIndex - 1);
    } else if (direction === 'next' && currentPageIndex < batchData.pages.length - 1) {
      setCurrentPageIndex(currentPageIndex + 1);
    }
  };

  if (isLoading) {
    return (
      <Dialog open={true} onOpenChange={onClose}>
        <DialogContent className="max-w-6xl max-h-[90vh] overflow-hidden">
          <DialogHeader>
            <DialogTitle>Loading OCR Text...</DialogTitle>
          </DialogHeader>
          <div className="p-8 text-center">
            <div className="animate-spin w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto mb-4"></div>
            <div>Loading batch OCR data...</div>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={true} onOpenChange={onClose}>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-hidden">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            <span className="flex items-center gap-2">
              <FileText className="w-5 h-5" />
              OCR Text - Pages {startPage}-{endPage}
            </span>
            <Button
              variant="outline"
              size="sm"
              onClick={onClose}
              className="h-8 w-8 p-0"
            >
              <X className="w-4 h-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {/* Stats */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 p-4 bg-slate-50 dark:bg-slate-800 rounded-lg">
            <div className="text-center">
              <div className="text-xl font-bold text-blue-600">{batchData?.totalPages || 0}</div>
              <div className="text-xs text-gray-600">Total Pages</div>
            </div>
            <div className="text-center">
              <div className="text-xl font-bold text-green-600">{batchData?.pagesWithText || 0}</div>
              <div className="text-xs text-gray-600">With Text</div>
            </div>
            <div className="text-center">
              <div className="text-xl font-bold text-purple-600">
                {batchData?.averageConfidence ? Math.round(batchData.averageConfidence * 100) : 0}%
              </div>
              <div className="text-xs text-gray-600">Avg Confidence</div>
            </div>
            <div className="text-center">
              <div className="text-xl font-bold text-orange-600">
                {batchData?.totalText?.length?.toLocaleString() || 0}
              </div>
              <div className="text-xs text-gray-600">Characters</div>
            </div>
          </div>

          {/* View Mode Toggle and Action Buttons */}
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-2">
              <Button
                variant={viewMode === 'combined' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setViewMode('combined')}
                className="flex items-center gap-2"
              >
                <FileText className="w-4 h-4" />
                Combined View
              </Button>
              <Button
                variant={viewMode === 'individual' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setViewMode('individual')}
                className="flex items-center gap-2"
              >
                <Eye className="w-4 h-4" />
                Page-by-Page
              </Button>
            </div>

            <div className="flex gap-2">
              <Button
                onClick={() => reOcrBatch.mutate()}
                disabled={isReOCRing}
                className="bg-orange-500 hover:bg-orange-600 text-white flex items-center gap-2"
                data-testid="button-re-ocr-batch"
              >
                <RefreshCw className={`w-4 h-4 ${isReOCRing ? 'animate-spin' : ''}`} />
                {isReOCRing ? 'Re-OCR in Progress...' : 'Re-OCR Entire Batch'}
              </Button>
              <Button
                onClick={copyToClipboard}
                variant="outline"
                className="flex items-center gap-2"
              >
                <Copy className="w-4 h-4" />
                Copy Text
              </Button>
              <Button
                onClick={downloadText}
                variant="outline"
                className="flex items-center gap-2"
              >
                <Download className="w-4 h-4" />
                Download
              </Button>
            </div>
          </div>

          {/* Page Navigation (Individual View Only) */}
          {viewMode === 'individual' && canNavigate && (
            <div className="flex items-center justify-between p-4 bg-slate-100 dark:bg-slate-700 rounded-lg">
              <Button
                variant="outline"
                size="sm"
                onClick={() => goToPage('prev')}
                disabled={currentPageIndex === 0}
                className="flex items-center gap-2"
              >
                <ChevronLeft className="w-4 h-4" />
                Previous
              </Button>

              <div className="flex items-center gap-4">
                <span className="text-sm font-medium">
                  Page {currentPage?.pageNumber || (startPage + currentPageIndex)} of {endPage}
                </span>
                {currentPage && (
                  <div className="flex items-center gap-2">
                    {getPageStatusBadge(currentPage)}
                    <Button
                      onClick={() => reOcrPage.mutate({ pageNumber: currentPage.pageNumber })}
                      disabled={reOCRingPage === currentPage.pageNumber}
                      size="sm"
                      variant="outline"
                      className="flex items-center gap-1"
                    >
                      <RotateCcw className={`w-3 h-3 ${reOCRingPage === currentPage.pageNumber ? 'animate-spin' : ''}`} />
                      {reOCRingPage === currentPage.pageNumber ? 'Re-OCR...' : 'Re-OCR Page'}
                    </Button>
                  </div>
                )}
              </div>

              <Button
                variant="outline"
                size="sm"
                onClick={() => goToPage('next')}
                disabled={currentPageIndex >= (batchData?.pages?.length || 1) - 1}
                className="flex items-center gap-2"
              >
                Next
                <ChevronRight className="w-4 h-4" />
              </Button>
            </div>
          )}

          {/* OCR Text Display */}
          <div className="space-y-4 max-h-[60vh] overflow-y-auto">
            {viewMode === 'combined' ? (
              /* Combined Text View */
              <div>
                <h3 className="font-semibold mb-2">Combined OCR Text ({batchData?.totalText?.length || 0} characters)</h3>
                <Textarea
                  value={batchData?.totalText || 'No OCR text found for this batch.'}
                  readOnly
                  className="min-h-[400px] font-mono text-sm"
                  placeholder="No OCR text available..."
                />

                {/* Page Summary Cards */}
                <div className="mt-4">
                  <h4 className="font-medium mb-2">Page Summary</h4>
                  <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2">
                    {batchData?.pages?.map((page) => (
                      <div key={page.pageNumber} className="border rounded p-2 text-sm">
                        <div className="flex items-center justify-between mb-1">
                          <span className="font-medium">Page {page.pageNumber}</span>
                          {getPageStatusBadge(page)}
                        </div>
                        <div className="text-xs text-gray-600">
                          {page.extractedText ? `${page.extractedText.length} chars` : 'No text'}
                          {page.confidence > 0 && (
                            <span className="ml-2">{Math.round(page.confidence * 100)}%</span>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            ) : (
              /* Individual Page View */
              <div>
                {currentPage ? (
                  <div>
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="font-semibold">Page {currentPage.pageNumber} OCR Text</h3>
                      <div className="flex items-center gap-2">
                        {getPageStatusBadge(currentPage)}
                        {currentPage.confidence > 0 && (
                          <span className="text-sm text-gray-600">
                            {Math.round(currentPage.confidence * 100)}% confidence
                          </span>
                        )}
                      </div>
                    </div>

                    {currentPage.extractedText ? (
                      <div>
                        <div className="text-sm text-gray-600 mb-2">
                          {currentPage.extractedText.length} characters extracted
                          {currentPage.isCorrected && (
                            <span className="ml-2 text-green-600">‚Ä¢ Manually corrected</span>
                          )}
                        </div>
                        <Textarea
                          value={currentPage.extractedText}
                          readOnly
                          className="min-h-[400px] font-mono text-sm"
                          placeholder="No OCR text available for this page..."
                        />
                      </div>
                    ) : (
                      <div className="text-center py-8">
                        <AlertCircle className="w-16 h-16 mx-auto mb-4 text-gray-400" />
                        <div className="text-lg font-medium text-gray-600 mb-2">
                          No OCR text found for Page {currentPage.pageNumber}
                        </div>
                        <div className="text-sm text-gray-500 mb-4">
                          This page may need to be re-processed with OCR
                        </div>
                        <Button
                          onClick={() => reOcrPage.mutate({ pageNumber: currentPage.pageNumber })}
                          disabled={reOCRingPage === currentPage.pageNumber}
                          className="flex items-center gap-2"
                        >
                          <RotateCcw className={`w-4 h-4 ${reOCRingPage === currentPage.pageNumber ? 'animate-spin' : ''}`} />
                          {reOCRingPage === currentPage.pageNumber ? 'Re-OCR in Progress...' : 'Re-OCR This Page'}
                        </Button>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="text-center py-8 text-gray-500">
                    No page data available
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

