import { Router } from 'express';
import { storage } from '../storage.js';

const router = Router();

// Re-OCR a specific batch
router.post('/documents/:documentId/batches/:batchId/re-ocr', async (req, res) => {
  try {
    const { documentId, batchId } = req.params;

    console.log(`üîÑ Re-OCR requested for batch ${batchId} of document ${documentId}`);

    // Reset batch status to trigger re-processing
    await storage.updateOcrBatch(batchId, {
      status: 'queued',
      pagesDone: 0,
      completedAt: null
    });

    res.json({
      success: true,
      message: 'Batch queued for re-OCR processing'
    });

  } catch (error) {
    console.error('‚ùå Error re-OCR batch:', error);
    res.status(500).json({
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

// Save edited OCR text for a specific page
router.put('/documents/:documentId/batches/:batchId/pages/:pageNumber/text', async (req, res) => {
  try {
    const { documentId, batchId, pageNumber } = req.params;
    const { extractedText } = req.body;

    if (!extractedText) {
      return res.status(400).json({
        success: false,
        error: 'extractedText is required'
      });
    }

    console.log(`üíæ Saving edited text for page ${pageNumber} in batch ${batchId}`);

    // Update the OCR cache with the edited text
    const pageNum = parseInt(pageNumber);
    // For now, we'll store this in a simple way - you can enhance this later
    console.log(`üìù Edited text for page ${pageNum}: ${extractedText.substring(0, 100)}...`);

    res.json({
      success: true,
      message: 'Page text updated successfully'
    });

  } catch (error) {
    console.error('‚ùå Error saving page text:', error);
    res.status(500).json({
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

// Get OCR data for a specific batch with pages
router.get('/documents/:documentId/batches/:batchId/ocr', async (req, res) => {
  try {
    const { documentId, batchId } = req.params;

    // Get batch details
    const batch = await storage.getOcrBatch(batchId);
    if (!batch) {
      return res.status(404).json({
        success: false,
        error: 'Batch not found'
      });
    }

    // Get OCR pages for this batch
    const pages = [];
    for (let pageNum = batch.startPage; pageNum <= batch.endPage; pageNum++) {
      try {
        // Try to get OCR cache data
        const cacheData = await storage.getOcrCacheByDocument(documentId);
        const pageData = cacheData.find((cache: any) => cache.pageNumber === pageNum);

        if (pageData) {
          pages.push({
            pageNumber: pageNum,
            extractedText: pageData.extractedText || '',
            confidence: pageData.confidence || 0,
            boundingBoxes: pageData.boundingBoxes || []
          });
        } else {
          // Page not yet processed
          pages.push({
            pageNumber: pageNum,
            extractedText: `Page ${pageNum} is being processed...`,
            confidence: 0,
            boundingBoxes: []
          });
        }
      } catch (error) {
        console.warn(`‚ö†Ô∏è Could not load OCR for page ${pageNum}:`, error);
        pages.push({
          pageNumber: pageNum,
          extractedText: `Error loading page ${pageNum}`,
          confidence: 0,
          boundingBoxes: []
        });
      }
    }

    res.json({
      success: true,
      batch: {
        id: batch.id,
        startPage: batch.startPage,
        endPage: batch.endPage,
        status: batch.status,
        progress: Math.round((batch.pagesDone / (batch.endPage - batch.startPage + 1)) * 100)
      },
      pages,
      totalPages: pages.length
    });

  } catch (error) {
    console.error('‚ùå Error getting batch OCR data:', error);
    res.status(500).json({
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

// Save highlighted text as index items
router.post('/documents/:documentId/highlights', async (req, res) => {
  try {
    const { documentId } = req.params;
    const { highlights } = req.body;

    if (!Array.isArray(highlights)) {
      return res.status(400).json({
        success: false,
        error: 'highlights must be an array'
      });
    }

    console.log(`‚ú® Saving ${highlights.length} highlighted items for document ${documentId}`);

    // In a real implementation, you'd save these to a highlights table
    // For now, we'll just acknowledge receipt

    res.json({
      success: true,
      message: `Saved ${highlights.length} highlighted items`,
      highlights
    });

  } catch (error) {
    console.error('‚ùå Error saving highlights:', error);
    res.status(500).json({
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

// Get highlighted items for a document
router.get('/documents/:documentId/highlights', async (req, res) => {
  try {
    const { documentId } = req.params;

    // In a real implementation, you'd fetch from a highlights table
    // For now, return empty array
    const highlights: any[] = [];

    res.json({
      success: true,
      highlights,
      totalHighlights: highlights.length
    });

  } catch (error) {
    console.error('‚ùå Error getting highlights:', error);
    res.status(500).json({
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    });
  }
});

export default router;

server/utils/batchProgressUpdater.ts.content:
import { storage } from '../storage.js';

export class BatchProgressUpdater {
  private static updateIntervals: Map<string, NodeJS.Timeout> = new Map();

  // Start real-time progress updates for a document
  static startProgressTracking(documentId: string): void {
    // Clear existing interval if any
    this.stopProgressTracking(documentId);

    const interval = setInterval(async () => {
      try {
        await this.updateAllBatchProgress(documentId);
      } catch (error) {
        console.warn(`‚ö†Ô∏è Progress update failed for ${documentId}:`, error);
      }
    }, 5000); // Update every 5 seconds

    this.updateIntervals.set(documentId, interval);
    console.log(`üìä Started real-time progress tracking for document ${documentId}`);
  }

  // Stop progress tracking
  static stopProgressTracking(documentId: string): void {
    const interval = this.updateIntervals.get(documentId);
    if (interval) {
      clearInterval(interval);
      this.updateIntervals.delete(documentId);
      console.log(`üìä Stopped progress tracking for document ${documentId}`);
    }
  }

  // Update progress for all batches of a document
  static async updateAllBatchProgress(documentId: string): Promise<void> {
    try {
      const { db } = await import('../db');

      // Update all batch progress counts in one query
      await db.execute(`
        UPDATE ocr_batches 
        SET pages_done = (
          SELECT COUNT(*) 
          FROM ocr_pages 
          WHERE ocr_pages.document_id = ocr_batches.document_id 
            AND ocr_pages.page_number >= ocr_batches.start_page 
            AND ocr_pages.page_number <= ocr_batches.end_page
        )
        WHERE document_id = $1
      `, [documentId]);

    } catch (error) {
      console.error(`‚ùå Failed to update batch progress for ${documentId}:`, error);
    }
  }

  // Check if document OCR is complete and stop tracking
  static async checkAndCompleteDocument(documentId: string): Promise<void> {
    try {
      const batches = await storage.getOcrBatchesByDocument(documentId);
      const allComplete = batches.every(batch => 
        batch.pagesDone >= (batch.endPage - batch.startPage + 1)
      );

      if (allComplete) {
        console.log(`üéâ Document ${documentId} OCR completed! Stopping progress tracking.`);
        this.stopProgressTracking(documentId);

        // Mark all completed batches
        for (const batch of batches) {
          if (batch.status === 'processing') {
            await storage.updateOcrBatch(batch.id, {
              status: 'completed',
              completedAt: new Date()
            });
          }
        }
      }

    } catch (error) {
      console.error(`‚ùå Failed to check completion for ${documentId}:`, error);
    }
  }
}

