  // Update row function with auto URL generation
  const updateRow = useCallback((i: number, patch: Partial<IndexRow>) => {
    setIndexRows(prev => {
      const next = [...prev];
      const row = {...next[i], ...patch};
      if (row.hyperlinkPage && !isNaN(Number(row.hyperlinkPage))) {
