import { Router } from 'express';
import { db } from '../db.js';
import { screenshots, screenshotHyperlinks } from '../../shared/schema.js';
import { eq, and } from 'drizzle-orm';
import { z } from 'zod';

const router = Router();

// Schema for screenshot validation
const ScreenshotSchema = z.object({
  documentId: z.string(),
  filename: z.string(),
  originalName: z.string(),
  mimeType: z.string(),
  fileSize: z.number().optional(),
  imageData: z.string(), // Base64 encoded
  ocrText: z.string().optional(),
  clickableAreas: z.array(z.any()).optional(),
});

const ScreenshotHyperlinkSchema = z.object({
  screenshotId: z.string(),
  documentId: z.string(),
  tabNumber: z.string(),
  title: z.string(),
  dateField: z.string().optional(),
  targetPage: z.number(),
  sourceType: z.string().optional(),
  hyperlinks: z.array(z.any()).optional(),
  bboxCoordinates: z.any().optional(),
  confidence: z.number().optional(),
});

// GET /api/documents/:documentId/screenshots - Get all screenshots for a document
router.get('/documents/:documentId/screenshots', async (req, res) => {
  try {
    const { documentId } = req.params;

    const documentScreenshots = await db
      .select()
      .from(screenshots)
      .where(eq(screenshots.documentId, documentId))
      .orderBy(screenshots.createdAt);

    // Also get associated hyperlinks for each screenshot
    const screenshotsWithHyperlinks = await Promise.all(
      documentScreenshots.map(async (screenshot) => {
        const hyperlinks = await db
          .select()
          .from(screenshotHyperlinks)
          .where(eq(screenshotHyperlinks.screenshotId, screenshot.id));

        return {
          ...screenshot,
          hyperlinks
        };
      })
    );

    res.json(screenshotsWithHyperlinks);
  } catch (error) {
    console.error('Error fetching screenshots:', error);
    res.status(500).json({ error: 'Failed to fetch screenshots' });
  }
});

// POST /api/documents/:documentId/screenshots - Save a new screenshot
router.post('/documents/:documentId/screenshots', async (req, res) => {
  try {
    const { documentId } = req.params;
    const screenshotData = { ...req.body, documentId };

    const validatedData = ScreenshotSchema.parse(screenshotData);

    const [screenshot] = await db
      .insert(screenshots)
      .values({
        ...validatedData,
        clickableAreas: validatedData.clickableAreas || [],
        ocrProcessingStatus: validatedData.ocrText ? 'completed' : 'pending'
      })
      .returning();

    res.json(screenshot);
  } catch (error) {
    console.error('Error saving screenshot:', error);
    res.status(500).json({ error: 'Failed to save screenshot' });
  }
});

// PUT /api/screenshots/:screenshotId/ocr - Update OCR text for a screenshot
router.put('/screenshots/:screenshotId/ocr', async (req, res) => {
  try {
    const { screenshotId } = req.params;
    const { ocrText, processingNotes } = req.body;

    console.log('Updating OCR for screenshot ID:', screenshotId);

    // First check if screenshot exists
    const existing = await db.select().from(screenshots).where(eq(screenshots.id, screenshotId)).limit(1);

    if (existing.length === 0) {
      console.log('Screenshot not found in database for ID:', screenshotId);
      // Try to find by filename/originalName as fallback
      const byName = await db.select().from(screenshots).where(eq(screenshots.filename, screenshotId)).limit(1);
      if (byName.length === 0) {
        return res.status(404).json({ error: 'Screenshot not found' });
      } else {
        console.log('Found screenshot by filename, updating...');
        const [updatedScreenshot] = await db
          .update(screenshots)
          .set({
            ocrText,
            processingNotes,
            ocrProcessingStatus: 'completed',
            updatedAt: new Date()
          })
          .where(eq(screenshots.filename, screenshotId))
          .returning();
        return res.json(updatedScreenshot);
      }
    }

    const [updatedScreenshot] = await db
      .update(screenshots)
      .set({
        ocrText,
        processingNotes,
        ocrProcessingStatus: 'completed',
        updatedAt: new Date()
      })
      .where(eq(screenshots.id, screenshotId))
      .returning();

    res.json(updatedScreenshot);
  } catch (error) {
    console.error('Error updating OCR text:', error);
    res.status(500).json({ error: 'Failed to update OCR text' });
  }
});

// PUT /api/screenshots/:screenshotId/clickable-areas - Update clickable areas
router.put('/screenshots/:screenshotId/clickable-areas', async (req, res) => {
  try {
    const { screenshotId } = req.params;
    const { clickableAreas } = req.body;

    const [updatedScreenshot] = await db
      .update(screenshots)
      .set({
        clickableAreas,
        updatedAt: new Date()
      })
      .where(eq(screenshots.id, screenshotId))
      .returning();

    if (!updatedScreenshot) {
      return res.status(404).json({ error: 'Screenshot not found' });
    }

    res.json(updatedScreenshot);
  } catch (error) {
    console.error('Error updating clickable areas:', error);
    res.status(500).json({ error: 'Failed to update clickable areas' });
  }
});

// POST /api/screenshots/:screenshotId/hyperlinks - Create hyperlinks from screenshot
router.post('/screenshots/:screenshotId/hyperlinks', async (req, res) => {
  try {
    const { screenshotId } = req.params;
    const hyperlinksData = req.body.hyperlinks || [];

    const validatedHyperlinks = hyperlinksData.map((link: any) => 
      ScreenshotHyperlinkSchema.parse({ ...link, screenshotId })
    );

    // Insert all hyperlinks
    const createdHyperlinks = await db
      .insert(screenshotHyperlinks)
      .values(validatedHyperlinks)
      .returning();

    res.json(createdHyperlinks);
  } catch (error) {
    console.error('Error creating hyperlinks:', error);
    res.status(500).json({ error: 'Failed to create hyperlinks' });
  }
});

// GET /api/screenshots/:screenshotId/hyperlinks - Get hyperlinks for a screenshot
router.get('/screenshots/:screenshotId/hyperlinks', async (req, res) => {
  try {
    const { screenshotId } = req.params;

    const hyperlinks = await db
      .select()
      .from(screenshotHyperlinks)
      .where(eq(screenshotHyperlinks.screenshotId, screenshotId))
      .orderBy(screenshotHyperlinks.createdAt);

    res.json(hyperlinks);
  } catch (error) {
    console.error('Error fetching hyperlinks:', error);
    res.status(500).json({ error: 'Failed to fetch hyperlinks' });
  }
});

// DELETE /api/screenshots/:screenshotId - Delete a screenshot and its hyperlinks
router.delete('/screenshots/:screenshotId', async (req, res) => {
  try {
    const { screenshotId } = req.params;

    // Delete associated hyperlinks first (cascade should handle this, but being explicit)
    await db
      .delete(screenshotHyperlinks)
      .where(eq(screenshotHyperlinks.screenshotId, screenshotId));

    // Delete the screenshot
    const [deletedScreenshot] = await db
      .delete(screenshots)
      .where(eq(screenshots.id, screenshotId))
      .returning();

    if (!deletedScreenshot) {
      return res.status(404).json({ error: 'Screenshot not found' });
    }

    res.json({ message: 'Screenshot deleted successfully' });
  } catch (error) {
    console.error('Error deleting screenshot:', error);
    res.status(500).json({ error: 'Failed to delete screenshot' });
  }
});

export default router;

