import React, { useEffect, useMemo, useState } from "react";
import { motion } from "framer-motion";
import { ExternalLink, Save, LinkIcon, ListChecks } from "lucide-react";

/**
 * HyperlinkIndexPanel
 * Right-side replacement for PDF View when the user switches to "Hyperlink Index" tab.
 * Renders OCR Index items as a full-width, scrollable table with inline editable page numbers
 * and auto-generated PDF links (pdfUrl#page=n).
 *
 * Props
 * - ocrText: string                        // raw OCR text captured from left panel (batch or single image)
 * - pdfUrl: string                          // accessible URL of the uploaded PDF
 * - totalPages: number                      // total pages in the PDF (for validation)
 * - initialItems?: IndexItem[]              // optional pre-saved items
 * - onSave?: (items: IndexItem[]) => void   // persist handler (saves to DB / state in parent)
 * - showLinkColumn?: boolean                // if true, shows explicit link column (optional)
 */

export type IndexItem = {
  id: string;              // stable id (uuid or tabNo-based)
  tabNo: number;           // 1..n
  date?: string;           // original date text (optional)
  title: string;           // "Nature of Document" / OCR tag text
  detectedPage?: number;   // auto-detected page from your detector
  page?: number;           // user-confirmed target page (editable)
  link?: string;           // derived from pdfUrl + page
};

export function HyperlinkIndexPanel({
  ocrText,
  pdfUrl,
  totalPages,
  initialItems,
  onSave,
  showLinkColumn = false,
}: {
  ocrText: string;
  pdfUrl: string;
  totalPages: number;
  initialItems?: IndexItem[];
  onSave?: (items: IndexItem[]) => void;
  showLinkColumn?: boolean;
}) {
  const [items, setItems] = useState<IndexItem[]>(() => {
    if (initialItems?.length) return withLinks(initialItems, pdfUrl);
    const parsed = parseIndexOcr(ocrText);
    return withLinks(parsed, pdfUrl);
  });

  // Which row is being reviewed
  const [selectedIdx, setSelectedIdx] = useState<number>(0);
  // Live preview page for the selected row (detected by default, follows edits)
  const previewPage = useMemo(() => {
    const it = items[selectedIdx];
    if (!it) return 1;
    // Prefer current edit value if present, else detectedPage, else confirmed page
    return clampInt(String(it.page ?? it.detectedPage ?? 1), 1, totalPages) ?? 1;
  }, [items, selectedIdx, totalPages]);

  useEffect(() => {
    // Merge new OCR data with existing items, preserving user edits
    const parsed = parseIndexOcr(ocrText);
    const newItems = withLinks(parsed, pdfUrl);

    setItems(prevItems => {
      // If no previous items, use new parsed items
      if (!prevItems.length) return newItems;

      // Merge strategy: preserve user-edited items, add new items
      const mergedItems: IndexItem[] = [];
      const prevItemsByTabNo = new Map(prevItems.map(item => [item.tabNo, item]));

      // First, add all new parsed items, but preserve edits for existing ones
      newItems.forEach(newItem => {
        const existingItem = prevItemsByTabNo.get(newItem.tabNo);
        if (existingItem && existingItem.page) {
          // Keep the user-edited version, but update title/date if they changed
          mergedItems.push({
            ...existingItem,
            title: newItem.title, // Allow title updates from OCR
            date: newItem.date,   // Allow date updates from OCR
            link: existingItem.page ? makePdfLink(pdfUrl, existingItem.page) : undefined
          });
        } else {
          mergedItems.push(newItem);
        }
        prevItemsByTabNo.delete(newItem.tabNo);
      });

      // Add any remaining previous items that weren't in the new OCR
      prevItemsByTabNo.forEach(item => {
        mergedItems.push(item);
      });

      return mergedItems.sort((a, b) => a.tabNo - b.tabNo);
    });
    setSelectedIdx(0);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [ocrText, pdfUrl]);

  const allValid = useMemo(
    () => items.every((it) => (it.page ?? it.detectedPage) && (it.page ?? it.detectedPage)! >= 1 && (it.page ?? it.detectedPage)! <= totalPages),
    [items, totalPages]
  );

  function handlePageChange(idx: number, value: string) {
    const page = clampInt(value, 1, totalPages);
    setItems((prev) => {
      const next = [...prev];
      next[idx] = {
        ...next[idx],
        page: page,
        link: page ? makePdfLink(pdfUrl, page) : undefined,
      };
      return next;
    });
  }

  function handleConfirm(idx: number) {
    setItems((prev) => {
      const next = [...prev];
      const it = next[idx];
      const chosen = it.page ?? it.detectedPage ?? 1;
      next[idx] = { ...it, page: chosen, link: makePdfLink(pdfUrl, chosen) };
      return next;
    });
  }

  function handleSave() {
    if (onSave) onSave(items);
  }

  return (
    <div className="h-full w-full grid grid-cols-12 gap-3">
      {/* Left: table */}
      <div className="col-span-7 flex flex-col gap-2">
        {/* Header */}
        <div className="flex items-center justify-between py-2 px-3 bg-slate-900/40 rounded-xl border border-slate-800">
          <div className="flex items-center gap-2">
            <ListChecks className="h-5 w-5" />
            <h2 className="text-lg font-semibold">Hyperlink Index</h2>
            <span className="text-xs text-slate-400">{items.length} items</span>
          </div>
          <div className="flex items-center gap-2">
            <button
              className="px-3 py-1.5 rounded-xl bg-emerald-600 hover:bg-emerald-500 text-white text-sm flex items-center gap-2"
              onClick={handleSave}
            >
              <Save className="h-4 w-4" /> Save
            </button>
          </div>
        </div>

        {/* Table */}
        <div className="flex-1 overflow-auto rounded-2xl border border-slate-800 bg-slate-950/30">
          <table className="min-w-full table-fixed">
            <colgroup>
              <col className="w-14" />
              <col className="w-44" />
              <col />
              <col className="w-24" />
              <col className="w-28" />
              <col className="w-28" />
              {showLinkColumn && <col className="w-40" />}
            </colgroup>
            <thead className="sticky top-0 bg-slate-900/70 backdrop-blur">
              <tr>
                <th className="text-left px-3 py-2 text-xs text-slate-300">Tab</th>
                <th className="text-left px-3 py-2 text-xs text-slate-300">Date</th>
                <th className="text-left px-3 py-2 text-xs text-slate-300">OCR Tag / Nature of Document</th>
                <th className="text-left px-3 py-2 text-xs text-slate-300">Detected</th>
                <th className="text-left px-3 py-2 text-xs text-slate-300">Page #</th>
                <th className="text-left px-3 py-2 text-xs text-slate-300">Confirm</th>
                {showLinkColumn && (
                  <th className="text-left px-3 py-2 text-xs text-slate-300">Link</th>
                )}
              </tr>
            </thead>
            <tbody>
              {items.map((it, idx) => (
                <tr
                  key={it.id}
                  className={`border-t border-slate-800 hover:bg-slate-900/40 ${selectedIdx===idx? 'bg-slate-900/50':''}`}
                  onClick={() => setSelectedIdx(idx)}
                >
                  <td className="px-3 py-2 text-sm text-slate-200">{it.tabNo}</td>
                  <td className="px-3 py-2 text-sm text-slate-200">{it.date ?? "‚Äî"}</td>
                  <td className="px-3 py-2 text-sm text-slate-100">{it.title}</td>
                  <td className="px-3 py-2 text-sm text-slate-200">{it.detectedPage ?? "‚Äî"}</td>
                  <td className="px-3 py-2">
                    <input
                      type="number"
                      min={1}
                      max={totalPages}
                      value={it.page ?? ""}
                      placeholder={String(it.detectedPage ?? '')}
                      onChange={(e) => handlePageChange(idx, e.target.value)}
                      className="w-24 px-2 py-1 rounded-lg bg-slate-800 border border-slate-700 text-slate-100 focus:outline-none focus:ring-2 focus:ring-emerald-500"
                    />
                  </td>
                  <td className="px-3 py-2">
                    <button
                      onClick={() => handleConfirm(idx)}
                      className="px-2 py-1 rounded-lg bg-blue-600 hover:bg-blue-500 text-white text-xs"
                    >Confirm</button>
                  </td>
                  {showLinkColumn && (
                    <td className="px-3 py-2 text-sm">
                      {(it.page ?? it.detectedPage) ? (
                        <a
                          className="inline-flex items-center gap-1 text-emerald-400 hover:text-emerald-300 underline"
                          href={makePdfLink(pdfUrl, it.page ?? it.detectedPage!)}
                          target="_blank"
                          rel="noreferrer"
                        >
                          Open <ExternalLink className="h-3 w-3" />
                        </a>
                      ) : (
                        <span className="text-slate-500">‚Äî</span>
                      )}
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Footer status */}
        <div className="flex items-center justify-between text-xs text-slate-400">
          <div>
            {allValid ? (
              <span className="text-emerald-400">All items have detected or confirmed page numbers.</span>
            ) : (
              <span>Enter page numbers 1‚Äì{totalPages} for each row.</span>
            )}
          </div>
          <div className="flex items-center gap-2">
            <LinkIcon className="h-3.5 w-3.5" />
            <span>Links are generated as <code>pdf#page=n</code> and will open in a new tab.</span>
          </div>
        </div>
      </div>

      {/* Right: live PDF preview that follows the selected row & edits */}
      <div className="col-span-5 flex flex-col rounded-2xl border border-slate-800 overflow-hidden">
        <div className="px-3 py-2 text-xs bg-slate-900/60 border-b border-slate-800 flex items-center gap-2">
          <span className="text-slate-300">Preview</span>
          <span className="text-slate-400">Row {selectedIdx + 1} ‚Üí Page {previewPage}</span>
        </div>
        <iframe
          key={`preview-${selectedIdx}-${previewPage}`}
          src={makePdfLink(pdfUrl, previewPage)}
          className="w-full h-full"
          title="PDF Preview"
        />
      </div>
    </div>
  );
}

// ‚Äî‚Äî‚Äî Helpers ‚Äî‚Äî‚Äî

function withLinks(items: IndexItem[], pdfUrl: string): IndexItem[] {
  return items.map((it) => ({
    ...it,
    link: it.page ? makePdfLink(pdfUrl, it.page) : undefined,
  }));
}

function makePdfLink(pdfUrl: string, page: number) {
  const hash = `#page=${page}`;
  return pdfUrl.includes("#") ? pdfUrl.replace(/#.*$/, hash) : `${pdfUrl}${hash}`;
}

function clampInt(v: string, min: number, max: number): number | undefined {
  if (!v) return undefined;
  const n = parseInt(v, 10);
  if (Number.isNaN(n)) return undefined;
  return Math.max(min, Math.min(max, n));
}

/**
 * parseIndexOcr
 * Very forgiving parser for lines like:
 *  "12.     August 16, 2023      Affidavit ‚Äì Serafina Ferrante"
 *  "13.     September 23, 2019   Recognizance of Bail ‚Äì Rino Ferrante"
 *
 * It extracts: tabNo, date (string), title (rest of line after date)
 */
export function parseIndexOcr(ocrText: string): IndexItem[] {
  if (!ocrText) return [];
  const lines = ocrText
    .split(/\r?\n/)
    .map((l) => l.trim())
    .filter(Boolean);

  const items: IndexItem[] = [];

  // Join wrapped lines: if a line begins with a digit+dot, start new; else append to previous
  const normalized: string[] = [];
  for (const line of lines) {
    if (/^\d+\./.test(line)) {
      normalized.push(line);
    } else if (normalized.length) {
      normalized[normalized.length - 1] += " " + line;
    } else {
      normalized.push(line);
    }
  }

  normalized.forEach((row) => {
    const m = row.match(/^(\d+)\.?\s+(.*)$/);
    if (!m) return;
    const tabNo = parseInt(m[1], 10);
    const rest = m[2];

    // try to pull a date (month word or numeric) and the title after it
    // Accept patterns like "February 28, 2022" or "Nov 2022" or "2023-08-16"
    const dateRegex = /((January|February|March|April|May|June|July|August|September|October|November|December|Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Sept|Oct|Nov|Dec)\s+\d{1,2},\s*\d{4}|(January|February|March|April|May|June|July|August|September|October|November|December|Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Sept|Oct|Nov|Dec)\s+\d{4}|\d{4}-\d{2}-\d{2})/i;
    const dm = rest.match(dateRegex);

    let date: string | undefined;
    let title = rest;
    if (dm) {
      date = dm[0].replace(/\s+/g, " ").trim();
      title = rest.slice(dm.index! + dm[0].length).replace(/^[\s‚Äì-]+/, "").trim();
    }

    items.push({
      id: `${tabNo}-${Math.random().toString(36).slice(2, 8)}`,
      tabNo,
      date,
      title: title || rest,
      page: undefined,
    });
  });

  // sort by tab number just in case
  items.sort((a, b) => a.tabNo - b.tabNo);
  return items;
}

server/indexExtractor.ts.content:
// server/indexExtractor.ts
export interface IndexItem {
  label: string;
  pageHint?: number;
  confidence: number;
  tabNumber?: string;
  title?: string;
  dateField?: string;
}

export function extractIndexFromText(text: string): IndexItem[] {
  // Find "INDEX" header and then numbered lines until a blank gap
  const lines = text.split(/\r?\n/).map(l => l.trim()).filter(Boolean);
  const start = lines.findIndex(l => /^index\b/i.test(l));
  if (start === -1) return [];

  const items: IndexItem[] = [];
  for (let i = start + 1; i < lines.length; i++) {
    const l = lines[i];

    // Stop if we hit another section heading
    if (/^(exhibits?|schedule|table of contents|attachments?)$/i.test(l)) break;

    // Match various patterns:
    // "1. Something ‚Ä¶ Page 15" OR "1 Something" OR "Tab 1: Something"
    const patterns = [
      /^(?:Tab\s+)?(\d+)[\.\):]?\s+(.+?)(?:\.*\s*(?:p(?:age)?\s*)?(\d+))?$/i,
      /^([A-Z]\d*|\d+[A-Z]?)[\.\):]?\s+(.+?)(?:\.*\s*(?:p(?:age)?\s*)?(\d+))?$/i,
      /^(Exhibit\s+[A-Z0-9\-]+|\d+)[\.\):]?\s+(.+?)(?:\.*\s*(?:p(?:age)?\s*)?(\d+))?$/i
    ];

    let matched = false;
    for (const pattern of patterns) {
      const m = l.match(pattern);
      if (m) {
        const [, tabNum, title, page] = m;

        // Extract date from title if present
        const dateMatch = title.match(/(\d{1,2}\/\d{1,2}\/\d{2,4}|\w+\s+\d{1,2},?\s+\d{4}|dated\s+[^,]+)/i);
        const dateField = dateMatch ? dateMatch[1] : undefined;

        // Clean title by removing date if found
        const cleanTitle = dateField && dateMatch ? title.replace(dateMatch[0], '').replace(/[,\s]+$/, '') : title;

        items.push({
          label: l,
          pageHint: page ? Number(page) : undefined,
          confidence: 0.95,
          tabNumber: tabNum,
          title: cleanTitle.trim(),
          dateField: dateField?.trim()
        });
        matched = true;
        break;
      }
    }

    // Stop if we get long run of non-matches (likely end of index section)
    if (items.length > 0 && !matched) {
      // Allow a few non-matching lines, but stop after too many
      const nonMatches = lines.slice(i).slice(0, 5).filter(line => {
        return !patterns.some(pattern => pattern.test(line));
      });
      if (nonMatches.length >= 4) break;
    }
  }

  return items;
}

