/**
 * PDF Link Template Utilities
 * 
 * These functions handle generating page-specific URLs from templates
 * instead of storing full URLs in the database.
 */

/**
 * Build a PDF page URL from a template
 * @param template - URL template with :page, :caseId, and :docId placeholders
 * @param page - Page number to insert
 * @param docId - Document ID to insert
 * @param caseId - Case ID to insert (optional, for templates that include it)
 * @returns Complete URL with page fragment
 */
export function buildPdfPageUrl(template: string, page: number, docId: string, caseId?: string): string {
  let result = template
    .replace(':page', String(page))
    .replace(':docId', docId);

  if (caseId) {
    result = result.replace(':caseId', caseId);
  }

  return result;
}

/**
 * Format page number for display
 * @param n - Page number (can be null/undefined)
 * @returns Formatted string or empty if invalid
 */
export function formatPageNumber(n?: number | null): string {
  return Number.isFinite(n as number) && (n as number)! > 0 ? String(n) : '';
}

/**
 * Get default PDF URL template for a document
 * @param caseId - Case ID 
 * @param docId - Document ID
 * @returns Default template string
 */
export function getDefaultPdfTemplate(caseId: string, docId: string): string {
  return `/online/pdf/:caseId/:docId#page=:page`;
}

/**
 * Extract page number from a PDF URL with page fragment
 * @param url - URL that may contain #page=X
 * @returns Page number or null if not found
 */
export function extractPageFromUrl(url: string): number | null {
  const match = url.match(/#page=(\d+)/);
  if (match) {
    const page = parseInt(match[1], 10);
    return isNaN(page) ? null : page;
  }
  return null;
}

/**
 * Validate if a page number is valid
 * @param page - Page number to validate
 * @param maxPages - Maximum allowed pages (optional)
 * @returns True if valid
 */
export function isValidPageNumber(page: number | null | undefined, maxPages?: number): boolean {
  if (!Number.isFinite(page) || page === null || page === undefined) {
    return false;
  }
  if (page < 1) {
    return false;
  }
  if (maxPages && page > maxPages) {
    return false;
  }
  return true;
}

