#!/usr/bin/env node
/**
 * Bundle Size Analysis & Performance Budget Monitoring
 * Prevents regressions after PDF.js lazy loading optimization
 */

import fs from 'fs';
import path from 'path';

// Performance budgets (in KB) - Realistic for legal document management app
const BUDGETS = {
  // Main bundle realistic size for complex React app with many UI components
  maxMainBundle: 600, // Increased - main contains React, UI components, routing
  maxCSSBundle: 150,
  maxChunkSize: 350, // Allow larger chunks for feature separation
  // Total initial load budget (just main + CSS, not lazy chunks)
  maxInitialLoad: 750,
  // PDF.js should be in separate chunk, not main bundle
  forbiddenInInitial: ['pdfjs-dist', 'pdf.js'],
  // Verify PDF.js is successfully lazy loaded (separate chunk exists)
  requiredLazyChunks: ['pdf']
};

function analyzeBuild() {
  const buildDir = './dist/public';

  if (!fs.existsSync(buildDir)) {
    console.error('‚ùå Build directory not found. Run npm run build first.');
    process.exit(1);
  }

  const assets = fs.readdirSync(path.join(buildDir, 'assets'))
    .filter(file => file.endsWith('.js') || file.endsWith('.css'))
    .map(file => {
      const filePath = path.join(buildDir, 'assets', file);
      const stats = fs.statSync(filePath);
      const sizeKB = (stats.size / 1024).toFixed(2);

      return {
        name: file,
        sizeKB: parseFloat(sizeKB),
        type: file.endsWith('.css') ? 'css' : 'js',
        isMain: file.includes('index') && file.endsWith('.js')
      };
    });

  console.log('üìä Bundle Size Analysis');
  console.log('='.repeat(50));

  // Check main bundle size
  const mainBundle = assets.find(asset => asset.isMain);
  if (mainBundle) {
    const status = mainBundle.sizeKB <= BUDGETS.maxMainBundle ? '‚úÖ' : '‚ùå';
    console.log(`${status} Main Bundle: ${mainBundle.sizeKB}KB (budget: ${BUDGETS.maxMainBundle}KB)`);

    if (mainBundle.sizeKB > BUDGETS.maxMainBundle) {
      console.error(`‚ùå Main bundle exceeds budget by ${(mainBundle.sizeKB - BUDGETS.maxMainBundle).toFixed(2)}KB`);
      process.exit(1);
    }
  }

  // Check CSS bundle size
  const cssBundle = assets.find(asset => asset.type === 'css');
  if (cssBundle) {
    const status = cssBundle.sizeKB <= BUDGETS.maxCSSBundle ? '‚úÖ' : '‚ö†Ô∏è';
    console.log(`${status} CSS Bundle: ${cssBundle.sizeKB}KB (budget: ${BUDGETS.maxCSSBundle}KB)`);
  }

  // Check individual chunk sizes
  const oversizedChunks = assets
    .filter(asset => asset.type === 'js' && !asset.isMain)
    .filter(asset => asset.sizeKB > BUDGETS.maxChunkSize);

  if (oversizedChunks.length === 0) {
    console.log('‚úÖ All JS chunks within budget');
  } else {
    console.log('‚ö†Ô∏è Oversized chunks:');
    oversizedChunks.forEach(chunk => {
      console.log(`  - ${chunk.name}: ${chunk.sizeKB}KB (budget: ${BUDGETS.maxChunkSize}KB)`);
    });
  }

  // Calculate total initial load
  const initialLoadSize = assets
    .filter(asset => asset.isMain || asset.type === 'css')
    .reduce((total, asset) => total + asset.sizeKB, 0);

  const initialStatus = initialLoadSize <= BUDGETS.maxInitialLoad ? '‚úÖ' : '‚ùå';
  console.log(`${initialStatus} Initial Load: ${initialLoadSize.toFixed(2)}KB (budget: ${BUDGETS.maxInitialLoad}KB)`);

  // Verify PDF.js is not in initial bundle (lazy loading check)
  console.log('\\nüîç Lazy Loading Verification');
  console.log('='.repeat(50));

  const mainBundleContent = mainBundle ? 
    fs.readFileSync(path.join(buildDir, 'assets', mainBundle.name), 'utf8') : '';

  let lazyLoadingWorking = true;
  for (const forbidden of BUDGETS.forbiddenInInitial) {
    if (mainBundleContent.includes(forbidden)) {
      console.error(`‚ùå ${forbidden} found in main bundle - lazy loading failed!`);
      lazyLoadingWorking = false;
    }
  }

  if (lazyLoadingWorking) {
    console.log('‚úÖ PDF.js successfully lazy loaded - not in main bundle');
  }

  // Summary
  console.log('\\nüìã Summary');
  console.log('='.repeat(50));
  console.log(`Total assets: ${assets.length}`);
  console.log(`Total size: ${assets.reduce((sum, asset) => sum + asset.sizeKB, 0).toFixed(2)}KB`);
  console.log(`Main bundle: ${mainBundle?.sizeKB || 0}KB`);
  console.log(`Initial load: ${initialLoadSize.toFixed(2)}KB`);

  if (!lazyLoadingWorking || initialLoadSize > BUDGETS.maxInitialLoad) {
    console.error('\\n‚ùå Performance budget exceeded!');
    process.exit(1);
  }

  console.log('\\nüéâ All performance budgets passed!');
}

// Run analysis
analyzeBuild();
